INSERT INTO `php_methods` VALUES (28,'cms_preload_session','$domain = strtolower(SITE_DOMAIN);\r\n//ini_set(\'session.cookie_domain\', \'.\'.$domain);\r\nini_set(\'session.cookie_domain\', (strpos($_SERVER[\'HTTP_HOST\'],\'.\') !== false) ? \'.\'.$domain : \'\');\r\n\r\n//include_once \'inc/securimage/securimage.php\';\r\n\r\n// Start the session so variables can be passed between all the pages\r\nsession_start();\r\n\r\n// Set the default timezone\r\n//date_default_timezone_set(\'GMT\');\r\n//date_timezone_set($date, timezone_open(\'US/Eastern\'));\r\ndate_default_timezone_set(\'UTC\');\r\n\r\n// Register the shutdown function to handle fatal errors\r\nregister_shutdown_function(\'shutdownFunction\');\r\n\r\nset_error_handler(\'cms_error_handler\');\r\n\r\n// Prevent session hijacking\r\nfunction validate_session()\r\n{\r\n	// If the user agent is not available, use a dummy value\r\n	$user_agent = \'Not specified\';\r\n	if (isset($_SERVER[\'HTTP_USER_AGENT\'])) $user_agent = $_SERVER[\'HTTP_USER_AGENT\'];\r\n\r\n	// Ensure the first time the session is random\r\n	if (!isset($_SESSION[\'PREV_ACTIVITY\']) || !isset($_SESSION[\'PREV_REMOTEADDR\']) || !isset($_SESSION[\'PREV_USERAGENT\']))\r\n	{\r\n		session_destroy();\r\n		session_start();\r\n        session_regenerate_id(true);	\r\n	}\r\n	// Ensure the session doesn\'t stay alive too long (3 hours)\r\n	else if (time() - $_SESSION[\'PREV_ACTIVITY\'] > (60*60*3))\r\n	{\r\n		session_destroy();\r\n		session_start();\r\n        session_regenerate_id(true);\r\n	}\r\n	// Ensure the session can\'t be used by a different computer\r\n	else if (md5($_SERVER[\'REMOTE_ADDR\']) != $_SESSION[\'PREV_REMOTEADDR\'] || md5($user_agent) != $_SESSION[\'PREV_USERAGENT\'])\r\n	{\r\n		session_destroy();\r\n		session_start();\r\n        session_regenerate_id(true);\r\n	}\r\n	\r\n	$_SESSION[\'PREV_ACTIVITY\'] = time();\r\n	$_SESSION[\'PREV_USERAGENT\'] = md5($user_agent);\r\n	$_SESSION[\'PREV_REMOTEADDR\'] = md5($_SERVER[\'REMOTE_ADDR\']);\r\n	$_SESSION[\'USERAGENT\'] = $user_agent;\r\n	$_SESSION[\'REMOTEADDR\'] = $_SERVER[\'REMOTE_ADDR\'];\r\n}\r\nvalidate_session();','2012-08-08 22:33:17',2,1,'','','',''),(31,'cms_preload_define','// Grab the domain\r\n$arr = explode(\'.\',RECORDED_DOMAIN);\r\nif (count($arr)==3) define(\"SITE_DOMAIN\", $arr[1].\'.\'.$arr[2]);\r\nelse define(\"SITE_DOMAIN\", RECORDED_DOMAIN);\r\nif (!defined(\"SITE_TITLE\")) define(\"SITE_TITLE\",RECORDED_DOMAIN);\r\ndefine(\"SITE_FULL\",\'http://\'.RECORDED_DOMAIN);\r\n\r\n// Site description should go here\r\ndefine(\"SITE_TAG\",\'\');\r\n\r\n// Site keywords should go here\r\ndefine(\"SITE_KEYWORDS\",\'\');\r\n\r\n// Google Analytics should go here\r\n$analytics = <<< OUTPUT\r\n	<script type=\"text/javascript\">\r\n	</script>\r\nOUTPUT;\r\n\r\nif (RECORDED_DOMAIN==strtolower(\'localhost\')) $analytics=\'\';\r\ndefine(\"GOOGLE_ANALYTICS\",$analytics);\r\n\r\n// Twitter information should go here (grab from dev.twitter.com)\r\ndefine(\"TWITTER_NAME\", \'\');\r\ndefine(\"TWITTER_CONSUMER_KEY\", \'\');\r\ndefine(\"TWITTER_CONSUMER_SECRET\", \'\');\r\ndefine(\"TWITTER_ACCESS_TOKEN\", \'\');\r\ndefine(\"TWITTER_ACCESS_SECRET\", \'\');','2012-08-04 14:39:21',2,1,'','','',''),(62,'hlp_html_output','// ************************** HTML Helpers **************************\r\nfunction h2($text)\r\n{\r\n	return \"<h2>$text</h2>\";\r\n}	\r\n\r\nfunction p($text)\r\n{\r\n	return \"<p>$text</p>\";\r\n}\r\n\r\nfunction a($text,$url)\r\n{\r\n	return \"<a href=\\\"index.php?$url\\\">$text</a>\";\r\n}\r\n\r\nfunction ahome($text)\r\n{\r\n	return \"<a href=\\\"index.php\\\">$text</a>\";\r\n}\r\n\r\nfunction br()\r\n{\r\n	return \"<br />\".NEWLINE;\r\n}\r\n\r\nfunction img($text,$url,$visible=true,$padding=\'0px\')\r\n{\r\n	//$arr = getimagesize(str_replace(\"\\\\\",\'/\',$url));\r\n	//$width = $arr[0];\r\n	//return \"<img src=\\\"$url\\\" alt=\\\"$text\\\" title=\\\"$text\\\" width=\\\"$width\\\" />\";\r\n	if (!$visible) $vis = \'visibility: hidden;\';\r\n	else $vis = \'\';\r\n	return \"<img src=\\\"$url\\\" alt=\\\"$text\\\" title=\\\"$text\\\" style=\\\"max-width: 100%; $vis padding:$padding; \\\" />\";\r\n	//return \"<img src=\\\"$url\\\" alt=\\\"$text\\\" title=\\\"$text\\\" />\";\r\n}\r\n\r\nfunction img_special($text,$url,$visible=true,$padding=\'0px\',$onclick=\'\')\r\n{\r\n	//$arr = getimagesize(str_replace(\"\\\\\",\'/\',$url));\r\n	//$width = $arr[0];\r\n	//return \"<img src=\\\"$url\\\" alt=\\\"$text\\\" title=\\\"$text\\\" width=\\\"$width\\\" />\";\r\n	if (!$visible) $vis = \'visibility: hidden;\';\r\n	else $vis = \'\';\r\n	return \"<img src=\\\"$url\\\" alt=\\\"$text\\\" title=\\\"$text\\\" onclick=\\\"$onclick\\\" style=\\\"max-width: 100%; $vis padding:$padding; \\\" />\";\r\n	//return \"<img src=\\\"$url\\\" alt=\\\"$text\\\" title=\\\"$text\\\" />\";\r\n}\r\n\r\nfunction cb($arr)\r\n{\r\n	$item = \'\';\r\n	foreach ($arr as $cb)\r\n	{\r\n		if (isset($cb[3]) && $cb[3]==true) $checked = \'checked=\"checked\"\';\r\n		else $checked = \'\';\r\n		$item .= \'<input type=\"checkbox\" name=\"\'.$cb[0].\'[]\" value=\"\'.$cb[1].\'\" \'.$checked.\' /> \'.$cb[2].br();\r\n	}\r\n\r\n	return $item;\r\n}\r\n\r\nfunction drop_menu_bootstrap($arr, $full_menu=false)\r\n{\r\n	$menu = \'\';\r\n\r\n	// Start the menu\r\n	if ($full_menu) $menu .= \'<ul class=\"nav\">\';\r\n	\r\n	// Loop through each of the parent menu items\r\n	foreach ($arr as $row)\r\n	{\r\n		// If the menu item IS clickable\r\n		if ($row[1]) $menu .= \'<li><a href=\"\'.$row[1].\'\">\'.$row[0].\'</a>\';\r\n	\r\n		// Else the menu item is NOT clickable\r\n		else $menu .= \'<li class=\"dropdown\"><a href=\"#\" class=\"dropdown-toggle\" data-toggle=\"dropdown\">\'.$row[0].\'<b class=\"caret\"></b></a>\';\r\n	\r\n		// If the menu has a child array\r\n		if (isset($row[2]) && is_array($row[2]))\r\n		{\r\n			if (count($row[2])>0)\r\n			{\r\n				$menu .= \'<ul class=\"dropdown-menu\">\';\r\n				foreach ($row[2] as $child)\r\n				{\r\n					if ($child[1]) $menu .= \'<li><a href=\"\'.$child[1].\'\">\'.$child[0].\'</a></li>\';\r\n					else $menu .= \'<li><p style=\"font-weight: normal; text-align: left; border-top: 1px silver solid;\">\'.$child[0].\'</p></li>\';\r\n	\r\n				}\r\n				$menu .= \'</ul>\';\r\n			}\r\n		}\r\n		$menu .= \'</li>\';\r\n	}\r\n	\r\n	if ($full_menu) $menu .= \'</ul>\';\r\n	\r\n	return $menu;\r\n}\r\n\r\n\r\nfunction load_menu($group)\r\n{\r\n	$qry = \"SELECT php_menu.id,php_menu.label,php_menu.url FROM php_menu\r\n	INNER JOIN access_menu WHERE access_menu.menu_id = php_menu.id AND \r\n	access_menu.group_id=? AND php_menu.parent=? ORDER BY php_menu.position\";\r\n	\r\n	$params = array($group, 0);\r\n	$result = query($qry, $params);\r\n	\r\n	$new_menu = array();\r\n	foreach($result as $row)\r\n	{\r\n		$qry = \"SELECT id,label,url FROM php_menu WHERE parent=? ORDER BY position\";\r\n		$params = array($row[\'id\']);\r\n		$result2 = query($qry, $params);\r\n		$children = array();\r\n		foreach($result2 as $row2)\r\n		{\r\n			array_push($children, array($row2[\'label\'],$row2[\'url\']));\r\n		}\r\n		$parent_url = false;\r\n		if (strlen($row[\'url\'])>0) $parent_url = $row[\'url\'];\r\n		$parent = array($row[\'label\'],$parent_url,$children);\r\n		array_push($new_menu, $parent);\r\n	}\r\n	$menu = drop_menu_bootstrap($new_menu);\r\n	\r\n	return $menu;\r\n}','2012-08-04 15:33:24',2,1,'','','',''),(125,'hlp_db_method_control','// ************************** Method Control Queries **************************\r\nfunction db_method_add($name, $method, $type=0, $post=\'\')\r\n{\r\n	$qry = \"INSERT INTO php_methods (name,method,type,modified,modifier,post) VALUES(?,?,?,CURRENT_TIMESTAMP,2,?)\";\r\n	$params = array($name, $method, $type, $post);\r\n	query_execute($qry, $params);\r\n}\r\n\r\nfunction db_method_update($name, $method, $type=0, $oldname=\'NOTHING__NOTHING\', $post=\'\', $title=\'\', $keyword=\'\', $description=\'\')\r\n{\r\n	$qry = \"SELECT id FROM php_methods WHERE name=? LIMIT 1\";\r\n	$params = array($oldname);\r\n	$result = query($qry, $params);\r\n\r\n	if (count($result)==1)\r\n	{\r\n		$qry = \"UPDATE php_methods SET name=?, method=?, type=?, modified=CURRENT_TIMESTAMP, modifier=2, post=?, title=?, keyword=?, description=? WHERE name=? LIMIT 1\";\r\n		$params = array($name, $method, $type, $post, $title, $keyword, $description, $oldname);\r\n		query_execute($qry, $params);\r\n	}\r\n	else\r\n	{\r\n		$qry = \"UPDATE php_methods SET name=?, method=?, type=?, modified=CURRENT_TIMESTAMP, modifier=2, post=?, title=?, keyword=?, description=? WHERE name=? LIMIT 1\";\r\n		$params = array($name, $method, $type, $post, $title, $keyword, $description, $name);\r\n		query_execute($qry, $params);\r\n	}\r\n}\r\n\r\nfunction db_method_trash($name)\r\n{\r\n	$qry = \"INSERT INTO php_trash SELECT * FROM php_methods WHERE name=? AND modifier!=1 LIMIT 1\";\r\n	$params = array($name);\r\n	query_execute($qry, $params);\r\n\r\n	$qry = \"DELETE FROM php_methods WHERE name=? AND modifier!=1 LIMIT 1\";\r\n	$params = array($name);\r\n	query_execute($qry, $params);\r\n}\r\n\r\nfunction db_method_untrash($name)\r\n{\r\n	$qry = \"INSERT INTO php_methods SELECT * FROM php_trash WHERE name=? LIMIT 1\";\r\n	$params = array($name);\r\n	query_execute($qry, $params);\r\n\r\n	$qry = \"DELETE FROM php_trash WHERE name=? LIMIT 1\";\r\n	$params = array($name);\r\n	query_execute($qry, $params);\r\n}\r\n\r\nfunction db_method_delete($name)\r\n{\r\n	$qry = \"DELETE FROM php_trash WHERE name=? LIMIT 1\";\r\n	$params = array($name);\r\n	query_execute($qry, $params);\r\n}\r\n\r\nfunction db_method_type($name)\r\n{\r\n	$qry = \"SELECT type FROM php_methods WHERE name=? LIMIT 1\";\r\n	$params = array($name);\r\n	$result = query($qry, $params);\r\n	\r\n	if (count($result)==1) return $result[0][\'type\'];\r\n	else return false;\r\n}\r\n\r\nfunction db_method_list()\r\n{\r\n	$qry = \"SELECT id,name,type,modified,title,keyword,description FROM php_methods ORDER BY name\";\r\n	$params = array();\r\n	return query($qry, $params);\r\n}\r\n\r\nfunction db_method_list_trash()\r\n{\r\n	$qry = \"SELECT name,id FROM php_trash ORDER BY name\";\r\n	$params = array();\r\n	return query($qry, $params);\r\n}','2012-05-18 22:26:36',2,1,'','','',''),(123,'script_javascript','// Generic Form\r\nfunction validateForm()\r\n{	\r\n    var tbs = document.getElementsByTagName(\"input\");\r\n    for (var i = 0 ; i < tbs.length ; i++)\r\n    {\r\n	var opt1 = tbs[i].getAttribute(\"name\");\r\n	var opt2 = tbs[i].getAttribute(\"type\");\r\n        if (opt1.indexOf(\"_optional\") == -1 && opt2.toLowerCase() != \"hidden\" && opt1.indexOf(\"search\") != 0)\r\n        {\r\n            if (tbs[i].value==null || tbs[i].value==\"\")\r\n            {\r\n            	alert(\"Please fill out all the fields: \"+ opt1);\r\n		return false;\r\n            }\r\n        }\r\n    }\r\n    \r\n    tbs = document.getElementsByTagName(\"textarea\");\r\n    for (var i = 0 ; i < tbs.length ; i++)\r\n    {\r\n		if (tbs[i].value==null || tbs[i].value==\"\")\r\n		{\r\n			alert(\"Please fill out all the textareas: \" + tbs[i].id);\r\n			return false;\r\n		}\r\n    }\r\n}\r\n\r\n\r\n// Edit Method\r\nfunction indentSelection(myField, myValue)\r\n{\r\n    //MOZILLA/NETSCAPE support\r\n    if (myField.selectionStart || myField.selectionStart == \'0\')\r\n    {\r\n        var startPos = myField.selectionStart;\r\n        var endPos = myField.selectionEnd;\r\n\r\n	// If nothing is selected, just add a tab\r\n	if (endPos-startPos==\'0\')\r\n	{\r\n	        var startPos = myField.selectionStart;\r\n	        var endPos = myField.selectionEnd;\r\n	        myField.value = myField.value.substring(0, startPos)\r\n	        + myValue\r\n	        + myField.value.substring(endPos, myField.value.length);\r\n\r\n	        myField.setSelectionRange(endPos+1, endPos+1);\r\n	}\r\n	// Else there is a text selection\r\n	else\r\n	{\r\n		var startValue = myField.value.substring(0, startPos);\r\n		var oldValue = myField.value.substring(startPos, endPos);\r\n		var newValue = myValue + myField.value.substring(startPos, endPos).replace(/\\n/g, \'\\n\' + myValue);\r\n		var endValue = myField.value.substring(endPos, myField.value.length);\r\n\r\n		myField.value = startValue + newValue + endValue;\r\n		myField.setSelectionRange(startPos, endPos+(newValue.length-oldValue.length));\r\n	}\r\n    }\r\n}\r\n\r\nfunction outdentSelection(myField, myValue)\r\n{\r\n    //MOZILLA/NETSCAPE support\r\n    if (myField.selectionStart || myField.selectionStart == \'0\')\r\n    {\r\n        var startPos = myField.selectionStart;\r\n        var endPos = myField.selectionEnd;\r\n\r\n	// If nothing is selected, just remove the previous tab\r\n	if (endPos-startPos==\'0\')\r\n	{\r\n		if (myField.value.substring(startPos-1,startPos)!= \"	\") return;\r\n		var startValue = myField.value.substring(0, startPos-1);\r\n		var endValue = myField.value.substring(endPos, myField.value.length);\r\n\r\n		myField.value = startValue + endValue;\r\n		myField.setSelectionRange(startPos-1, startPos-1);\r\n	}\r\n	// Else there is a text selection\r\n	else	\r\n	{\r\n		var startValue = myField.value.substring(0, startPos);\r\n		var oldValue = myField.value.substring(startPos, endPos);\r\n		var newValue = myField.value.substring(startPos+1, endPos).replace(/\\n	/g, \'\\n\');\r\n		var endValue = myField.value.substring(endPos, myField.value.length);\r\n\r\n		if (myField.value.substring(startPos,startPos+1)!= \"	\") return;\r\n\r\n		myField.value = startValue + newValue + endValue;\r\n		myField.setSelectionRange(startPos, endPos+(newValue.length-oldValue.length));\r\n	}\r\n    }\r\n}\r\n\r\nfunction validateEditMethodForm()\r\n{	\r\n    var tbs = document.getElementsByTagName(\"input\");\r\n    for (var i = 0 ; i < tbs.length ; i++)\r\n    {\r\n	var opt1 = tbs[i].getAttribute(\"name\");\r\n	var opt2 = tbs[i].getAttribute(\"type\");\r\n        if (opt1.indexOf(\"_optional\") == -1 && opt2.toLowerCase() != \"hidden\" && opt1.indexOf(\"search\") != 0)\r\n        {\r\n            if (tbs[i].value==null || tbs[i].value==\"\")\r\n            {\r\n            	alert(\"Please fill out all the fields\");\r\n		return false;\r\n            }\r\n        }\r\n    }\r\n    \r\n    tbs = document.getElementsByTagName(\"textarea\");\r\n    for (var i = 0 ; i < tbs.length ; i++)\r\n    {\r\n		if (tbs[i].id == \"methodjsedit\" || tbs[i].id == \"methodpostedit\"\r\n		|| tbs[i].id == \"methodjseditorg\" || tbs[i].id == \"methodposteditorg\")\r\n		{\r\n			//alert(\"Please fill out all the textareas2: \" + tbs[i].id);\r\n		}\r\n		else if (tbs[i].value==null || tbs[i].value==\"\")\r\n		{\r\n			alert(\"Please fill out all the textareas: \" + tbs[i].id);\r\n			return false;\r\n		}\r\n    }\r\n	\r\n	var radio_choice = false;\r\n	for (counter = 0; counter < document.forms[\'editfrm\'].methodtypeedit.length; counter++) if (document.forms[\'editfrm\'].methodtypeedit[counter].checked) radio_choice = true; \r\n	if (!radio_choice)\r\n	{\r\n		alert(\"Please select a box\");\r\n		return false;\r\n	}\r\n	return true;\r\n}\r\n\r\n// Login Form\r\nfunction validateLoginForm()\r\n{\r\n	var x=document.forms[0][\"user\"].value\r\n	if (x==null || x==\"\" || x.length < 1)\r\n  	{\r\n		alert(\"Please enter in a username\");\r\n		return false;\r\n	}\r\n	\r\n	var x=document.forms[0][\"pass\"].value\r\n	if (x==null || x==\"\" || x.length < 1)\r\n  	{\r\n		alert(\"Please enter in a password\");\r\n		return false;\r\n	}\r\n}\r\n\r\n// Login Create Form\r\nfunction validateLoginCreateForm()\r\n{\r\n	var x=document.forms[0][\"user\"].value\r\n	if (x==null || x==\"\" || x.length < 1)\r\n  	{\r\n		alert(\"Please enter in a username\");\r\n		return false;\r\n	}\r\n	\r\n	var x=document.forms[0][\"pass\"].value\r\n	if (x==null || x==\"\" || x.length < 1)\r\n  	{\r\n		alert(\"Please enter in a password\");\r\n		return false;\r\n	}\r\n	\r\n	var x=document.forms[0][\"access\"].value\r\n	if (x==null || x==\"\" || x.length < 1)\r\n  	{\r\n		alert(\"Please enter in an access level\");\r\n		return false;\r\n	}\r\n	\r\n	var x=document.forms[0][\"email\"].value\r\n	if (x==null || x==\"\" || x.length < 1)\r\n  	{\r\n		alert(\"Please enter in an email\");\r\n		return false;\r\n	}\r\n}\r\n\r\nfunction receiveSyntaxCheck(passVal)\r\n{\r\n//alert(\"it works\");\r\n	var xmlhttp;\r\n	if (window.XMLHttpRequest)\r\n	{// code for IE7+, Firefox, Chrome, Opera, Safari\r\n		xmlhttp=new XMLHttpRequest();\r\n	}\r\n	else\r\n	{// code for IE6, IE5\r\n		xmlhttp=new ActiveXObject(\"Microsoft.XMLHTTP\");\r\n	}\r\n	\r\n	if (xmlhttp==null)\r\n	{\r\n		alert (\"Your browser does not support XMLHTTP!\");\r\n		return;\r\n	}\r\n	\r\n	xmlhttp.onreadystatechange=function()\r\n	{		\r\n		if (xmlhttp.readyState==4 && xmlhttp.status==200)\r\n		{\r\n\r\n			if (xmlhttp.responseText.indexOf(\'success\') && xmlhttp.responseText.length>0)\r\n			{\r\nalert(xmlhttp.responseText);\r\n\r\n\r\n				if (xmlhttp.responseText.length==2 && xmlhttp.responseText.charCodeAt(0)==13 && xmlhttp.responseText.charCodeAt(1)==10)\r\n				{\r\n					// BAD\r\n					//alert(\"bad\");\r\n					//window.setTimeout(receiveChat,5000);\r\n					//var p = document.createElement(\'p\');\r\n					//p.innerHTML = xmlhttp.responseText;\r\n					//document.getElementById(\"chatwindow\").appendChild(p);\r\n				}\r\n				else\r\n				{\r\n					//var rt = xmlhttp.responseText;\r\n					//var at_loc = rt.lastIndexOf(\'at\');\r\n					//var rt_end = rt.length;\r\n					//var ts = rt.substring(at_loc+3,rt_end-6);\r\n\r\n					//var p = document.createElement(\'p\');\r\n					//p.innerHTML = xmlhttp.responseText.substring(0, at_loc-1);\r\n					//document.getElementById(\"lastCheck\").value = ts;\r\n\r\n					//document.getElementById(\"chatwindow\").appendChild(p);\r\n					//bottomScroll();\r\n					//setTimeout(updateChat(), 5000);\r\n					//window.setTimeout(receiveChat,5000);\r\n				}\r\n			}\r\n			else\r\n			{\r\n				alert(\"No output\");\r\n			}\r\n		}\r\n	}\r\n	//var pass = \"index.php?ajax_syntax_check&receive=\"+escape(document.getElementById(\"sendUser\").value)+\"&t=\" + Math.random() + \"&ts=\" + escape(document.getElementById(\"lastCheck\").value);\r\n//var pass = \"index.php?ajax_syntax_check&txtcode=\"+escape(passVal)+\"&t=\" + Math.random();\r\n	\r\n	//xmlhttp.open(\"POST\",pass,true);\r\n\r\n\r\nvar params = \"txtcode=\"+escape(passVal);\r\nxmlhttp.open(\"POST\",\"index.php?toolkit_edit_method&syntax\",true);\r\nxmlhttp.setRequestHeader(\"Content-type\",\"application/x-www-form-urlencoded\");\r\nxmlhttp.setRequestHeader(\"Content-length\", params.length);\r\nxmlhttp.setRequestHeader(\"Connection\", \"close\");\r\n	//xmlhttp.send(null);\r\n//xmlhttp.send(\"txtcode=\"+escape(passVal));\r\nxmlhttp.send(params);\r\n}','2012-08-04 06:16:22',2,3,'','','',''),(150,'cms_preload_error','/* 	************************** Error Handlers **************************\r\n * \r\n * cms_er ror_handler() 	- must set set_er ror_handler(\"cms_er ror_handler\") in the index.php to set the PHP error handler\r\n * cms_sh ow_error() 	- called by appearance/footer which will display a DIV if an error occurs\r\n * gather_er ror_info()	- called by cms_er ror_handler AND any other method (like DB) if I control the error catching\r\n * print_array()		- prints an array so it is easy to read\r\n * \r\n * restore_er ror_handler();					- set back to the default error handler if my error messages are not helping\r\n * set_er ror_handler(\"cms_er ror_handler\");	- set the default error handler to my function\r\n */\r\nfunction shutdownFunction()\r\n{\r\n	$error = error_get_last();\r\n\r\n	// Ignore errors is only to prevent PHP Syntax Check from showing up as a real error\r\n\r\n	if ($error!=NULL && !isset($_SESSION[\'ignore_errors\']))\r\n	{\r\n		$handle = false;\r\n		switch ($error[\'type\'])\r\n		{\r\n			case 1:\r\n			    	$handle = true;\r\n				break;\r\n			case 4:\r\n			    	$handle = true;\r\n				break;\r\n			case 16:\r\n			    	$handle = true;\r\n				break;\r\n			case 32:\r\n			    	$handle = true;\r\n				break;\r\n			case 64:\r\n			    	$handle = true;\r\n				break;\r\n			case 128:\r\n			    	$handle = true;\r\n				break;\r\n			case 2048:\r\n			    	$handle = true;\r\n				break;\r\n			default:\r\n			    	//$handle = false;\r\n				break;\r\n		}\r\n		if ($handle) cms_error_handler($error[\'type\'], $error[\'message\'], $error[\'file\'], $error[\'line\']);\r\n	}\r\n	else\r\n	{\r\n		// A successful pageload will clear up the error loop variable\r\n		if (isset($_SESSION[\'error_loop\'])) unset($_SESSION[\'error_loop\']);\r\n\r\n	    	// I tried this and it throws a lot of errors msgs\r\n	    	//sendText(\'shutdownFunction\', \'Found error code: \'.$error[\'type\'].$error[\'message\']);\r\n	}\r\n\r\n	// Errors are only ignored when using the PHP Syntax Check in the Code Editor\r\n	if (isset($_SESSION[\'ignore_errors\'])) unset($_SESSION[\'ignore_errors\']);\r\n\r\n	// Wait until the page is not being redirect to show the error message\r\n	if (function_exists(\'cms_show_error\') && !isset($_SESSION[\'wait\'])) echo cms_show_error();\r\n\r\n	// Unset the wait variable so the User Message and Error Message (if applicable) will display on the next page load\r\n	if (isset($_SESSION[\'wait\'])) unset($_SESSION[\'wait\']);\r\n} \r\n\r\nfunction cms_error_handler($errno, $errstr, $errfile, $errline)\r\n{\r\n	// If this error code is not included in error_reporting (anything with a @ at the beginning is ignored)\r\n	/*if (!(error_reporting() & $errno))\r\n	{\r\n		//die(error_reporting().\',\'.$errno);\r\n		// No error message\r\n		//return;\r\n	}*/\r\n\r\n	if (error_reporting()===0) { return; }\r\n\r\n	$lasterror = array();\r\n	$lasterror[\'message\'] = $errstr;\r\n	$lasterror[\'type\'] = $errno;\r\n	$lasterror[\'file\'] = $errfile;\r\n	$lasterror[\'line\'] = $errline;\r\n\r\n	$es1 = htmlentities($errstr, ENT_QUOTES, \"UTF-8\");\r\n	$es2 = htmlentities($errfile, ENT_QUOTES, \"UTF-8\");\r\n	$lasterror = new ErrorException($es1, $errno, 0, $es2, $errline);\r\n	\r\n	//echo $errstr;\r\n	//echo \"<br /><br />\";\r\n	\r\n	// Suppress the output, save to a variable (must trace from here)\r\n	// Source: http://stackoverflow.com/questions/171318/how-do-i-capture-php-output-into-a-variable\r\n	/*ob_start();\r\n	debug_print_backtrace();\r\n	$trace = ob_get_contents();\r\n	ob_end_clean();*/\r\n	$trace = \'\'; // The above code was causing a memory error because everything was being stored, backtrace is not necessary\r\n	\r\n	gather_error_info($lasterror, false, $trace, false);\r\n	\r\n	// Get the last error in PHP\r\n	//$lasterror = error_get_last();\r\n	\r\n	// For the error to be thrown\r\n	//throw new ErrorException($lasterror[\'message\'], $lasterror[\'type\'], 0, addslashes($lasterror[\'file\']), $lasterror[\'line\']);\r\n	return false;\r\n}\r\n\r\nfunction cms_show_error()\r\n{\r\n	$errormsg = \'\';\r\n	\r\n	if (isset($_SESSION[\'error\']))\r\n	{\r\n		$qry = \"INSERT INTO log_error (userid,error_time,error_details) VALUES (1,CURRENT_TIMESTAMP,?)\";\r\n		$params = array($_SESSION[\'error\']);\r\n		$result = query_execute($qry, $params);\r\n	\r\n		$etime = $_SESSION[\'error\'];\r\n		unset($_SESSION[\'error\']);\r\n		if (isset($_SESSION[\'errortop\'])) unset($_SESSION[\'errortop\']);\r\n	\r\n		$errormsg = \'\';\r\n		if (isGroupMember(1))\r\n		{\r\n	\r\n			$errormsg = <<< OUTPUT\r\n	\r\n<!--User Popup-->\r\n	<div id=\'PopUpError\'>\r\n\r\n	<h2 style=\"border: none;\">Error Message</h2>	\r\n	<p>{$etime}</p> \r\n		<div style=\'text-align: right;\'>\r\n			<a onmouseover=\'this.style.cursor=\"pointer\" \' onfocus=\'this.blur();\' onclick=\"document.getElementById(\'PopUpError\').style.display = \'none\' \" >\r\n				<p type=\"psulink\">Close</p>\r\n			</a>\r\n		</div>\r\n	</div>\r\n<!--User Popup-->\r\n\r\nOUTPUT;\r\n		}\r\n	}\r\n	return $errormsg;\r\n}\r\n	\r\nfunction gather_error_info($e, $sql, $trace, $show_user_output=false)\r\n{\r\n	// Set all the error information to a session variable called error		\r\n	// Only show safe information that won\'t harm the server\r\n	$slim = true;\r\n				\r\n	$errorout = \'\';\r\n\r\n	// Only output the previous error message heading\r\n	if (isset($_SESSION[\'errortop\'])) $errorout .= $_SESSION[\'errortop\'];\r\n	\r\n	$eredirect = false;\r\n\r\n	if ($sql)\r\n	{\r\n		// Show the basic error message\r\n		$errorout .= \"<b><u>Database Error</u></b>\";\r\n		$errorout .= \"<br />\";\r\n		\r\n		$errorout .= $e->getMessage();\r\n	}\r\n	else\r\n	{\r\n		// Show the basic error message\r\n		$errorout .= \"<b><u>Website Error</u></b>\";\r\n		$errorout .= \"<br />\";\r\n		\r\n		$econstant = \'\';\r\n		$edscription = \'\';\r\n\r\n		switch ($e->getCode())\r\n		{\r\n			case 1:\r\n				$econstant = \"FATAL\";\r\n				$edescription = \"Fatal run-time error. Execution of the script is halted.\";\r\n				$eredirect = true;\r\n				break;\r\n			case 2:\r\n				$econstant = \"WARNING\";\r\n				$edescription = \"Non-fatal run-time error. Execution of the script is not halted.\";\r\n				break;\r\n			case 4:\r\n				$econstant = \"PARSE\";\r\n				$edescription = \"Compile-time parse errors. Parse errors should only be generated by the parser.\";\r\n				break;\r\n			case 8:\r\n				$econstant = \"NOTICE\";\r\n				$edescription = \"Run-time notice. The script found something that might be an error, but could also happen when running a script normally.\";\r\n				break;\r\n			case 16:\r\n				$econstant = \"CORE_ERROR\";\r\n				$edescription = \"Fatal errors that occur during PHP\'s initial startup.\";\r\n				$eredirect = true;\r\n				break;\r\n			case 32:\r\n				$econstant = \"CORE_WARNING\";\r\n				$edescription = \"Warnings (non-fatal errors) that occur during PHP\'s initial startup.\";\r\n				break;\r\n			case 64:\r\n				$econstant = \"COMPILE_ERROR\";\r\n				$edescription = \"Fatal compile-time errors.\";\r\n				$eredirect = true;\r\n				break;\r\n			case 128:\r\n				$econstant = \"COMPILE_WARNING\";\r\n				$edescription = \"Compile-time warnings (non-fatal errors).\";\r\n				break;\r\n			case 256:\r\n				$econstant = \"USER_ERROR\";\r\n				$edescription = \"Fatal user-generated error. This is like an E_ERROR set by the programmer using the PHP function trigger_error().\";\r\n				$eredirect = true;\r\n				break;\r\n			case 512:\r\n				$econstant = \"USER_WARNING\";\r\n				$edescription = \"Non-fatal user-generated warning. This is like an E_WARNING set by the programmer using the PHP function trigger_error().\";\r\n				break;\r\n			case 1024:\r\n				$econstant = \"USER_NOTICE\";\r\n				$edescription = \"User-generated notice. This is like an E_NOTICE set by the programmer using the PHP function trigger_error().\";\r\n				break;\r\n			case 2048:\r\n				$econstant = \"STRICT\";\r\n				$edescription = \"Suggested changes to code which will ensure the best interoperability and forward compatibility of your code..\";\r\n				break;\r\n			case 4096:\r\n				$econstant = \"RECOVERABLE_ERROR\";\r\n				$edescription = \"Catchable fatal error. This is like an E_ERROR but can be caught by a user defined handle (see also set_err or_handler()).\";\r\n				break;\r\n			case 8191:\r\n				$econstant = \"DEPRECATED\";\r\n				$edescription = \"Run-time notices. Warnings about code that will not work in future versions.\";\r\n				break;\r\n			case 16384:\r\n				$econstant = \"USER_DEPRECATED\";\r\n				$edescription = \"User-generated warning message.\";\r\n				break;\r\n			case 32767:\r\n				$econstant = \"ALL\";\r\n				$edescription = \"All errors and warnings, as supported, except of level E_STRICT prior to PHP 5.4.0.\";\r\n				break;\r\n			default:\r\n				$econstant = \"ERROR\";\r\n				$edescription = \"Problem with code.\";\r\n		}\r\n		\r\n		//$errorout .= print_array($e);\r\n		$errorout .= \"<b>\".$econstant.\":</b> \".$e->getMessage();\r\n		$errorout .= \"<br />\";\r\n		$errorout .= \'<b>\'.$e->getFile().\'</b> on line <b>\'.$e->getLine().\'</b>\';\r\n		//$errorout .= \"<br />\";\r\n		//$errorout .= \'<b>Notice:</b> \'.$edescription;\r\n	}\r\n	\r\n	$errorout .= \"<br />\";\r\n	//$errorout .= \"<br />\";\r\n	\r\n	// Save the current error message (without all the crap below)\r\n	$_SESSION[\'errortop\'] = $errorout;\r\n	\r\n	// Show every step to get to the error message\r\n	//$errorout .= \"<b><u>Backtrace</u></b>\";\r\n	//$errorout .= \"<br />\";\r\n	//$errorout .= addslashes($trace);\r\n	$errorout .= htmlentities($trace, ENT_QUOTES, \"UTF-8\");\r\n\r\n	//$errorout .= \"<pre>\";\r\n	//$errorout .= debug_backtrace();\r\n	//$errorout .= \"</pre>\";\r\n	\r\n	//$errorout .= array_walk(debug_backtrace(),create_function(\'$a,$b\',\'print \"{$a[\\\'function\\\']}()(\".basename($a[\\\'file\\\']).\":{$a[\\\'line\\\']}); \";\'));\r\n	//$errorout .= array_walk( debug_backtrace(), create_function( \'$a,$b\', \'print \"<br /><b>\". basename( $a[\\\'file\\\'] ). \"</b> &nbsp; <font color=\\\"red\\\">{$a[\\\'line\\\']}</font> &nbsp; <font color=\\\"green\\\">{$a[\\\'function\\\']} ()</font> &nbsp; -- \". dirname( $a[\\\'file\\\'] ). \"/\";\' ) );\r\n	//$errorout .= print_array(debug_backtrace());\r\n	//$errorout .= \"<br />\";\r\n	\r\n	// Save the current error message (without all the crap below)\r\n	//$_SESSION[\'errortop\'] = htmlentities($errorout, ENT_QUOTES, \"UTF-8\");\r\n	$_SESSION[\'errortop\'] = $errorout;\r\n	\r\n	//$errorout .= \"<br />\";\r\n	\r\n	//$errorout .= \"<b><u>Additional Information</u></b>\";\r\n	//$errorout .= \"<br />\";\r\n	$errorout .= \'<h4>Additional Information</h4>\';\r\n	\r\n	// Only uncomment this when testing with CMDline\r\n	//echo $trace;\r\n	\r\n	if (isset($_SERVER[\'REMOTE_ADDR\']))\r\n	{\r\n		$user_agent = \'Not specified\';\r\n		if (isset($_SERVER[\'HTTP_USER_AGENT\'])) $user_agent = $_SERVER[\'HTTP_USER_AGENT\'];\r\n\r\n		$errorout .= \"<b>Remote Address:</b> \".$_SERVER[\'REMOTE_ADDR\'];$errorout .= \"<br />\";\r\n		$errorout .= \"<b>Browser:</b> \".$user_agent;$errorout .= \"<br />\";\r\n		\r\n		if (isset($_SERVER[\'HTTP_REFERER\'])) $errorout .= \"<b>Previous Page:</b> \".$_SERVER[\'HTTP_REFERER\'];$errorout .= \"<br />\";\r\n		\r\n		$errorout .= \"<b>Query:</b> \".$_SERVER[\'QUERY_STRING\'];$errorout .= \"<br />\";\r\n		$errorout .= \"<b>Method:</b> \".$_SERVER[\'REQUEST_METHOD\'];$errorout .= \"<br />\";\r\n		$errorout .= \"<b>PHP File:</b> \".$_SERVER[\'PHP_SELF\'];$errorout .= \"<br />\";\r\n		$errorout .= \"<b>PHP Script:</b> \".$_SERVER[\'SCRIPT_NAME\'];$errorout .= \"<br />\";\r\n		$errorout .= \"<b>URI:</b> \".$_SERVER[\'REQUEST_URI\'];$errorout .= \"<br />\";\r\n		$errorout .= \"<b>Protocol:</b> \".$_SERVER[\'SERVER_PROTOCOL\'];$errorout .= \"<br />\";\r\n		$errorout .= \"<br />\";\r\n		\r\n		// These didn\'t output anything for me\r\n		/*$errorout .= $php_errormsg;\r\n		$errorout .= $HTTP_RAW_POST_DATA;\r\n		$errorout .= $http_response_header;\r\n		$errorout .= $argc;\r\n		$errorout .= $argv;*/\r\n	}\r\n	\r\n	if (!empty($_SESSION))\r\n	{\r\n		$errorout .= \"<b><u>Session</u></b>\";\r\n		ob_start();\r\n		$errorout .= print_array(print_array_better($_SESSION));\r\n		$errorout .= ob_get_contents();\r\n		ob_end_clean();\r\n		$errorout .= \"<br />\";\r\n	}\r\n\r\n	if (!empty($_POST))\r\n	{\r\n		$errorout .= \"<b><u>Post</u></b>\";\r\n		ob_start();\r\n		$errorout .= print_array($_POST);\r\n		$errorout .= ob_get_contents();\r\n		ob_end_clean();\r\n		$errorout .= \"<br />\";\r\n	}\r\n	\r\n	if (!empty($_GET))\r\n	{\r\n		$errorout .= \"<b><u>Get</u></b>\";\r\n		ob_start();\r\n		$errorout .= print_array($_GET);\r\n		$errorout .= ob_get_contents();\r\n		ob_end_clean();\r\n		$errorout .= \"<br />\";\r\n	}\r\n	\r\n	if (!$slim)\r\n	{\r\n		if (!empty($_ENV))\r\n		{\r\n			$errorout .= \"<b><u>Environment</u></b>\";\r\n			ob_start();\r\n			print_array($_ENV);\r\n			$errorout .= ob_get_contents();\r\n			ob_end_clean();\r\n			$errorout .= \"<br />\";\r\n		}\r\n		\r\n		if (!empty($_COOKIE))\r\n		{\r\n			$errorout .= \"<b><u>Cookie</u></b>\";\r\n			ob_start();\r\n			print_array($_COOKIE);\r\n			$errorout .= ob_get_contents();\r\n			ob_end_clean();\r\n			$errorout .= \"<br />\";\r\n		}\r\n		\r\n		if (!empty($_REQUEST))\r\n		{\r\n			$errorout .= \"<b><u>Request</u></b>\";\r\n			ob_start();\r\n			print_array($_REQUEST);\r\n			$errorout .= ob_get_contents();\r\n			ob_end_clean();\r\n			$errorout .= \"<br />\";\r\n		}\r\n		\r\n		if (!empty($_SERVER))\r\n		{\r\n			$errorout .= \"<b><u>Server</u></b>\";\r\n			ob_start();\r\n			print_array($_SERVER);\r\n			$errorout .= ob_get_contents();\r\n			ob_end_clean();\r\n			$errorout .= \"<br />\";\r\n		}\r\n	}\r\n	\r\n	// Remove the characters that javascript doesn\'t like\r\n	$errorout = str_replace(\"\'\",\"\", $errorout);\r\n	$errorout = str_replace(\"\\\"\",\"\", $errorout);\r\n	$errorout = str_replace(\"\\n\",\"<br />\", $errorout);\r\n	\r\n	$cdate = date_create();\r\n	date_timezone_set($cdate, timezone_open(\'US/Eastern\'));\r\n	$cdate2 = date_format($cdate, \'M d, Y \\a\\t h:i A\');\r\n	\r\n	// Save the good information for the server\r\n	$_SESSION[\'error\'] = $errorout.\"<br /><b>Generated:</b> $cdate2<br /><br />\";\r\n	\r\n	file_put_contents(CMS_ERROR_PHP, $_SESSION[\'error\'].\'<hr><br />\', FILE_APPEND);\r\n\r\n	// If there is a fatal error, redirect immediately\r\n	if ($eredirect)\r\n	{\r\n		// If there are 2 fatal errors in a row, redirect to a page_not_found\r\n		if (isset($_SESSION[\'error_loop\']) && $_SESSION[\'error_loop\']>2) { header(\"location: /page_not_found&loop\"); exit; }\r\n\r\n		if (isset($_SESSION[\'error_loop\'])) $_SESSION[\'error_loop\'] += 1;\r\n		else $_SESSION[\'error_loop\'] = 1;\r\n\r\n		header(\"location: index.php\");\r\n		exit;\r\n	}\r\n\r\n	//msg_user(\"You found a bug!\",\"Calling in the bug squisher now. Please try again later.\");\r\n}\r\n\r\n/* ************************* Array Handlers ************************** */\r\n\r\nfunction print_array_better($ar)\r\n{\r\n	$new = $ar;\r\n	if (isset($new[\'error\'])) unset($new[\'error\']);\r\n	if (isset($new[\'errortop\'])) unset($new[\'errortop\']);\r\n	return $new;\r\n}\r\n\r\nfunction print_array($out)\r\n{\r\n	// Output the array in a pretty