pt>\r\n<script type=\"text/javascript\" src=\"/js/jquery.js\"></script>\r\n<script type=\"text/javascript\" src=\"/inc/jquery/jquery-ui.min.js\"></script>\r\n\r\n<meta property=\"og:title\" content=\"{$title}\" />\r\n<meta property=\"og:type\" content=\"website\" />\r\n<meta property=\"og:url\" content=\"{$constant[\'SITE_FULL\']}\" />\r\n<meta property=\"og:image\" content=\"{$constant[\'SITE_FULL\']}/img/logo/icon128.png\" />\r\n<meta property=\"og:locale\" content=\"en_US\" />\r\n    \r\n<!-- Update your html tag to include the itemscope and itemtype attributes -->\r\n\r\n<!-- Add the following three tags inside head -->\r\n<meta itemprop=\"name\" content=\"{$title}\" />\r\n<meta itemprop=\"description\" content=\"{$description}\" />\r\n<meta itemprop=\"image\" content=\"{$constant[\'SITE_FULL\']}/img/logo/icon128.png\" />\r\n\r\n<!-- Google Analytics -->\r\n{$constant[\'GOOGLE_ANALYTICS\']}\r\n<!-- Google Analytics -->\r\n\r\n<script src=\"/inc/jquery-linedtextarea/jquery-linedtextarea.js\"></script>\r\n<link href=\"/inc/jquery-linedtextarea/jquery-linedtextarea.css\" type=\"text/css\" rel=\"stylesheet\" />\r\n\r\n<!-- Bootstrap, from Twitter -->\r\n	<!-- Le styles -->\r\n	<link href=\"/inc/bootstrap/css/bootstrap.min.css{$update_bootstrap}\" rel=\"stylesheet\" class=\"simpleswitch\">\r\n	<style>\r\n	body {\r\n        	padding-top: {$pad_top}px; /* 60px to make the container go all the way to the bottom of the topbar */\r\n		padding-bottom: {$pad_bottom}px;\r\n	}\r\n	.sidebar-nav {\r\n	padding: 9px 0;\r\n	}\r\n\r\n	textarea {\r\n		font-family: Menlo, Monaco, Consolas, \"Courier New\", monospace;\r\n	}\r\n\r\n	/* Place the popover on top of the nav bar since the titles are very close */\r\n	.popover { z-index: 1031; }\r\n\r\n	</style>\r\n	<link href=\"/inc/bootstrap/css/bootstrap-responsive.css\" rel=\"stylesheet\">\r\n\r\n	<!-- Le HTML5 shim, for IE6-8 support of HTML5 elements -->\r\n	<!--[if lt IE 9]>\r\n		<script src=\"http://html5shim.googlecode.com/svn/trun