th: 500px; height: 300px; }\r\n#PopUpError h2	{ color: black; }\r\n#PopUpError p	{ color: black; }\r\np[type=\"psulink\"] { text-decoration:underline; }\r\n#PopUpNotice	{  position: fixed; right: 20px; bottom: 0px; }\r\n\r\n.three_col	{ -moz-column-count:3; -webkit-column-count:3; column-count:3; }\r\n.two_col	{ -moz-column-count:2; -webkit-column-count:2; column-count:2; }\r\n\r\n.basic_div a	{ text-align: center; text-decoration: none; border: solid 1px silver;\r\n	padding: 10px; font-size: 14px; display: inline-block; margin: 5px; }\r\n\r\n/* ****************************************************************************************************************************************\r\n* Main Styles\r\n* body, containers\r\n**************************************************************************************************************************************** */\r\nbody		{ font: 11px \"proxima-nova-1\",\"proxima-nova-2\",\"Helvetica Neue\",\"Arial\",sans-serif; {$background_image} background-attachment: fixed; }\r\n#content	{ text-align: center; top: 0px; width: 100%; height: 100%;  padding: 10px 0px 10px 0px; position: relative; }\r\n#content_in_0	{ text-align: {$constant[\'SETTING_PAGE_TITLE\']}; padding-bottom: 10px; }\r\n.container	{ text-align: left; }\r\n.container-fluid	{ text-align: left; }','2012-06-21 17:58:58',2,0,'','','',''),(92,'toolkit_control','if (isset($_GET[\'trash\']) && strlen($_GET[\'trash\'])>0)\r\n{\r\n	db_method_trash($_GET[\'trash\']);\r\n	msg_user(\"Trash\",\"Method \".$_GET[\'trash\'].\" moved to the trash!\");\r\n}\r\nelse if (isset($_GET[\'untrash\']) && strlen($_GET[\'untrash\'])>0)\r\n{\r\n	db_method_untrash($_GET[\'untrash\']);\r\n	msg_user(\"Restored from Trash\",\"Method \".$_GET[\'untrash\'].\" restored from the trash!\");\r\n}\r\nelse if (isset($_GET[\'delete\']) && strlen($_GET[\'delete\'])>0 && isset($_GET[\'id\']))\r\n{\r\n	db_method_delete($_GET[\'delete\']);\r\n\r\n	// Delete the method from the access list\r\n	$qry = \"DELETE FROM access_list WHERE method_id=?\";\r\n	$params = array($_GET[\'id\']);\r\n	query_execute($qry, $params);\r\n\r\n	msg_user(\"Deleted from Database\",\"Method \".$_GET[\'delete\'].\" permanently deleted!\");\r\n}\r\nelse if (isset($_GET[\'preview\']) && strlen($_GET[\'preview\'])>0)\r\n{\r\n	$output .= \'<pre class=\"prettyprint linenums\">\';\r\n	\r\n	if (isset($_GET[\'preview_trash\'])) $table = \"php_trash\";\r\n	else  $table = \"php_methods\";\r\n	\r\n	$output .= htmlentities(method_retrieve($_GET[\'preview\'],false,$table));\r\n	$output .= \"</pre>\";\r\n}\r\nelse msg_user(\"Toolkit Control\",\"No valid arguments passed.\");','2012-06-17 08:07:18',2,1,'','Page View','',''),(165,'toolkit_index','if (isset($_GET[\'empty_trash\']))\r\n{\r\n	$output .= <<< OUTPUT\r\n	\r\n<div class=\"fluid-row\">\r\n\r\n<div class=\"span4\">\r\n</div>\r\n\r\n<div class=\"span4\">\r\n<form class=\"well\" onsubmit=\"return validateForm()\" method=\"post\">\r\n<p class=\"help-block\">All pages in the trash will be removed permanently.</p>\r\n<button type=\"submit\" name=\"submit\" class=\"btn\" title=\"Click once to empty\">Empty Trash</button>\r\n</form>\r\n</div>\r\n\r\n<div class=\"span4\">\r\n</div>\r\n\r\n</div>\r\n<div class=\"clear\"></div>\r\n\r\nOUTPUT;\r\n}\r\n\r\n// Show the index page\r\n$index = \'index.php&path=\'.CMS_ROOT;\r\n$define = \'define.php&path=\'.CMS_PHP_SCRIPT;\r\n$sitemap = \'sitemap.xml&path=\'.CMS_ROOT;\r\n$robots = \'robots.txt&path=\'.CMS_ROOT;\r\n$indexinc = \'indexinc.php&path=\'.CMS_HIDDEN;\r\n\r\n/*\r\n$output .= i(\'div\',\'btn-group\',\r\n	a2(\'btn dropdown-toggle\',\'Index\'\r\n	.i(\'span\',\'caret\'),\'#\',\'dropdown\')\r\n	.i(\'ul\',\'dropdown-menu\',\r\n		i(\'li\',\'\',a2(\'\',img(\'Preview\',\'/img/silk/page_white_magnify.png\').\' Preview\',\'toolkit_file_manager&file_view=\'.$index))\r\n		.i(\'li\',\'\',a2(\'\', img(\'Edit\',\'/img/silk/page_white_edit.png\').\' Edit\',\'toolkit_file_manager&file_view=\'.$define))\r\n	)\r\n);\r\n\r\n$output 