*********\r\nfunction query_execute($qry, $params=array(), $sqlwrite=true, $close_db=true)\r\n{\r\n	// Handle memcache\r\n	global $memcache;\r\n	if (isset($memcache)) $memcache->flush();\r\n\r\n	// Handle XCache\r\n	if (extension_loaded(\'xcache\'))\r\n	{\r\n		xcache_clear_cache(XC_TYPE_PHP,0);\r\n		xcache_clear_cache(XC_TYPE_VAR,0);\r\n	}\r\n\r\n	$return = query($qry, $params, $sqlwrite, $close_db, false, true);\r\n\r\n	if (isset($_SESSION[\'id\'])) $id = $_SESSION[\'id\'];\r\n	else $id = 0;\r\n\r\n	$qry_log = \"INSERT INTO log_mysql (userid,change_time,mysql_query,mysql_params,mysql_result) \r\n		VALUES(?,CURRENT_TIMESTAMP,?,?,?)\";\r\n	$params_log = array($id,$qry,print_array_mysql($params),$return);\r\n	if (constant_test(\'SETTING_LOG_DB_CHANGE\',\'enable\')) query_execute_nolog($qry_log, $params_log);\r\n\r\n	return $return;\r\n}\r\n\r\nfunction query_execute_nolog($qry, $params=array(), $sqlwrite=true, $close_db=true)\r\n{\r\n	return query($qry, $params, $sqlwrite, $close_db, false, true);\r\n}\r\n\r\nfunction query($qry, $params=array(), $sql=false, $close_db=true, $count=false, $get_result=false, $temp_tables=false)\r\n{		\r\n	// Source: http://www.populate.it/prepared_query_to_array.html\r\n	// This function easily queries mySQL and projects against SQL injection\r\n	// All the parameters in params() are trimmed before they get to mySQL\r\n	// All the return parameters are cleaned by htmlentities()\r\n\r\n	// Handle memcache\r\n	global $memcache;\r\n	if (isset($memcache) && !$sql)\r\n	{\r\n		$mem_params = \'\';\r\n		if ($params) $mem_params = implode($params);\r\n	\r\n		// Create a lookup key for memcache\r\n		$memcache_key = md5($qry.$mem_params);\r\n		\r\n		// Lookup value in memcache\r\n		$result = $memcache->get($memcache_key);\r\n		// If memcache doesn\'t have the result\r\n		if($result!=null)\r\n		{\r\n			return $result;\r\n		}//else echo $qry.\' \'.$memcache_key.\'<br />\';\r\n	}\r\n\r\n	global $sql_counter_in;\r\n	$sql_counter_in += 1;\r\n\r\n	if (isset($_SESSION[\'sqlcount\']) && constant_test(\'SETTING_PHP_VARIABLES\',\'show\')) $_SESSION[\'sqlcount\']+=1;\r\n\r\n	$protect_output = false;\r\n	\r\n	// Show the error message\r\n	$verbose = true;\r\n	\r\n	// If the settings have the mysql in readonly mode\r\n	if (constant_test(\'SETTING_DB_WRITE\',\'disable\')) $sql = false;\r\n	if (count($params) == 3 && $params[2]==\'SETTING_DB_WRITE\') $sql = true;\r\n	\r\n	try\r\n	{\r\n		// If the user did NOT pass the database credentials, use the default\r\n		if ($sql) $sql = getCMSWriter();\r\n		else $sql = getCMSReader();\r\n		\r\n		// Connect to SQL\r\n		$db = @mysqli_connect($sql[\'host\'], $sql[\'username\'], $sql[\'password\'], $sql[\'database\'], $sql[\'port\']);\r\n		\r\n		// If database connection was unsuccessful\r\n		if (!$db)\r\n		{\r\n			// If there was a problem in the PHP code\r\n			if ($db==\'\')\r\n			{\r\n				// Get the last error in PHP\r\n				$lasterror = error_get_last();\r\n\r\n				// For the error to be thrown\r\n				throw new ErrorException($lasterror[\'message\'], $lasterror[\'type\'], 0, addslashes($lasterror[\'file\']), $lasterror[\'line\']);\r\n			}\r\n			// Else if the error was in SQL, output the error information\r\n			else throw new Exception (mysqli_errno($db).\": \".mysqli_error($db));\r\n		}\r\n\r\n		// Create temp tables\r\n		//if ($temp_tables) mysqli_stmt_execute(mysqli_prepare($db, $temp_tables));\r\n		if ($temp_tables)\r\n		{\r\n			mysqli_multi_query($db, $temp_tables);\r\n			while ($db->next_result()) {;}\r\n		}\r\n\r\n		// If the prepared stmt does not return an error\r\n		if ($preparedStmt = mysqli_prepare($db, $qry))\r\n		{\r\n			// Get the types of inputs (s,i,or d)\r\n			$paramPattern =  getPreparedParamPattern($params);\r\n			\r\n			if (!empty($params))\r\n			{\r\n				// Trim all the variables\r\n				array_walk($params, create_function(\'&$val\', \'$val = trim($val);\'));\r\n			\r\n				// Bind the parameters to the SQL statement\r\n				$test1 = @call_user_func_array(\'mysqli_stmt_bind_param\', refValues(array_merge (array($preparedStmt, $paramPattern), $params)));\r\n			} else $test1 = true;\r\n			\r\n			// If the statement is not valid\r\n			if (!$test1)\r\n			{\r\n				// Get the last error in PHP\r\n				$lasterror = error_get_last();\r\n				\r\n				// For the error to be thrown\r\n				throw new ErrorException($lasterror[\'message\'], $lasterror[\'type\'], 0, addslashes($lasterror[\'file\']), $lasterror[\'line\']);\r\n			}\r\n			\r\n			// Execute the SQL statement\r\n			$exeresult = mysqli_stmt_execute($preparedStmt);\r\n\r\n			// If the statement is not valid\r\n			if (mysqli_errno($db))\r\n			{\r\n				// If the statement caused an error, throw an error\r\n    				throw new Exception (\"<b>Message:</b> \".mysqli_error($db).\"<br /><b>DB Code:</b> \".mysqli_errno($db));\r\n			}\r\n\r\n			if ($get_result)\r\n			{\r\n				//if ($sql) $affected = mysqli_affected_rows($db);\r\n				//else $affected = false;\r\n				//$affected = false;\r\n				$exeresult = mysqli_affected_rows($db);\r\n\r\n				return $exeresult;\r\n			}\r\n\r\n			if ($count)\r\n			{\r\n				//Store the result\r\n				mysqli_stmt_store_result($preparedStmt);\r\n	    \r\n				// Count the number of rows\r\n				$rowcount = mysqli_stmt_num_rows($preparedStmt);\r\n				return $rowcount;\r\n			}\r\n			\r\n			// Get the statement information\r\n			$meta = mysqli_stmt_result_metadata($preparedStmt);\r\n\r\n			// Create a new array for the field names\r\n			$fieldNames = array();\r\n			\r\n			// For each field in the statement\r\n			while ($field = mysqli_fetch_field($meta))\r\n			{\r\n				// Store the column name to an array\r\n			   $fieldNames[] = $field->name;\r\n			}\r\n			\r\n			// Store the array of column names\r\n			$resultRow = array($preparedStmt);\r\n			\r\n			// For each column name\r\n			foreach($fieldNames as $value)\r\n			{\r\n				// Store the cell value in the array\r\n				$resultRow[] = &$$value;\r\n			}\r\n			\r\n			// Bind the column name as the variable\r\n			call_user_func_array(\'mysqli_stmt_bind_result\', $resultRow);\r\n			\r\n			// Create a new array for the results\r\n			$result = array();\r\n			\r\n			// For each record\r\n			while(mysqli_stmt_fetch($preparedStmt))\r\n			{\r\n				// Create a new array for the record\r\n				$thisRow = array();\r\n				\r\n				// For each column\r\n				foreach($fieldNames as $value)\r\n				{\r\n					// Store the cell value to the column in the array\r\n					if ($protect_output) $thisRow[$value] = htmlentities($$value, ENT_QUOTES, \"UTF-8\");\r\n					else $thisRow[$value] = $$value;\r\n				}\r\n				// Store the record to the results array\r\n				$result[] = $thisRow;\r\n			}\r\n\r\n			// Close the statement\r\n			mysqli_stmt_close($preparedStmt);\r\n			\r\n			// If not specified, close the database connection\r\n			if ($close_db) mysqli_close($db);\r\n			\r\n			// If the array is empty, return true because the query completed successfully (no errors)\r\n			//if (empty($result)) $result = true;\r\n			//if ($affected) $result = $affected;\r\n\r\n			// Handle memcache\r\n			if (isset($memcache))\r\n			{	//if (is_array($result)) echo \'yep!\';\r\n				$memcache->set($memcache_key,$result,MEMCACHE_ADD_FLAG,MEMCACHE_ADD_TIMEOUT);\r\n			}\r\n\r\n			// Return either the array with values or the value of true\r\n			return $result;\r\n		}\r\n		// If the prepared stmt DOES return an error\r\n		else\r\n		{\r\n    		// If the statement caused an error, throw an error\r\n    		throw new Exception (\"<b>Message:</b> \".mysqli_error($db).\"<br /><b>DB Code:</b> \".mysqli_errno($db));\r\n		}\r\n	}\r\n	// Catch a PHP error\r\n	catch (ErrorException $e)\r\n	{			\r\n		// If the output should be verbose\r\n		if (showError($verbose) && !isset($_SESSION[\'ignore_errors\']))\r\n		{\r\n			// Suppress the output, save to a variable (must trace from here)\r\n			// Source: http://stackoverflow.com/questions/171318/how-do-i-capture-php-output-into-a-variable\r\n			ob_start();\r\n			debug_print_backtrace();\r\n			$errorout = ob_get_contents();\r\n			ob_end_clean();\r\n	\r\n			// Output the error in a fancybox\r\n			gather_error_info($e, false, $errorout);\r\n		}\r\n	}\r\n	// Catch a mySQL error\r\n	catch (Exception $e)\r\n	{\r\n		// If the output should be verbose\r\n		if (showError($verbose) && !isset($_SESSION[\'ignore_errors\']))\r\n		{\r\n			// Suppress the output, save to a variable (must trace from here)\r\n			// Source: http://stackoverflow.com/questions/171318/how-do-i-capture-php-output-into-a-variable\r\n			ob_start();\r\n			debug_print_backtrace();\r\n			$errorout = ob_get_contents();\r\n			ob_end_clean();\r\n	\r\n			// Output the error in a fancybox\r\n			gather_error_info($e, true, $errorout);\r\n		} \r\n	}\r\n}\r\n\r\nfunction refValues($arr)\r\n{\r\n	// Source: http://stackoverflow.com/questions/3681262/php5-3-mysqli-stmtbind-params-with-call-user-func-array-problem \r\n    if (strnatcmp(phpversion(),\'5.3\') >= 0) //Reference is required for PHP 5.3+\r\n     { \r\n        $refs = array(); \r\n        foreach($arr as $key => $value) \r\n            $refs[$key] = &$arr[$key]; \r\n        return $refs; \r\n    } \r\n    return $arr; \r\n} \r\n\r\nfunction getPreparedParamPattern ($params)\r\n{\r\n	// Source: http://www.populate.it/prepared_query_to_array.html\r\n	// This function easily determines the value type of the value\r\n	\r\n	$paramPattern = \"\";			\r\n	foreach ($params as $thisParam)\r\n	{					\r\n		if (is_string($thisParam))\r\n		{\r\n			$paramPattern .= \"s\";\r\n		}\r\n		else if (is_int($thisParam))\r\n		{\r\n			$paramPattern .= \"i\";\r\n		}\r\n		else if (is_float($thisParam))\r\n		{\r\n			$paramPattern .= \"d\";\r\n		}\r\n		else\r\n		{\r\n			//die(\"Error: Invalid prepare type\");\r\n			//die(\"Error: Invalid prepare type\");\r\n			//echo \"Error: Invalid prepare type: $thisParam\".print_array($params);\r\n			//print_r($params);\r\n			//debug_print_backtrace();\r\n		}\r\n	}\r\n	return $paramPattern;\r\n}','2012-07-02 19:03:31',2,1,'','','',''),(158,'page_welcome','$output .= <<< OUTPUT\r\n<p>You are now logged in...and can\'t really do anything but <a href=\"/page_dynamic_demo\">build dynamic pages</a>. Enjoy!</p>\r\nOUTPUT;\r\n// here','2012-06-25 19:34:15',2,1,'','Welcome','',''),(157,'page_public','$img = img(\'Image Text\',\'/img/logo/icon128.png\');\r\n$output .= <<< OUTPUT\r\n<div class=\"row-fluid\">\r\n	<div class=\"span10\">\r\n		<div class=\"row-fluid\">\r\n			<div class=\"span8\">\r\n				<div class=\"hero-unit\" style=\"padding-left: 130px; background-image:url(\'/img/logo/icon128.png\'); background-position:left; background-repeat:no-repeat;\">\r\n					<h1>FusionLeaf</h1>\r\n					<p>Grow a Web Presence</p>\r\n					<p>\r\n						<a class=\"btn btn-primary btn-large\" href=\"/page_screenshots\">CMS</a>\r\n						<a class=\"btn btn-primary btn-large\" href=\"/page_flstudio\">Studio</a>\r\n					</p>\r\n				</div>\r\n			</div>\r\n			<div class=\"span4\">\r\n				<h3>What is it?</h3>\r\n				<p>FusionLeaf CMS is a dynamic content management system written in PHP. Designed from the ground up, \r\n				FusionLeaf elevates web site control to the next level using MySQL as a database back-end and Nginx \r\n				(pronounced \"engine x\") as the web front-end. FusionLeaf surpasses with its \"change anything\" attitude.</p>\r\n				<p>There are two components: the CMS and Studio.<br />\r\n				<i class=\"icon-search icon-asterisk\"></i> The CMS (FusionLeaf CMS) is a fully functional website \r\n				that allows you to customize every aspect<br />\r\n				<i class=\"icon-search icon-asterisk\"></i> The Studio (FusionLeaf Studio) is a preconfigured \r\n				set of tools that turns any computer with Microsoft Windows into a web server in less than 30 seconds \r\n				(not required for FusionLeaf CMS, just gets it running quickly)</p>\r\n			</div>\r\n		</div>\r\n\r\n		<div class=\"row-fluid\">\r\n			<div class=\"span4\">\r\n				<h3>Not technical?</h3>\r\n				<p>Know anything about SEO (Search Engine Optimization)? \r\n				FusionLeaf handles the technical agenda while you focus on the content. Want a few dropdown menus? \r\n				Fill a couple text boxes and click a few arrows. Assign it to the Anonymous (public) group.\r\n				Add a photo gallery? Drag a couple pictures and write a description for each. \r\n				<a href=\"http://twitter.github.com/bootstrap\">Bootstrap, from Twitter</a> is at the foundation \r\n				of FusionLeaf so all pages use very familiar styling.</p>\r\n			</div>\r\n			<div class=\"span4\">\r\n				<h3>Technical!!</h3>\r\n				<p>If you know PHP, HTML, CSS, JavaScript, jQuery ... you\'ll LOVE what FusionLeaf has for you. \r\n				Try the Code Editor with PHP syntax validation, quick saving, isolated POST handling, CSS variables in PHP, \r\n				notepad, code snippets, multi-line indenting/outdenting, unique title/keywords/description on every page, \r\n				and write all your PHP/HTML/CSS/JavaScript/jQuery from a single tabbed page. Memcache and XCache are also integrated.</p>\r\n			</div>\r\n			<div class=\"span4\">\r\n				<h3>I want control.</h3>\r\n				<p> Good. You should know every little thing about your website. FusionLeaf includes tools to see every logged in \r\n				user along with the ability to kick them. Enable or disable pages with a single click. Grant or deny access to \r\n				pages through intuitive interfaces. View every change that is made on the website or see every database \r\n				modification by user and modification time. Manage every file and folder without using FTP. \r\n				Backup and email the entire website with a single click. View every PHP error that occurs with a detailed \r\n				reporting of who, where, why, and when.</p>\r\n			</div>\r\n		</div>\r\n		\r\n		<div class=\"row-fluid\">\r\n			<div class=\"span4\">\r\n				<h3>Is it fast?</h3>\r\n				<p>Absolutely. Using the integrated PHP debugging tools, you can see exactly how many queries are run on each page \r\n				load as well as the length of time in seconds. Currently, FusionLeaf requires 20 MySQL queries \r\n				to load a blank (uncached) page in .59 seconds. To load the \r\n				same page once it is cached, FusionLeaf requires 2 MySQL queries and loads in .09 seconds. The caching allows \r\n				FusionLeaf to load pages twice as fast with half the load on the database. Nginx allows for the quick \r\n				page loads through its scalable and high performance design.</p>\r\n			</div>\r\n			<div class=\"span4\">\r\n				<h3>Change anything.</h3>\r\n				<p>Don\'t like something? Change it. Change everything. FusionLeaf doesn\'t have hidden or encrypted code. It\'s all \r\n				there to show how powerful it really is. FusionLeaf started from a notepad and a CLI (command-line interface). \r\n				Now FusionLeaf is written entirely from itself using the internal Code Editor. The Code Editor can now modify code \r\n				in the database as well as text in files and includes a Help section with code samples and reference snippets.</p>\r\n			</div>\r\n			<div class=\"span4\">\r\n				<h3>Windows or Linux?</h3>\r\n				<p>FusionLeaf is designed to run on both Windows and Linux operating systems, as well as on servers and desktops. \r\n				FusionLeaf.com is currently hosted on a Linux server while most of the development occurs on a Windows laptop. \r\n				Developers of FusionLeaf can set up a \r\n				Development or \"Localhost\" environment in less than 30 seconds with FusionLeaf Studio (FLS). The current FLS is \r\n				less than 20MB and decompresses to 40MB. One of the \r\n				goals for FusionLeaf is to grow its usage to include offline applications, products, and installers.</p>\r\n			</div>\r\n		</div>\r\n	</div> <!-- span10 -->\r\n	<div class=\"span2\">\r\n		<!--<ul class=\"nav nav-list\">\r\n			<li class=\"nav-header\">Quick Links</li>\r\n			<li><a href=\"/\">Home</a></li>\r\n			<li><a href=\"/page_changelog\">Changelog</a></li>\r\n			<li><a href=\"/page_screenshots\">CMS Screenshots</a></li>\r\n			<li><a href=\"/page_flstudio\">FLS Screenshots</a></li>\r\n		</ul><br />-->\r\n				<div class=\"well\">\r\n					<h4>News</h4>\r\n					<p>Follow all progress through the \r\n					<a href=\"/page_changelog\">Changelog</a> which is updated daily and <a href=\"/page_screenshots\">Screenshot</a> gallery which \r\n					is updated weekly!</p>\r\n				</div>\r\n\r\n<div class=\"well\">\r\n<h4>Current Versions</h4>\r\n<b>CMS:</b> Aug 8, 2012 (<a href=\"http://code.google.com/p/fusionleaf\">v0.4.1</a>)<br />\r\n<b>Studio:</b> Aug 8, 2012 (<a href=\"http://code.google.com/p/fusionleaf\">v0.4.1</a>)</div>\r\n\r\n<div class=\"well\">\r\n<h4>Utilizes</h4>\r\n<i class=\"icon-search icon-asterisk\"></i> <a href=\"http://twitter.github.com/bootstrap\">Bootstrap</a>\r\n<br /><i class=\"icon-search icon-asterisk\"></i> <a href=\"http://github.com/lecar-red/bootstrapx-clickover\">BootstrapX-Clickover</a>\r\n<br /><i class=\"icon-search icon-asterisk\"></i> <a href=\"http://bootswatch.com\">Bootswatch</a>\r\n<br /><i class=\"icon-search icon-asterisk\"></i> <a href=\"http://famfamfam.com\">FamFamFam</a>\r\n<br /><i class=\"icon-search icon-asterisk\"></i> <a href=\"http://acko.net/blog/farbtastic-jquery-color-picker-plug-in\">Farbtastic Color Picker</a>\r\n<br /><i class=\"icon-search icon-asterisk\"></i> <a href=\"http://jquery.com\">jQuery</a>\r\n<br /><i class=\"icon-search icon-asterisk\"></i> <a href=\"http://alan.blog-city.com/jquerylinedtextarea.htm\">JQuery Lined TextArea</a>\r\n<br /><i class=\"icon-search icon-asterisk\"></i> <a href=\"http://lesscss.org\">LESS</a>\r\n<br /><i class=\"icon-search icon-asterisk\"></i> <a href=\"http://leafo.net/lessphp\">Lessphp</a>\r\n<br /><i class=\"icon-search icon-asterisk\"></i> <a href=\"http://memcached.org\">Memcached</a>\r\n<br /><i class=\"icon-search icon-asterisk\"></i> <a href=\"http://mysql.com\">MySQL</a>\r\n<br /><i class=\"icon-search icon-asterisk\"></i> <a href=\"http://nginx.org\">Nginx</a>\r\n<br /><i class=\"icon-search icon-asterisk\"></i> <a href=\"http://php.net\">PHP</a>\r\n<br /><i class=\"icon-search icon-asterisk\"></i> <a href=\"http://code.google.com/p/google-code-prettify\">Prettify</a>\r\n<br /><i class=\"icon-search icon-asterisk\"></i> <a href=\"http://www.phpcaptcha.org\">Securimage</a>\r\n<br /><i class=\"icon-search icon-asterisk\"></i> <a href=\"http://www.komodomedia.com/blog/2008/12/social-media-mini-iconpack\">Social Network Icon Pack</a>\r\n<br /><i class=\"icon-search icon-asterisk\"></i> <a href=\"http://code.google.com/p/twitter-php\">Twitter for PHP</a>\r\n</div>\r\n\r\n	</div><!-- span2 -->\r\n</div> <!-- row-fluid -->\r\n\r\nOUTPUT;\r\n\r\n// Need this to prevent problems with the heredoc','2012-08-08 17:43:57',2,1,'','Grow a Web Presence','',''),(151,'hlp_common_functions','// Output a message to the user\r\nfunction msg_user($title, $msg, $location=false)\r\n{\r\n	$_SESSION[\'message_title\'] = $title;\r\n	$_SESSION[\'message\'] = $msg;\r\n	if ($location) $_SESSION[\'redirect\'] = $location;\r\n\r\n	// Load a new page so notifications are seamless (ignores Page Not Found) - basically a Reload()\r\n	$_SESSION[\'wait\']=true;\r\n}\r\n\r\n// Determines if the current user is a member of the access group (default is Anonymous)\r\nfunction isGroupMember($group_id=3)\r\n{\r\n	// Set the user_id to the session id\r\n	$user_id=0;\r\n	if (isset($_SESSION[\'id\'])) $user_id=$_SESSION[\'id\'];\r\n\r\n	// If the user is a member of the Anonymous group no NOT logged in, return true\r\n	if ($group_id==3 && $user_id==0) return true;\r\n	// Else if the user is a member of the Authenticated group and is logged in, return true\r\n	else if ($group_id==2 && $user_id>0) return true;\r\n\r\n	// Check the database for the group\r\n	$qry = \"SELECT group_id FROM access_membership WHERE user_id=? AND group_id=? LIMIT 1\";\r\n	$params = array($user_id,$group_id);\r\n	$result = query($qry, $params);\r\n	\r\n	// If the user is a member of the group, return true\r\n	if (count($result)==1) return true;\r\n	// Else the user is not a member of the group, return false\r\n	else return false;\r\n}\r\n\r\nfunction send_mail($to=BACKUP_EMAIL,$subject=\"Database Backup\", $message=\'This is an automated email.\',$file=false)\r\n{\r\n	// Source: http://geekology.co.za/article/2009/06/how-to-send-emails-with-attachments-using-php-mail-function\r\n	$random_hash = md5(date(\'r\', time()));\r\n	$headers = \"From: noreply@\".SITE_DOMAIN.\"\\r\\nReply-To: noreply@\".SITE_DOMAIN;\r\n	\r\n	if ($file && file_exists($file))\r\n	{\r\n	$headers .= \"\\r\\nContent-Type: multipart/mixed; boundary=\\\"PHP-mixed-\".$random_hash.\"\\\"\";\r\n	$attachment = chunk_split(base64_encode(file_get_contents($file)));\r\n	\r\n	$message = \"\r\n--PHP-mixed-$random_hash\r\nContent-Type: multipart/alternative; boundary=\'PHP-alt-$random_hash\'\r\n--PHP-alt-$random_hash\r\nContent-Type: text/plain; charset=\'iso-8859-1\'\r\nContent-Transfer-Encoding: 7bit\r\n\r\nThis is an automated email.\r\n\r\n--PHP-alt-$random_hash\r\nContent-Type: text/html; charset=\'iso-8859-1\'\r\nContent-Transfer-Encoding: 7bit\r\n\r\n<p>This is an <b>automated</b> email.</p>\r\n\r\n--PHP-alt-$random_hash--\r\n\r\n--PHP-mixed-$random_hash\r\nContent-Type: application/zip; name=\".basename($file).\"\r\nContent-Transfer-Encoding: base64\r\nContent-Disposition: attachment\r\n\r\n$attachment\r\n--PHP-mixed-$random_hash--\";\r\n	}\r\n	$result = mail($to, $subject, $message, $headers);\r\n	\r\n	if ($result) msg_user(\"Send Mail\",\"Email was sent successfully!\");\r\n	else msg_user(\"Send Mail\",\"Email was NOT sent successfully.\");\r\n}\r\n\r\nfunction database_size()\r\n{\r\n	$qry = \'SELECT table_schema \"name\",sum( data_length + index_length ) \"size\" FROM information_schema.TABLES WHERE table_schema = \"\'.DB_DATABASE.\'\" GROUP BY table_schema\';\r\n	$params = array();\r\n	$result = query($qry, $params);\r\n	\r\n	if (isset($result[0][\'size\']))\r\n	{\r\n		$db_size = $result[0][\'size\'];\r\n		if ($db_size>(1024*1024))\r\n		{\r\n			$db_size = round($db_size/1024/1024,2).\" MB\";\r\n		}else $db_size = round($db_size/1024,2).\" KB\";\r\n	\r\n	} else $db_size = \'unavailable\';\r\n	return $db_size;\r\n}\r\n\r\nfunction secure_password($user,$pass)\r\n{\r\n	// Username is NOT case sensitive, Password IS case sensitive\r\n	// Uses Standard DES to encrypt\r\n	return md5(crypt(md5(trim($pass)),UNIQUE_ID));\r\n}\r\n\r\nfunction secure_username($user)\r\n{\r\n	// Username is NOT case sensitive, Password IS case sensitive\r\n	// Uses Standard DES to encrypt\r\n	return strtolower(trim($user));\r\n}\r\n\r\nfunction random_phrase()\r\n{\r\n	$array = array();\r\n	$array[] = \'surfing in Costa Rica\';\r\n	$array[] = \'skiing on the Alps\';\r\n	$array[] = \'climbing Mount Everest\';\r\n	$array[] = \'sailing in Bermuda\';\r\n	$array[] = \'on an African safari\';\r\n	$array[] = \'taking a sabbatical in Singapore\';\r\n	$array[] = \'on a journey to Mecca\';\r\n	$array[] = \'swimming with the dolphins\';\r\n	$array[] = \'feeding the gerbils\';\r\n	$array[] = \'flying a kite in London\';\r\n	$array[] = \'hang gliding in Bali\';\r\n	$array[] = \'skydiving in New Zealand\';\r\n	return $array[rand(0,count($array)-1)].\' so it is \';\r\n}\r\n\r\nif (!function_exists(\'get_bool\'))\r\n{\r\n	// Returns the boolean value of a string\r\n	function get_bool($v)\r\n	{\r\n		if (strlen($v)==5) return false;\r\n		else return true;\r\n	}\r\n}\r\n\r\n// Creates a popover box that allows updating to the database\r\nfunction smartDB($row,$column,$table)\r\n{\r\n	// If the user is not an admin, just return the plain value\r\n	if (!isGroupMember(1))\r\n	{\r\n		return $row[$column];\r\n	}\r\n	// If the user IS an admin, but the id is missing, just return the plain value\r\n	else if (!isset($row[\'id\']))\r\n	{\r\n		$trace=debug_backtrace(false);\r\n		$str = \'The function was called on line \'.$trace[0][\'line\'].\' of file \'.basename($trace[0][\'file\']).\'. \';\r\n		$str .= \'The request URI was \'.$_SERVER[\'REQUEST_URI\'].\'.\';\r\n\r\n		$mark = \'<i class=\"icon-exclamation-sign\"></i>\';\r\n		$icon = \'<a href=\"#\" rel=\"popover\" \r\n			data-content=\"ID was not passed to smartDB correctly. \'.$str.\'\">\'.$mark.\'</a>\';\r\n		return $row[$column].\' \'.$icon;\r\n	}\r\n	$rand = rand();\r\n	$html_safe = htmlentities($row[$column], ENT_QUOTES, \'UTF-8\');\r\n	$return = <<< OUTPUT\r\n	<p href=\"#\" style=\"display: inline-block; cursor: pointer;\" rel=\"clickover\"\r\n	data-content=\"\r\n	<form class=\'form-horizontal\' name=\'form_{$rand}\' id=\'form_{$rand}\'>\r\n		<div class=\'control-group\'>\r\n			<label></label>\r\n			<input type=\'hidden\'name=\'db_table\' value=\'{$table}\'>\r\n			<input type=\'hidden\'name=\'db_column\' value=\'{$column}\'>\r\n			<input type=\'hidden\'name=\'db_id\' value=\'{$row[\'id\']}\'>\r\n			<input type=\'hidden\'name=\'db_old\' value=\'{$row[$column]}\'>\r\n			<input type=\'text\' class=\'span2\' name=\'db_value\' id=\'db_value\' value=\'{$html_safe}\'\r\n			onkeydown=\'return checkEnter_{$rand}()\'>\r\n			<a id=\'post_{$rand}\' class=\'btn btn-info\' onclick=\'updateDB_{$rand}()\'>Update</a>\r\n		</div>\r\n\r\n	</form>\">{$html_safe} <i class=\"icon-pencil\"></i></p>\r\n	<script>\r\n	// Update the database\r\n	function updateDB_{$rand}()\r\n	{\r\n		$.post(\"/hlp_common_functions\", $(\"#form_{$rand}\").serialize(), function(data) {\r\n			//alert(\'{$_SERVER[\'REQUEST_URI\']}\');\r\n			window.location = \"{$_SERVER[\'REQUEST_URI\']}\";\r\n		});\r\n	}\r\n	// Determine if the user clicks enter\r\n	function checkEnter_{$rand}()\r\n	{\r\n		// If the user clicks enter\r\n		if (event.keyCode == 13)\r\n		{\r\n			// POST the data\r\n			updateDB_{$rand}();\r\n			// Return false so the variables won\'t be appended to the URL\r\n			return false;\r\n		}\r\n		// Else allow the character to be accepted\r\n		else return true;\r\n\r\n	}\r\n	</script>\r\nOUTPUT;\r\n	return $return;\r\n}\r\n\r\n// Creates a popover box that allows updating to a file\r\nfunction smartFile($key,$val,$is_key,$file)\r\n{\r\n	// If the user is not an admin, just return the plain value\r\n	if (!isGroupMember(1))\r\n	{\r\n		if ($is_key) return $key;\r\n		else return $val;\r\n	}\r\n	// If the user IS an admin, but the id is missing, just return the plain value\r\n	else if (!file_exists($file) || json_decode(file_get_contents($file),true)==null)\r\n	{\r\n		$trace=debug_backtrace(false);\r\n		$str = \'The function was called on line \'.$trace[0][\'line\'].\' of file \'.basename($trace[0][\'file\']).\'. \';\r\n		$str .= \'The request URI was \'.$_SERVER[\'REQUEST_URI\'].\'.\';\r\n\r\n		$mark = \'<i class=\"icon-exclamation-sign\"></i>\';\r\n		$icon = \'<a href=\"#\" rel=\"popover\" \r\n			data-content=\"File was not passed to smartFile correctly. \'.$str.\'\">\'.$mark.\'</a>\';\r\n		return $key.\' \'.$icon;\r\n	}\r\n	// Decode the file into an array\r\n	$arr = json_decode(file_get_contents($file),true);\r\n\r\n	$rand = rand();\r\n\r\n	// Code to ensure passed values (like *****) are separate from the real values\r\n	if ($is_key)\r\n	{\r\n		$html_safe_real = htmlentities($key, ENT_QUOTES, \'UTF-8\');\r\n		$html_safe_view = htmlentities($key, ENT_QUOTES, \'UTF-8\');\r\n	}\r\n	else\r\n	{\r\n		$html_safe_real = htmlentities($arr[$key], ENT_QUOTES, \'UTF-8\');\r\n		$html_safe_view = htmlentities($val, ENT_QUOTES, \'UTF-8\');\r\n	}\r\n\r\n	$return = <<< OUTPUT\r\n	<p href=\"#\" style=\"display: inline-block; cursor: pointer;\" rel=\"clickover\"\r\n	data-content=\"\r\n	<form class=\'form-horizontal\' name=\'form_{$rand}\' id=\'form_{$rand}\'>\r\n		<div class=\'control-group\'>\r\n			<label></label>\r\n			<input type=\'hidden\'name=\'file_name\' value=\'{$file}\'>\r\n			<input type=\'hidden\'name=\'file_key\' value=\'{$key}\'>\r\n			<input type=\'hidden\'name=\'file_iskey\' value=\'{$is_key}\'>\r\n			<input type=\'text\' class=\'span2\' name=\'file_value\' id=\'file_value\' value=\'{$html_safe_real}\'\r\n			onkeydown=\'return checkEnter_{$rand}()\'>\r\n			<a id=\'post_{$rand}\' class=\'btn btn-info\' onclick=\'updateFile_{$rand}()\'>Update</a>\r\n		</div>\r\n\r\n	</form>\">{$html_safe_view} <i class=\"icon-pencil\"></i></p>\r\n	<script>\r\n	// Update the database\r\n	function updateFile_{$rand}()\r\n	{\r\n		$.post(\"/hlp_common_functions\", $(\"#form_{$rand}\").serialize(), function(data) {\r\n			//alert(data);\r\n			window.location = \"{$_SERVER[\'REQUEST_URI\']}\";\r\n		});\r\n	}\r\n	// Determine if the user clicks enter\r\n	function checkEnter_{$rand}()\r\n	{\r\n		// If the user clicks enter\r\n		if (event.keyCode == 13)\r\n		{\r\n			// POST the data\r\n			updateFile_{$rand}();\r\n			// Return false so the variables won\'t be appended to the URL\r\n			return false;\r\n		}\r\n		// Else allow the character to be accepted\r\n		else return true;\r\n\r\n	}\r\n	</script>\r\nOUTPUT;\r\n	return $return;\r\n}\r\n\r\n// Redirect to the System Settings page\r\nfunction quickSystemSetting()\r\n{\r\n	if (!isGroupMember(1)) return;\r\n	else return \'<a class=\"brand\" href=\"/toolkit_smart_file?file=\'.CMS_FILE_SITE.\'\"><i class=\"icon-pencil icon-white\"></i></a>\';\r\n}\r\n\r\n// Redirect to the Site Settings page\r\nfunction quickSiteSetting()\r\n{\r\n	if (!isGroupMember(1)) return;\r\n	else return \'<a href=\"/toolkit_site_settings\"><i class=\"icon-pencil\"></i></a>\';\r\n}\r\n\r\nfunction startSQLCounter()\r\n{\r\n	// Set the session variable so the number of SQL queries can be counted after a page load\r\n	if (constant_test(\'SETTING_PHP_VARIABLES\',\'show\'))\r\n	{\r\n		if (!isset($_SESSION[\'sqlcount\'])) $_SESSION[\'sqlcount\']=0;\r\n	} else\r\n	{\r\n		if (isset($_SESSION[\'sqlcount\'])) unset($_SESSION[\'sqlcount\']);\r\n	}\r\n}\r\nfunction outputSQLCounter()\r\n{\r\n	$count = 0;\r\n	// Set the session variable so the number of SQL queries can be counted after a page load\r\n	if (constant_test(\'SETTING_PHP_VARIABLES\',\'show\') && isGroupMember(1))\r\n	{\r\n		if (isset($_SESSION[\'sqlcount\']))\r\n		{\r\n			$count = $_SESSION[\'sqlcount\'];\r\n			unset($_SESSION[\'sqlcount\']);\r\n		}\r\n		return \'<div class=\"container-fluid\">SQL queries using JavaScript/jQuery: \'.$count.\'</div>\';\r\n	}else return;\r\n}','2012-08-04 15:19:34',2,1,'// If data is POSTed from the smartDB() function, update the entry in the database\r\nif (isset($_POST[\'db_table\']) && isset($_POST[\'db_column\']) && isset($_POST[\'db_value\']) && isset($_POST[\'db_id\']))\r\n{\r\n	// Build a query that allows a value to be updated ONLY IF the previous values are known\r\n	$qry = \'UPDATE `\'.$_POST[\'db_table\'].\'` SET \'.$_POST[\'db_column\'].\'=? WHERE id=? AND \'.$_POST[\'db_column\'].\'=? LIMIT 1\';\r\n	\r\n	// Pass the new value, ID, and old value\r\n	$params = array($_POST[\'db_value\'],$_POST[\'db_id\'],$_POST[\'db_old\']);\r\n	\r\n	// Execute the query with the query and parameters\r\n	$result = query_execute($qry, $params);\r\n	\r\n	// If the query is successful\r\n	if ($result)\r\n	{\r\n		die(msg_user(\'Value Update\',\'The value was successfully updated:<br />\'\r\n		.$_POST[\'db_old\'].\' to \'.$_POST[\'db_value\'].\' on id \'.$_POST[\'db_id\'].\' in table \'.$_POST[\'db_table\']));\r\n	}\r\n	else\r\n	{\r\n		die(msg_user(\'Value Update\',\'The value was NOT successfully updated:<br />\'\r\n		.$_POST[\'db_old\'].\' to \'.$_POST[\'db_value\'].\' on id \'.$_POST[\'db_id\'].\' in table \'.$_POST[\'db_table\']));\r\n	}\r\n}\r\n\r\n// If data is POSTed from the smartFile() function, update the entry in the file\r\nelse if (isset($_POST[\'file_name\']) && isset($_POST[\'file_key\']) && isset($_POST[\'file_iskey\']) && isset($_POST[\'file_value\']))\r\n{\r\n	if (!file_exists($_POST[\'file_name\']) || json_decode(file_get_contents($_POST[\'file_name\']),true)==null)\r\n	{\r\n		die(msg_user(\'Value Update\',\'The file value was NOT updated:<br />\'\r\n		.$old.\' to \'.$_POST[\'file_value\'].\' on key \'.$_POST[\'file_key\'].\' in file \'.$_POST[\'file_name\']));\r\n	}\r\n	else\r\n	{\r\n		// Decode the file into an array\r\n		$arr = json_decode(file_get_contents($_POST[\'file_name\']),true);\r\n\r\n		$new_arr = array();\r\n\r\n		foreach($arr as $key=>$val)\r\n		{\r\n			if ($_POST[\'file_iskey\']==\'1\' && $key == $_POST[\'file_key\'])\r\n			{\r\n				$new_arr[$_POST[\'file_value\']]=$val;\r\n				$old = $key;\r\n			}\r\n			else if ($_POST[\'file_iskey\']!=\'1\' && $key == $_POST[\'file_key\'])\r\n			{\r\n				$new_arr[$key]=$_POST[\'file_value\'];\r\n				$old = $val;\r\n			}\r\n			else $new_arr[$key]=$val;\r\n		}\r\n	}\r\n	if (file_put_contents($_POST[\'file_name\'],json_encode(array_filter($new_arr))))\r\n	{\r\n		die(msg_user(\'Value Update\',\'The file value was successfully updated:<br />\'\r\n		.$old.\' to \'.$_POST[\'file_value\'].\' on key \'.$_POST[\'file_key\'].\' in file \'.$_POST[\'file_name\']));\r\n	}\r\n	else\r\n	{\r\n		die(msg_user(\'Value Update\',\'The file value was NOT written:<br />\'\r\n		.$old.\' to \'.$_POST[\'file_value\'].\' on key \'.$_POST[\'file_key\'].\' in file \'.$_POST[\'file_name\']));\r\n	}\r\n}','','',''),(156,'cms_pager','// Backbone of the entire CMS, controls security, posting, and page output\r\n// If you screw this up, the CMS will stop functioning...no joke\r\n\r\nfunction isArraySame($array1,$array2)\r\n{\r\n	if(count($array1) == count($array2))\r\n	{\r\n		$result = array_intersect($array1, $array2);\r\n		if(count($array1) == count($result))\r\n		{\r\n			//echo \'Both arrays are same\';\r\n			return true;\r\n		}\r\n		else\r\n		{\r\n			//echo \'array1 and array2 are not same\';\r\n			return false;\r\n		}\r\n	}\r\n	else\r\n	{\r\n		//echo \'array1 and array2 are not same\';\r\n		return false;\r\n	}\r\n}\r\n\r\n$title = SITE_TITLE;\r\n$keyword = SITE_KEYWORDS;\r\n$description = SITE_TAG;\r\n\r\nif (isset($_GET[\'recache\']))\r\n{\r\n	if (isGroupMember(1)) $_SESSION[\'recache\']=true;\r\n	header(\'location: index.php\');\r\n	return;\r\n}\r\n\r\n// If the settings have the site in disable all, except for admins\r\nif (constant_test(\'SETTING_SITE_MAINTENANCE\',\'enable\') && !isGroupMember(1))\r\n{\r\n	$output .= \"Website is currently under construction. Thanks for your patience.\";\r\n}\r\n// If the settings have the site in disable all\r\nif (constant_test(\'SETTING_SITE_DISASTER\',\'enable\'))\r\n{\r\n	die(\"Website is currently under construction. Thanks for your patience.\");\r\n}\r\nif (isset($_SESSION[\'attempts\']) && $_SESSION[\'attempts\']>=5)\r\n{\r\n	$output .= \"The system logged your failed attempts and an administrator has been notified of your IP: \".$_SERVER[\'REMOTE_ADDR\'];\r\n}\r\nelse\r\n{\r\n	// Set these to allow for better error handling of eval statements\r\n	//restore_error_handler();\r\n	ini_set(\'track_errors\', 1);\r\n\r\n	$error_before = error_get_last();\r\n\r\n	// If the user passed a parameter in the URL bar (or clicked on a link)\r\n	if (!isset($_GET) || !empty($_GET))\r\n	{\r\n		// Output the basic navigate Back link\r\n		//$output .= ahome(\"Back\").br().NEWLINE;\r\n\r\n		// For each get parameter (really will only get the first one)\r\n		foreach ($_GET as $key=>$val)\r\n		{\r\n			$clean_key = str_replace(\'/\',\'\',$key);\r\n			//$method = method_retrieve($clean_key,false,\"php_methods\",!empty($_POST));\r\n			//$type = db_method_type($clean_key);\r\n\r\n			$qry = \"SELECT method,post,type,title,keyword,description,id FROM php_methods WHERE name=? LIMIT 1\";\r\n			$params = array($clean_key);\r\n			$result = query($qry, $params);\r\n			if (isset($result[0][\'method\']))\r\n			{\r\n				//$title = $result[0][\'title\'];\r\n				$title = smartDB($result[0],\'title\',\'php_methods\');\r\n				$keyword = $result[0][\'keyword\'];\r\n				$description = $result[0][\'description\'];\r\n				$type = $result[0][\'type\'];\r\n				if (!empty($_POST)) $method = $result[0][\'post\'];\r\n				else $method = $result[0][\'method\'];\r\n			} else $method=false;\r\n	\r\n			// If the method exists\r\n			if ($method !==false && strpos($clean_key,\'cms_\') !== 0)\r\n			{	\r\n				$has_access=isGroupMember(1);\r\n				if (!$has_access)\r\n				{\r\n					// HERE IS WHERE THE ACCESS SHOULD BE CONTROLLED\r\n		\r\n					// Get all the groups with access\r\n					$qry = \"SELECT method_id, access_group.name, access_group.id\r\n					FROM access_list\r\n					INNER JOIN access_group, php_methods\r\n					WHERE group_id=access_group.id AND method_id=php_methods.id AND php_methods.name=?\";\r\n					$params = array($clean_key);\r\n					$result_access = query($qry, $params);\r\n					foreach($result_access as $row)\r\n					{\r\n						$has_access = isGroupMember($row[\'id\']);\r\n						if ($has_access) break;\r\n					}\r\n				}\r\n				if (!$has_access)\r\n				{\r\n					if (isset($_SESSION[\'id\']))\r\n					{\r\n						//$output .= \'You do not have access to this page.\'.br();\r\n						$output .=  catch_output(\'?>\'.method_retrieve(\'page_not_found\'));\r\n					}\r\n					else\r\n					{\r\n						$output .= \'You must login to access this page.\'.br();\r\n						$result = eval(method_retrieve(\'access_login\'));\r\n					}\r\n				}\r\n				else\r\n				{\r\n					// If the user posted data, get the POST part of the method\r\n					if (!empty($_POST))\r\n					{\r\n						// 10 - Dynamic\r\n						if ($type===10)\r\n						{\r\n							$dyn_table_name = trim($result[0][\'method\']);\r\n							if (strlen($dyn_table_name)<1 || strlen($dyn_table_name)>64)\r\n							{\r\n								$output .= \'Dynamic Page does not contain a valid table name\';\r\n								ob_start();\r\n								trigger_error(\'Dynamic Page does not contain a valid table name\');\r\n								ob_end_clean();\r\n							} else\r\n							{\r\n								$append_var = \'$page_name = \"/\'.$clean_key.\'\";$table_dynamic = \"\'.$dyn_table_name.\'\";\';\r\n								eval($append_var.method_retrieve(\'toolkit_dynamic\',false,\'php_methods\',true));\r\n							}\r\n						}\r\n						else\r\n						{\r\n							ob_start();\r\n							// The new line ensures an heredoc at the very end of a method is terminated correctly\r\n							//$clean_eval = eval($method.NEWLINE);\r\n							$clean_eval = eval($method);\r\n							$result = ob_get_contents();\r\n							ob_end_clean();\r\n							$error = error_get_last();\r\n							if ($error!=NULL && !isset($_SESSION[\'ignore_errors\']) && !isArraySame($error,$error_before))\r\n							//if ($clean_eval===false || isset(error_get_last()))\r\n							{\r\n								try\r\n								{\r\n									$output .= \'You found a bug! Calling in the bug squisher now. Please try again <a href=\"/\">later</a>.\';\r\n									//throw new Exception ($error[\'message\'].\" on line \".$error[\'line\'].\" in \".$error[\'file\'].\", code: \".$error[\'code\']);\r\n								}\r\n								// Catch a PHP error\r\n								catch (Exception $e)\r\n								{			\r\n										// Suppress the output, save to a variable (must trace from here)\r\n										// Source: http://stackoverflow.com/questions/171318/how-do-i-capture-php-output-into-a-variable\r\n										ob_start();\r\n										debug_print_backtrace();\r\n										$errorout = ob_get_contents();\r\n										ob_end_clean();\r\n								\r\n										// Output the error in a fancybox\r\n										gather_error_info($e, false, $errorout);\r\n								}\r\n							} else 	$output .= $result;\r\n						}\r\n					}\r\n					// 0 - HTML (default)		\r\n					//else if ($type===0) $output .=  $method;\r\n					// 1 - PHP\r\n					else if ($type===1 || $type===0)\r\n					{\r\n						ob_start();\r\n						// The new line ensures an heredoc at the very end of a method is terminated correctly\r\n\r\n						//$clean_eval = eval($method.NEWLINE);\r\n						if ($type===0) $clean_eval = eval(\'?>\'.$method);\r\n						else $clean_eval = eval($method);\r\n\r\n						$result = ob_get_contents();\r\n						ob_end_clean();\r\n						$error = error_get_last();\r\n						if ($error!=NULL && !isArraySame($error,$error_before))\r\n						//if ($clean_eval===false && isset(error_get_last()))\r\n						{\r\n							try\r\n							{\r\n								$output .= \'You found a bug! Calling in the bug squisher now. Please try again <a href=\"/\">later</a>.\';\r\n								//throw new Exception ($error[\'message\'].\" on line \".$error[\'line\'].\" in \".$error[\'file\'].\", code: \".$error[\'type\'].\',\'.error_reporting());\r\n							}\r\n							// Catch a PHP error\r\n							catch (Exception $e)\r\n							{			\r\n									// Suppress the output, save to a variable (must trace from here)\r\n									// Source: http://stackoverflow.com/questions/171318/how-do-i-capture-php-output-into-a-variable\r\n									ob_start();\r\n									debug_print_backtrace();\r\n									$errorout = ob_get_contents();\r\n									ob_end_clean();\r\n							\r\n									// Output the error in a fancybox\r\n									gather_error_info($e, false, $errorout);\r\n							}\r\n						} else $output .= $result;\r\n					}\r\n					// 10 - Dynamic\r\n					else if ($type===10)\r\n					{\r\n						$dyn_table_name = trim($result[0][\'method\']);\r\n						if (strlen($dyn_table_name)<1 || strlen($dyn_table_name)>64)\r\n						{\r\n							$output .= \'Dynamic Page does not contain a valid table name\';\r\n							ob_start();\r\n							trigger_error(\'Dynamic Page does not contain a valid table name\');\r\n							ob_end_clean();\r\n						} else\r\n						{\r\n							$append_var = \'$page_name = \"/\'.$clean_key.\'\";$table_dynamic = \"\'.$dyn_table_name.\'\";\';\r\n							eval($append_var.method_retrieve(\'toolkit_dynamic\',false,\'php_methods\',false));\r\n						}\r\n					}\r\n				}\r\n	\r\n			}\r\n			else if (isset($_SESSION[\'suppress\'])) {}\r\n			else\r\n			{\r\n				$test_file = $_SERVER[\'REQUEST_URI\'];\r\n				if (file_exists($test_file)) die(readfile($test_file));\r\n				$title = \'Page Not Found\';\r\n				$output .=  catch_output(\'?>\'.method_retrieve(\'page_not_found\'));\r\n			}\r\n			break;\r\n		}\r\n	}\r\n	// Else the user requested the default or main page\r\n	else\r\n	{\r\n		$qry = \"SELECT php_methods.name FROM php_methods INNER JOIN \r\n			php_index WHERE php_methods.id=php_index.page_id AND group_id=? LIMIT 1\";\r\n\r\n		if (isGroupMember(1))\r\n		{\r\n			$params = array(1);\r\n		}\r\n		else if (isset($_SESSION[\'id\']))\r\n		{\r\n			$params = array(2);\r\n		}\r\n		else\r\n		{\r\n			$params = array(3);\r\n		}\r\n\r\n		$result = query($qry, $params);\r\n		if ($result && isset($result[0][\'name\'])) $meth = $result[0][\'name\'];\r\n		else $meth = \'page_public\';\r\n\r\n		// Get the title, keywords, and description\r\n		$qry = \"SELECT title,keyword,description,type,method,id FROM php_methods WHERE name=? LIMIT 1\";\r\n		$params = array($meth);\r\n		$result = query($qry, $params);\r\n		if (isset($result[0][\'title\']))\r\n		{\r\n			//$title = $result[0][\'title\'];\r\n			$title = smartDB($result[0],\'title\',\'php_methods\');\r\n		}\r\n		if (isset($result[0][\'keyword\'])) $keyword = $result[0][\'keyword\'];\r\n		if (isset($result[0][\'description\'])) $description = $result[0][\'description\'];\r\n\r\n		$index_type = 0;\r\n		if (isset($result[0][\'type\'])) $index_type = $result[0][\'type\'];\r\n		// Evaluate as PHP code\r\n		if ($index_type===1)\r\n		{\r\n			$result = eval($result[0][\'method\']);\r\n			$error = error_get_last();\r\n			if ($error!=NULL && !isArraySame($error,$error_before))\r\n			//if ($clean_eval===false || isset(error_get_last()))\r\n			{\r\n				try\r\n				{\r\n					$output .= \'You found a bug! Calling in the bug squisher now. Please try again <a href=\"/\">later</a>.\';\r\n					//throw new Exception ($error[\'message\'].\" on line \".$error[\'line\'].\" in \".$error[\'file\']);\r\n				}\r\n				// Catch a PHP error\r\n				catch (Exception $e)\r\n				{			\r\n						// Suppress the output, save to a variable (must trace from here)\r\n						// Source: http://stackoverflow.com/questions/171318/how-do-i-capture-php-output-into-a-variable\r\n						ob_start();\r\n						debug_print_backtrace();\r\n						$errorout = ob_get_contents();\r\n						ob_end_clean();\r\n				\r\n						// Output the error in a fancybox\r\n						gather_error_info($e, false, $errorout);\r\n				}\r\n			} else 	$output .= $result;\r\n		}\r\n		// Evaluate as HTML code\r\n		else $output .= catch_output(\'?>\'.$result[0][\'method\']);\r\n	}\r\n}\r\n\r\n\r\nif (isset($_SESSION[\'suppress\'])) unset($_SESSION[\'suppress\']);','2012-08-02 21:59:59',2,1,'','','',''),(328,'page_screenshots','$output .= <<< OUTPUT\r\n<div id=\"myCarousel\" class=\"carousel slide\">\r\n  <!-- Carousel items -->\r\n  <div class=\"carousel-inner\" style=\"text-align: center;\">\r\nOUTPUT;\r\n\r\n$arr = glob (CMS_ROOT.DIR_SEP.\'img\'.DIR_SEP.\'screenshots\'.DIR_SEP.\'*.png\');\r\n$count=0;\r\nforeach($arr as $row)\r\n{\r\n	if ($count==0)\r\n	{\r\n		$output .= \'<div class=\"active item\">\';\r\n		$count +=1;\r\n	}\r\n	else $output .= \'<div class=\"item\">\';\r\n	$output .= \'<img src=\"\'.str_replace(CMS_ROOT,\'\',$row).\'\" style=\" display: inline-block;\"/>\';\r\n$output .= \'</div>\';\r\n}\r\n\r\n$output .= <<< OUTPUT\r\n  </div>\r\n  <!-- Carousel nav -->\r\n  <a class=\"carousel-control left\" href=\"#myCarousel\" data-slide=\"prev\">&lsaquo;</a>\r\n  <a class=\"carousel-control right\" href=\"#myCarousel\" data-slide=\"next\">&rsaquo;</a>\r\n</div>\r\nOUTPUT;\r\n\r\n// Need this for the heredoc','2012-07-09 22:06:54',2,1,'','Site Screenshots','',''),(284,'toolkit_index_selection','function page_list($group_id, $group_name)\r\n{\r\n	$output = \'\';\r\n\r\n	// Output the pages\r\n	$result = query(\"SELECT id,name,type,modified,title,keyword,description FROM php_methods ORDER BY name\", array());\r\n	$return=\'\';\r\n\r\n	$selected_page=0;\r\n\r\n	if (isset($_GET[\'page\']) && isset($_GET[\'group\']) && $group_id==$_GET[\'group\'])\r\n	{\r\n		$page_name = $_GET[\'page_name\'];\r\n		$selected_page = $_GET[\'page\'];\r\n		$qry = \"UPDATE php_index SET page_id=?, modified=CURRENT_TIMESTAMP, modifier=? WHERE group_id=? LIMIT 1\";\r\n		$params = array($_GET[\'page\'], $_SESSION[\'id\'], $group_id);\r\n		$result2 = query_execute($qry, $params);\r\n		if ($result2) msg_user(\"Index Update\",\"Index page for group, $group_name, updated to $page_name!\",\"index.php?toolkit_index_selection\");\r\n		else msg_user(\"Index Update\",\"Index page for group, $group_name, was NOT updated to $page_name.\",\"index.php?toolkit_index_selection\");\r\n	}\r\n	else\r\n	{\r\n		$qry = \"SELECT page_id FROM php_index WHERE group_id=? LIMIT 1\";\r\n		$params = array($group_id);\r\n		$result2 = query($qry, $params);\r\n		if ($result2 && isset($result2[0][\'page_id\'])) $selected_page = $result2[0][\'page_id\'];\r\n	}\r\n	$page_counter = 0;\r\n	\r\n	foreach($result as $element)\r\n	{\r\n		$name = $element[\'name\'];\r\n		$id = $element[\'id\'];\r\n	\r\n		if (strpos($name,\'cms_\')===0) {}\r\n		else if (strpos($name,\'hlp_\')===0) {}\r\n		else if (strpos($name,\'db_\')===0) {}\r\n		else if (strpos($name,\'delete_\')===0) {}\r\n		else if (strpos($name,\'style_\')===0) {}\r\n		else if (strpos($name,\'script_\')===0) {}\r\n		else if (strpos($name,\'div_\')===0) {}\r\n		else if (strpos($name,\'html_\')===0) {}\r\n		else\r\n		{\r\n			if ($id==$selected_page) $return .= img(\'Index\',\'/img/silk/tick.png\').\' \';\r\n			else $return .= img(\'Index\',\'/img/silk/tick.png\',false).\' \';\r\n			$return .= a($name,\"toolkit_index_selection&page=$id&group=$group_id&page_name=$name\").\' \'.br();\r\n			$page_counter += 1;\r\n		}\r\n	}\r\n	$output .= \'<div class=\"three_col\">\';\r\n	$output .= $return;\r\n	$output .= \'</div>\';\r\n	return array($output,$page_counter);\r\n}\r\n\r\n$arr = page_list(1, \'Administrators\');\r\n$output .= \'<b>Select the main page for the Administrators security group (\'.$arr[1].\'): </b>\';\r\n$output .= $arr[0].br();\r\n\r\n$arr = page_list(2, \'Authenticated\');\r\n$output .= \'<b>Select the main page for the Authenticated security group (\'.$arr[1].\'): </b>\';\r\n$output .= $arr[0].br();\r\n\r\n$arr = page_list(3, \'Anonymous\');\r\n$output .= \'<b>Select the main page for the Anonymous security group (\'.$arr[1].\'): </b>\';\r\n$output .= $arr[0];','2012-07-09 19:22:53',2,1,'','Main Page Selection','',''),(183,'cms_backbone','// Create the structure for the cached included file\r\n$writing = \'<?php\'.NEWLINE;\r\n\r\n//$writing .= \'if (file_exists(\"inc/cuteeditor_files/include_CuteEditor.php\")) require_once \"inc/cuteeditor_files/include_CuteEditor.php\";\'.NEWLINE;\r\n\r\n	$result_define = query(\"SELECT name, current_value FROM site_setting\", array());\r\n	foreach ($result_define as $row)\r\n	{\r\n		$writing .= \'if (!defined(\"\'.$row[\'name\'].\'\"))define(\"\'.$row[\'name\'].\'\",\"\'.$row[\'current_value\'].\'\");\'.NEWLINE;\r\n	}\r\n\r\n	$writing .= \'$constant = get_defined_constants();\'.NEWLINE;\r\n\r\n	$helpers = method_retrieve(\'hlp_%\', true);\r\n	foreach ($helpers as $hlp)\r\n	{\r\n		$writing .= $hlp[\'method\'];\r\n		$writing .= NEWLINE;\r\n	}\r\n\r\n	$writing .= NEWLINE;\r\n	$writing .= \'$output = \"\";\';\r\n	$writing .= NEWLINE;\r\n\r\n	// $output variable is first used here\r\n	$writing .= method_retrieve(\'cms_pager\').NEWLINE.NEWLINE;\r\n\r\n	$writing .= \'$html_output1 = <<< OUTPUT\'.NEWLINE;\r\n	$writing .= method_retrieve(\'html_doctype\').NEWLINE;\r\n	$writing .= \'<head lang=\"en\">\'.NEWLINE;\r\n	$writing .= NEWLINE.\'OUTPUT;\'.NEWLINE;\r\n\r\n	// $html_output2 is here\r\n	$writing .= method_retrieve(\'html_head\').NEWLINE;\r\n\r\n	$writing .= \'$html_output3 = <<< OUTPUT\'.NEWLINE;\r\n	$writing .= \'</head>\'.NEWLINE.NEWLINE;\r\n	$writing .= \'<body onload=\"prettyPrint()\">\'.NEWLINE;\r\n	$writing .= NEWLINE.\'OUTPUT;\'.NEWLINE;\r\n	\r\n	// $html_output4 is here\r\n	$writing .= method_retrieve(\'div_header\').NEWLINE;\r\n	\r\n	$writing .= \'$html_output5 = <<< OUTPUT\'.NEWLINE;\r\n	//$writing .= \'<div id=\"content\">\'.NEWLINE;\r\n	$writing .= \'<div class=\"container-fluid\"><div id=\"content_in_0\"><h1>$title_page</h1></div>\';\r\n	$writing .= NEWLINE.\'OUTPUT;\'.NEWLINE;\r\n\r\n	$writing .= \'if (isGroupMember(1)){\'.NEWLINE;\r\n	$writing .= \'$html_output5 .= <<< OUTPUT\'.NEWLINE;\r\n	$writing .= \'<div cl