nge_title,change_details) VALUES(?,CURRENT_TIMESTAMP,?,?)\";\r\n		$params = array($id,$_SESSION[\'message_title\'],$_SESSION[\'message\']);\r\n		if (constant_test(\'SETTING_LOG_CHANGE\',\'enable\')) query_execute_nolog($qry, $params);\r\n\r\n		$message = $_SESSION[\'message\'];\r\n		$message_title = $_SESSION[\'message_title\'];\r\n		\r\n		unset($_SESSION[\'message\']);\r\n		unset($_SESSION[\'message_title\']);\r\n		\r\n		$usermessage = <<< OUTPUT\r\n	\r\n<!--User Popup-->\r\n	<!-- <div id=\"PopUpNotice\">\r\n	<h2 style=\"border: none;\">{$message_title}</h2>	\r\n	<p>{$message}</p> \r\n		<div style=\"text-align: right;\">\r\n			<a onmouseover=\"this.style.cursor=\'pointer\';\"  onfocus=\"this.blur();\" onclick=\"document.getElementById(\'PopUpNotice\').style.display = \'none\';\">\r\n				<p type=\"psulink\">Close</p>\r\n			</a>\r\n		</div>\r\n	</div> -->\r\n	<div id=\"PopUpNotice\">\r\n<div class=\"alert alert-info\" style=\"text-align: left;\">\r\n<button class=\"close\" data-dismiss=\"alert\">Ã—</button>\r\n<strong>{$message_title}:</strong> {$message}\r\n</div>\r\n</div>\r\n<!--User Popup-->\r\n	\r\nOUTPUT;\r\n		return $usermessage;\r\n	}\r\n}\r\n\r\nif (isset($_SESSION[\'wait\']))\r\n{\r\n	if (isset($_SESSION[\'redirect\']))\r\n	{\r\n		$redirect = $_SESSION[\'redirect\'];\r\n		unset($_SESSION[\'redirect\']);\r\n	}\r\n	else $redirect = \'index.php\';\r\n\r\n	// Handle memcache\r\n	global $memcache;\r\n	if (isset($memcache)) $memcache->flush();\r\n\r\n	// Handle XCache\r\n	if (extension_loaded(\'xcache\'))\r\n	{\r\n		xcache_clear_cache(XC_TYPE_PHP,0);\r\n		xcache_clear_cache(XC_TYPE_VAR,0);\r\n	}\r\n\r\n	header(\'location: \'.$redirect);\r\n}\r\nelse\r\n{\r\n	echo $output;\r\n	if (function_exists(\'showUserMessage\')) echo showUserMessage();\r\n\r\n	// Write the error message\r\n	//if (function_exists(\'cms_show_error\')) echo cms_show_error();\r\n}','2012-07-02 02:49:51',2,1,'','','',''),(174,'cms_verbose','global $sql_counter_out, $sql_counter_in;\r\n\r\necho \'<div class=\"well\">\';\r\necho \'SQL queries using query/query_execute function: \'.($sql_counter_in-$sql_counter_out);\r\necho \'<br />SQL queries using method_retrieve function: \'.$sql_counter_out;\r\necho \'<br />SQL queries total: \'.$sql_counter_in;\r\necho \'</div>\';\r\necho \'Session\'.print_array($_SESSION);\r\necho \'<br />Server\'.print_array($_SERVER);\r\necho \'<br />Request\'.print_array($_REQUEST);\r\necho \'<br />Post\'.print_array($_POST);\r\necho \'<br />Get\'.print_array($_GET);\r\necho \'<br />Cookie\'.print_array($_COOKIE);\r\necho \'<br />Env\'.print_array($_ENV);\r\necho \'<br />\';\r\nif (function_exists(\'PtimerStop\')) echo PtimerStop();','2012-07-20 21:36:08',2,1,'','','',''),(185,'toolkit_search','function countResults($passed)\r\n{\r\n	$qry = \"SELECT id,name,method,post,type,title,keyword,description FROM php_methods WHERE method COLLATE latin1_bin LIKE ? || post COLLATE latin1_bin LIKE ?\";\r\n	$params = array(\'%\'.$passed.\'%\',\'%\'.$passed.\'%\');\r\n	$result = query($qry, $params);\r\n	\r\n	$return=\'\';\r\n	$counter = 0;\r\n	$page_counter = 0;\r\n	\r\n	// Add in the index\r\n	$file_index = file_get_contents(\"index.php\");\r\n	array_unshift ($result, array(\'id\'=>-1,\'name\'=>\'index.php\',\'method\'=>$file_index ,\'post\'=>\'\',\'type\'=>1,\'path\'=>CMS_ROOT));\r\n\r\n	// Add in the define.php\r\n	$file_index = file_get_contents(CMS_PHP_SCRIPT.DIR_SEP.\"define.php\");\r\n	array_unshift ($result, array(\'id\'=>-1,\'name\'=>\'define.php\',\'method\'=>$file_index ,\'post\'=>\'\',\'type\'=>1,\'path\'=>CMS_PHP_SCRIPT.DIR_SEP));\r\n\r\n	foreach($result as $row)\r\n	{\r\n		// Output the id and name from SQL\r\n	\r\n		if ($row[\'id\']==-1)\r\n		{\r\n			$index = $row[\'name\'].\'&path=\'.$row[\'path\'];\r\n			$elink = a($row[\'name\'], \'toolkit_file_manager&file_edit=\'.$index);\r\n		}\r\n		else\r\n		{\r\n			$elink = \'<a href=\"index.php?toolkit_edit_method=\'.$row[\'name\'].\'&type=\'.$row[\'type\'].\'&title=\'.$row[\'title\'].\'&keyword=\'.$row[\'keyword\'].\'&description=\'.$row[\'description\'].\'\">\'.$row[\'name\'].\'</a>\';\r\n		}\r\n	\r\n		//$num1 = explode($passed,htmlentities($row[\'method\'], ENT_QUOTES, \'UTF-8\'));\r\n		$num1 = explode($passed,$row[\'method\']);\r\n		//$num2 = explode($passed,htmlentities($row[\'post\'], ENT_QUOTES, \'UTF-8\'));\r\n		$num2 = explode($passed,$row[\'post\']);\r\n	\r\n		$n1 = (count($num1)-1);\r\n		$n2 = (count($num2)-1);\r\n	\r\n		if ($n1<1)\r\n		{\r\n			if (strpos($row[\'method\'],$passed)) $n1=1;\r\n			else $n1=0;\r\n		}\r\n		if ($n2<1)\r\n		{\r\n			if (strpos($row[\'post\'],$passed)) $n2=1;\r\n			else $n2=0;\r\n		}\r\n	\r\n		if ($n1>0) $r1 = $n1.\' in Page\';\r\n		else $r1 = \'\';\r\n		if ($n2>0) $r2 = $n2.\' in Post\';\r\n		else $r2 = \'\';\r\n		\r\n		if ($n1>0 && $n2>0) $comma = \', \';\r\n		else $comma = \'\';\r\n	\r\n		// This is to ensure index doesn\'t show up on 0 results\r\n		if ($n1 != 0 || $n2 != 0)\r\n		{\r\n			$return.=\'<br />Method: \'.$elink.\'<br />\';\r\n			$return.=\'Found: \'.$r1.$comma.$r2;\r\n			$return.=\'<br />\';\r\n			$counter = $counter + $n1 + $n2;\r\n			$page_counter += 1;\r\n		}\r\n	}\r\n	// This is to ensure index doesn\'t show up on 0 results\r\n	if ($counter==0) $result = array();\r\n	return array($counter, $result, $return, $page_counter);\r\n}\r\n\r\nif (isset($_GET[\'search\']) && strlen($_GET[\'search\'])>0)\r\n{\r\n	$passed = htmlentities($_GET[\'search\'], ENT_QUOTES, \'UTF-8\');\r\n	$result = countResults($_GET[\'search\']);\r\n\r\n$output .= <<< OUTPUT\r\n\r\n<div class=\"row-fluid\">\r\n<form class=\"well\" method=\"get\">\r\n<input type=\"hidden\" name=\"toolkit_search\" id=\"toolkit_search\" />\r\n<label>Search:</label><input class=\"span4\" type=\"text\" id=\"search\" name=\"search\" placeholder=\"What are you looking for?\" value=\"{$passed}\" />\r\n<p class=\"help-block\">Search is case-sensitive.</p>\r\n<button type=\"submit\" class=\"btn\" title=\"Click once to search\">Search</button>\r\n</form>\r\n</div>\r\n\r\n<script>document.getElementById(\"search\").focus();</script>\r\n\r\nOUTPUT;\r\n\r\n	$output .= \'Options:<br />\';\r\n	$output .= a(\'Reset Search\',\'toolkit_search\').\' or \'.a(\'Search and Replace\',\'toolkit_replace&search=\'.urlencode($_GET[\'search\'])).br();\r\n\r\n	$output .= \'<br /><b>\'.$result[0].\' results found in \'.$result[3].\' page(s)</b><br />\';\r\n	$output .= $result[2];\r\n\r\n}\r\nelse\r\n{\r\n\r\n$output .= <<< OUTPUT\r\n\r\n<div class=\"row-fluid\">\r\n<form class=\"well\" method=\"get\">\r\n<input type=\"hidden\" name=\"toolkit_search\" id=\"toolkit_search\" />\r\n<label>Search:</label><input class=\"span4\" type=\"text\" id=\"search\" name=\"search\" placeholder=\"What are you looking for?\" value=\"\" />\r\n<p class=\"help-block\">Search is case-sensitive.</p>\r\n<button type=\"submit\" class=\"btn\" title=\"Click once to search\">Search</button>\r\n</form>\r\n</div>\r\n\r\n<script>document.getElementById(\"search\").focus();</script>\r\n\r\nOUTPUT;\r\n\r\n	$output .= \'Options:<br />\';\r\n	if (isset($_GET[\'detail\'])) $output .= a(\'Less Detail\',\'toolkit_search\').br();\r\n	else $output .= a(\'More Detail\',\'toolkit_search&detail\').br();\r\n	\r\n	// Show all the methods not in the Trash\r\n	$arr = db_method_list();\r\n	$output .= br().\"Available pages for searching (\".count($arr).\"):\";\r\n	foreach($arr as $element)\r\n	{\r\n		$name = $element[\'name\'];\r\n		/* if (strpos($name,\'cms_\')===0) {}\r\n		else if (strpos($name,\'hlp_\')===0) {}\r\n		else if (strpos($name,\'db_\')===0) {}\r\n		else if (strpos($name,\'style_\')===0) {}\r\n		else $name = a($element[\'name\'], $element[\'name\']); */\r\n		\r\n		//if ($name==$element[\'name\']) $delete=\'\';\r\n		//else $delete=a(\'trash\', \'toolkit_control&trash=\'.$element[\'name\']);\r\n		$delete=\'\';\r\n		\r\n		// Output the method name, edit, delete\r\n		//$output .= br().$name.\" \".a(\'preview\', \'toolkit_control&preview=\'.$element[\'name\']).\" \".a(\'edit\', \'toolkit_edit_method=\'.$element[\'name\'].\'&type=\'.$element[\'type\']).\" \".$delete;\r\n	\r\n		if (isset($_GET[\'detail\']))\r\n		{\r\n			$result = countResults($name);\r\n			$output .= br().a($name,\'toolkit_search&search=\'.$name).\' (\'.$result[0].\' references)\';\r\n		}\r\n		else $output .= br().a($name,\'toolkit_search&search=\'.$name);\r\n	}\r\n}','2012-07-09 21:10:02',2,1,'','Search and Replace','',''),(191,'access_change_pass','<div class=\"fluid-row\">\r\n\r\n<div class=\"span4\">\r\n</div>\r\n\r\n<div class=\"span4\">\r\n<form class=\"well\" onsubmit=\"return validateForm()\" method=\"post\">\r\n<label>Old Password:</label><input class=\"span12\" type=\"password\" id=\"pass_old\" name=\"pass_old\" value=\"\" placeholder=\"case sensitive\" />\r\n<label>New Password:</p><input class=\"span12\" type=\"password\" id=\"pass_new\" name=\"pass_new\" value=\"\" placeholder=\"case sensitive\" />\r\n<div class=\"clear\"></div>\r\n<button type=\"submit\" class=\"btn\" title=\"Click once to change password\">Change</button>\r\n</form>\r\n</div>\r\n\r\n<div class=\"span4\">\r\n</div>\r\n\r\n</div>\r\n\r\n<script>document.getElementById(\"pass_old\").focus();</script>','2012-06-12 04:07:49',2,0,'// Check for required Post values\r\nif (!isset($_POST[\'pass_old\']) || !isset($_POST[\'pass_new\'])) die(\'Invalid Post!\');\r\n\r\n// Query to update password\r\n$qry = \"UPDATE access_user SET password=? WHERE password=? AND id=? LIMIT 1\";\r\n\r\n// Fill the array with the values\r\n//$params = array(md5(trim($_POST[\'pass_new\'])),md5(trim($_POST[\'pass_old\'])),$_SESSION[\'id\']);\r\n$params = array(secure_password($_SESSION[\'username\'], $_POST[\'pass_new\']),\r\n	secure_password($_SESSION[\'username\'], $_POST[\'pass_old\']),\r\n	$_SESSION[\'id\']);\r\n\r\n// Execute the query and return a success or failure\r\n$success = query_execute($qry, $params);\r\n\r\n// Output to the user\r\nif ($success) msg_user(\'Password Change\',\'Password changed successfully\');\r\nelse msg_user(\'Password Change\',\'Password changed failed - invalid password\');','Change Password','',''),(192,'misc_cause_error','$xzipp6 .= \'f\';\r\n\r\n$var = $_POST[\'user\'];\r\n\r\n//PHP or HTML code goes here\r\n//msg_user(\"Hello\",\"I love Justice and Jesus\");\r\n\r\n/*\r\nif (!isset($_POST[\'user\']) || !isset($_POST[\'pass\']))\r\n{\r\necho \'Bad HACKER!\';\r\n}\r\nelse\r\n{\r\n\r\n$qry = \"SELECT id FROM access_user2 WHERE last_name=? ORDER BY id\";\r\n	\r\n$params = array($_GET[\'passed_id\']);\r\n$result = query($qry, $params);\r\n}*/\r\n\r\n//eval(\"strpos2();\");\r\n//$xzipp6 .= \'f\';\r\n//strpos();\r\n//echo $php_errormsg;','2012-06-18 17:24:34',2,1,'','','',''),(196,'toolkit_file_manager','if (isset($_GET[\'show_image\']))\r\n{\r\n	//while(@ob_end_clean());\r\n	$img=$_GET[\'show_image\']; \r\n	$ext = pathinfo($img, PATHINFO_EXTENSION);\r\n	header(\"content-type: image/$ext\"); \r\n	readfile($img);\r\n	exit();\r\n}\r\n\r\n\r\n// If the path is in a GET, use it\r\nif (isset($_GET[\'path\'])) $path = $_GET[\'path\'];\r\nelse\r\n{\r\n$path = CMS_ROOT;\r\n\r\n$dir_name = CMS_HIDDEN.DIR_SEP.\"mysql_backup\";\r\n$img_dir = CMS_ROOT.DIR_SEP.\"img\";\r\n\r\n$output .= <<< OUTPUT\r\n\r\n<p><b>Quick Links:</b></p>\r\n<a href=\"index.php?toolkit_file_manager&path={$dir_name}\">MySQL Backup Folder</a><br />\r\n<a href=\"index.php?toolkit_file_manager&path={$img_dir}\">Web Image Folder</a><br />\r\n<br />\r\n\r\nOUTPUT;\r\n}\r\n\r\n// *********************************************** Create a folder ***********************************************\r\nif (isset($_GET[\'folder_create\']))\r\n{\r\n$output .= <<< OUTPUT\r\n\r\n<div class=\"fluid-row\">\r\n\r\n<div class=\"span4\">\r\n</div>\r\n\r\n<div class=\"span4\">\r\n<form class=\"well\" action=\"index.php?toolkit_file_manager&folder_create&path={$path}\" onsubmit=\"return validateForm()\" method=\"post\">\r\n<h3>Create Folder</h3>\r\n<label>Folder name:</label><input class=\"span12\" type=\"text\" id=\"folder\" name=\"folder\" value=\"\" />\r\n<div class=\"clear\"></div>\r\n<button type=\"submit\" class=\"btn\" title=\"Click once to create folder\">Create</button>\r\n</form>\r\n</div>\r\n\r\n<div class=\"span4\">\r\n</div>\r\n\r\n</div>\r\n<div class=\"clear\"></div>\r\n\r\n<script>document.getElementById(\"folder\").focus();</script>\r\n\r\nOUTPUT;\r\n}\r\n\r\n// *********************************************** Create a file ***********************************************\r\nelse if (isset($_GET[\'file_create\']))\r\n{\r\n$output .= <<< OUTPUT\r\n\r\n<div class=\"fluid-row\">\r\n\r\n<div class=\"span4\">\r\n</div>\r\n\r\n<div class=\"span4\">\r\n<form class=\"well\" action=\"index.php?toolkit_file_manager&file_create&path={$path}\" onsubmit=\"return validateForm()\" method=\"post\">\r\n<h3>Create File</h3>\r\n<label>File name:</label><input class=\"span12\" type=\"text\" id=\"file\" name=\"file\" value=\"\" />\r\n<div class=\"clear\"></div>\r\n<button type=\"submit\" class=\"btn\" title=\"Click once to create file\">Create</button>\r\n</form>\r\n</div>\r\n\r\n<div class=\"span4\">\r\n</div>\r\n\r\n</div>\r\n<div class=\"clear\"></div>\r\n\r\n<script>document.getElementById(\"file\").focus();</script>\r\n\r\nOUTPUT;\r\n}\r\n\r\n// *********************************************** Rename a folder ***********************************************\r\nelse if (isset($_GET[\'folder_rename\']))\r\n{\r\n\r\n$folder = $_GET[\'path\'].DIR_SEP.$_GET[\'folder_rename\'];\r\n\r\n$output .= <<< OUTPUT\r\n\r\n<div class=\"fluid-row\">\r\n\r\n<div class=\"span4\">\r\n</div>\r\n\r\n<div class=\"span4\">\r\n<form class=\"well\" action=\"index.php?toolkit_file_manager&folder_rename&path={$path}\" onsubmit=\"return validateForm()\" method=\"post\">\r\n<h3>Rename or Move Folder</h3>\r\n<label>Old folder name:</label><input class=\"span12\" type=\"text\" id=\"folder_old\" name=\"folder_old\" value=\"{$folder}\" />\r\n<label>New folder name:</label><input class=\"span12\" type=\"text\" id=\"folder_new\" name=\"folder_new\" value=\"{$folder}\" />\r\n<div class=\"clear\"></div>\r\n<button type=\"submit\" class=\"btn\" title=\"Click once to rename folder\">Rename</button>\r\n</form>\r\n</div>\r\n\r\n<div class=\"span4\">\r\n</div>\r\n\r\n</div>\r\n<div class=\"clear\"></div>\r\n\r\n<script>document.getElementById(\"folder_new\").focus();</script>\r\n\r\nOUTPUT;\r\n}\r\n\r\n// *********************************************** Rename a file ***********************************************\r\nelse if (isset($_GET[\'file_rename\']))\r\n{\r\n\r\n$file = $_GET[\'path\'].DIR_SEP.$_GET[\'file_rename\'];\r\n\r\n$output .= <<< OUTPUT\r\n\r\n<div class=\"fluid-row\">\r\n\r\n<div class=\"span4\">\r\n</div>\r\n\r\n<div class=\"span4\">\r\n<form class=\"well\" action=\"index.php?toolkit_file_manager&file_rename&path={$path}\" onsubmit=\"return validateForm()\" method=\"post\">\r\n<h3>Rename or Move File</h3>\r\n<label>Old file name:</label><input class=\"span12\" type=\"text\" id=\"file_old\" name=\"file_old\" value=\"{$file}\" />\r\n<label>New file name:</label><input class=\"span12\" type=\"text\" id=\"file_new\" name=\"file_new\" value=\"{$file}\" />\r\n<div class=\"clear\"></div>\r\n<button type=\"submit\" class=\"btn\" title=\"Click once to rename file\">Rename</button>\r\n</form>\r\n</div>\r\n\r\n<div class=\"span4\">\r\n</div>\r\n\r\n</div>\r\n<div class=\"clear\"></div>\r\n\r\n<script>document.getElementById(\"file_new\").focus();</script>\r\n\r\nOUTPUT;\r\n}\r\n\r\n// *********************************************** Delete a folder ***********************************************\r\nelse if (isset($_GET[\'folder_delete\']))\r\n{\r\n$output .= <<< OUTPUT\r\n\r\n<div class=\"fluid-row\">\r\n\r\n<div class=\"span4\">\r\n</div>\r\n\r\n<div class=\"span4\">\r\n<form class=\"well\" action=\"index.php?toolkit_file_manager&folder_delete&path={$path}\" onsubmit=\"return validateForm()\" method=\"post\">\r\n<h3>Delete Folder</h3>\r\n<label>Folder name:</label><input class=\"span12\" type=\"text\" id=\"folder\" name=\"folder\" value=\"{$_GET[\'folder_delete\']}\" readonly=\"readonly\" />\r\n<p class=\"help-block\">Note: All files and folder within this folder will also be deleted.</p>\r\n<div class=\"clear\"></div>\r\n<button type=\"submit\" class=\"btn\" title=\"Click once to delete folder\">Delete</button>\r\n</form>\r\n</div>\r\n\r\n<div class=\"span4\">\r\n</div>\r\n\r\n</div>\r\n<div class=\"clear\"></div>\r\n\r\nOUTPUT;\r\n}\r\n\r\n// *********************************************** Empty a folder ***********************************************\r\nelse if (isset($_GET[\'folder_delete_contents\']))\r\n{\r\n$output .= <<< OUTPUT\r\n\r\n<div class=\"fluid-row\">\r\n\r\n<div class=\"span4\">\r\n</div>\r\n\r\n<div class=\"span4\">\r\n<form class=\"well\" action=\"index.php?toolkit_file_manager&folder_delete_contents&path={$path}\" onsubmit=\"return validateForm()\" method=\"post\">\r\n<h3>Empty Folder</h3>\r\n<label>Folder name:</label><input class=\"span12\" type=\"text\" id=\"folder\" name=\"folder\" value=\"{$_GET[\'path\']}\" readonly=\"readonly\" />\r\n<p class=\"help-block\">Note: All files and folder within this folder will be deleted.</p>\r\n<div class=\"clear\"></div>\r\n<button type=\"submit\" class=\"btn\" title=\"Click once to empty the folder\">Empty</button>\r\n</form>\r\n</div>\r\n\r\n<div class=\"span4\">\r\n</div>\r\n\r\n</div>\r\n<div class=\"clear\"></div>\r\n\r\nOUTPUT;\r\n}\r\n\r\n// *********************************************** Delete a file ***********************************************\r\nelse if (isset($_GET[\'file_delete\']))\r\n{\r\n$output .= <<< OUTPUT\r\n\r\n<div class=\"fluid-row\">\r\n\r\n<div class=\"span4\">\r\n</div>\r\n\r\n<div class=\"span4\">\r\n<form class=\"well\" action=\"index.php?toolkit_file_manager&file_delete&path={$path}\" onsubmit=\"return validateForm()\" method=\"post\">\r\n<h3>Delete File</h3>\r\n<label>File name:</label><input class=\"span12\" type=\"text\" id=\"folder\" name=\"file\" value=\"{$_GET[\'file_delete\']}\" readonly=\"readonly\" />\r\n<div class=\"clear\"></div>\r\n<button type=\"submit\" class=\"btn\" title=\"Click once to delete file\">Delete</button>\r\n</form>\r\n</div>\r\n\r\n<div class=\"span4\">\r\n</div>\r\n\r\n</div>\r\n<div class=\"clear\"></div>\r\n\r\nOUTPUT;\r\n}\r\n\r\n// *********************************************** View a file ***********************************************\r\nelse if (isset($_GET[\'file_view\']) && isset($_GET[\'path\']))\r\n{\r\n\r\n$file = $_GET[\'path\'].DIR_SEP.$_GET[\'file_view\'];\r\n\r\nif (!file_exists($file))\r\n{\r\n	$output .= \"File does not exist.\".br();\r\n	return;\r\n}\r\n\r\n$contents = htmlentities(file_get_contents($file));\r\n$file_size = round(filesize($file)/1024,2);\r\n\r\nif (!$contents && $file_size!=0)\r\n{\r\n$contents = \'<div class=\"well\">File cannot be viewed in a webpage: \'.$_GET[\'file_view\'].\'</div>\';\r\n$output .= $contents;\r\n}\r\nelse\r\n{\r\n$output .= <<< OUTPUT\r\n<a href=\"index.php?toolkit_file_manager&path={$_GET[\'path\']}\">Close</a>\r\n<div style=\"width: auto; text-align:left;\">\r\n<h3>View File: {$_GET[\'file_view\']} ({$file_size} KB)</h3>\r\n\r\n<pre class=\"prettyprint linenums\">\r\n{$contents}\r\n</pre>\r\n\r\n<div class=\"clear\"></div>\r\n</div>\r\n\r\nOUTPUT;\r\n}\r\n}\r\n\r\n// *********************************************** Edit a file ***********************************************\r\nelse if (isset($_GET[\'file_edit\']) && isset($_GET[\'path\']))\r\n{\r\n\r\n$file = $_GET[\'path\'].DIR_SEP.$_GET[\'file_edit\'];\r\n$contents = htmlentities(file_get_contents($file));\r\n$file_size = round(filesize($file)/1024,2);\r\n\r\nif (!$contents && $file_size!=0)\r\n{\r\n//$contents = \"File cannot be edited in a webpage.\";\r\n$contents = \'<div class=\"well\">File cannot be edited in a webpage: \'.$_GET[\'file_edit\'].\'</div>\';\r\n$output .= $contents;\r\n}\r\nelse\r\n{\r\n\r\n\r\n$q = \'index.php?toolkit_file_manager&file_edit\';\r\n\r\n$output .= <<< OUTPUT\r\n<a href=\"index.php?toolkit_file_manager&path={$_GET[\'path\']}\">Close</a>\r\n<div class=\"well\" style=\"text-align:left;\">\r\n\r\n<h3>Edit File: {$_GET[\'file_edit\']} ({$file_size} KB)</h3>\r\n<form id=\"postfrm\" name=\"standardform\" method=\"post\">\r\n<textarea class=\"span12\" name=\"file_contents\" id=\"file_contents\" rows=\"25\" cols=\"65\" wrap=\"off\">{$contents}</textarea>\r\n<input type=\"hidden\" name=\"file\" id=\"file\" value=\"{$_GET[\'file_edit\']}\" />\r\n<input type=\"hidden\" name=\"path\" id=\"path\" value=\"{$_GET[\'path\']}\" />\r\n<p class=\"help-block\">Note: All content in file will be replaced.</p>\r\n\r\n<div class=\"btn-toolbar\">\r\n	<div class=\"btn-group\">\r\n		<button class=\"btn btn-primary\" title=\"Click once to update file\">Update</button>\r\n		<a class=\"btn\" title=\"Click once to save the code without leaving the page\" name=\"quicksave\" onClick=\"quickSave()\">Quick Save</a>\r\n	</div>\r\n	<div class=\"btn-group\">\r\n		<a name=\"insertTabs\" class=\"btn\" onClick=\"indentSelection(document.standardform.file_contents, \'	\')\">Indent Line(s)</a>\r\n		<a name=\"decreaseTabs\" class=\"btn\" onClick=\"outdentSelection(document.standardform.file_contents, \'	\')\">Outdent Line(s)</a>\r\n	</div>\r\n	<div class=\"btn-group\">\r\n		<a name=\"add_num1\" class=\"btn\" id=\"add_num1\" onClick=\"addNumbers1()\">Line Numbers On</a>\r\n		<a name=\"del_num1\" class=\"btn\" id=\"del_num1\" onClick=\"delNumbers1()\">Line Numbers Off</a>\r\n	</div>\r\n</div>\r\n</form>\r\n\r\n</div>\r\n\r\n<script>\r\nfunction quickSave()\r\n{\r\n	$.post(\"{$q}\", $(\"#postfrm\").serialize());\r\n}\r\n\r\n$(\"#del_num1\").hide();\r\nfunction addNumbers1()\r\n{\r\n	$(\"#file_contents\").linedtextarea();\r\n	$(\"#add_num1\").hide();\r\n	$(\"#del_num1\").show();\r\n}\r\nfunction delNumbers1()\r\n{\r\n	$(\"#file_contents\").unwrap();\r\n	$(\"#file_contents\").unwrap();\r\n	$(\"#file_contents\").unbind(\"scroll\");\r\n	$(\"#file_contents\").unbind(\"resize\");\r\n	$(\"#file_contents\").removeAttr(\'style\');\r\n	$(\'.lines\').remove();\r\n	$(\"#del_num1\").hide();\r\n	$(\"#add_num1\").show();\r\n}\r\n\r\n</script>\r\n\r\nOUTPUT;\r\n}\r\n}\r\n\r\n// *********************************************** Download a file ***********************************************\r\nelse if (isset($_GET[\'file_download\']) && isset($_GET[\'path\']))\r\n{\r\n	$file = $_GET[\'path\'].DIR_SEP.$_GET[\'file_download\'];\r\n	\r\n	// This cleans up any buffers and removes the leading line feed\r\n	//while(@ob_end_clean());\r\n\r\n	/*$file_handle = fopen($file, \"r\");\r\n	//$download_rate = 20.5;\r\n	//fseek($file_handle,0);\r\n	while(!feof($file_handle))\r\n	{\r\n		// send the current file part to the browser\r\n		//print fread($file_handle, round($download_rate * 1024));\r\n		print fread($file_handle, 1024);\r\n		flush();\r\n		//sleep(1);\r\n	}\r\n	fclose($file_handle);\r\n	*/\r\n	\r\n	//header(\'Content-Type: image/png\');\r\n	//header(\"Cache-Control: must-revalidate, post-check=0, pre-check=0\");\r\n	//header(\"Pragma: public\");     \r\n	//header(\"Cache-Control: maxage=1\"); \r\n	//header(\"Content-Transfer-Encoding: binary\");\r\n\r\nheader(\'Cache-Control: private\');\r\nheader(\"Content-Type: application/octet-stream\");\r\nheader(\"Content-Length: \" . filesize($file));\r\nheader(\'Content-Disposition: attachment; filename=\'.$_GET[\'file_download\']);\r\nheader(\'Expires: 0\');\r\n\r\nreadfile($file);\r\nexit();\r\n}\r\n\r\n// *********************************************** Compress a folder ***********************************************\r\nelse if (isset($_GET[\'folder_compress\']) && isset($_GET[\'path\']))\r\n{\r\n\r\nfunction create_zip($files = array(),$destination = \'\',$overwrite = false)\r\n{\r\n\r\n	// If the destination already exists, return false\r\n	if(file_exists($destination) && !$overwrite) return false;\r\n	\r\n	// Add each real file to another array\r\n	$valid_files = array();\r\n	if(is_array($files))\r\n	{\r\n		foreach($files as $file) { if(is_file($file)) $valid_files[] = $file; }\r\n	}\r\n	\r\n	if(count($valid_files)>0)\r\n	{\r\n		// Create the zip\r\n		$zip = new ZipArchive();\r\n		\r\n		// Check permissions\r\n		if($zip->open($destination,$overwrite ? ZIPARCHIVE::OVERWRITE : ZIPARCHIVE::CREATE) !== true) return false;\r\n		\r\n		foreach($valid_files as $file) $zip->addFile($file,str_replace($_GET[\'path\'].DIR_SEP,\'\',$file));\r\n		//echo \'The zip archive contains \',$zip->numFiles,\' files with a status of \',$zip->status;\r\n		    \r\n		// Close the zip\r\n		$zip->close();\r\n		    \r\n		// Ensure the file exists\r\n		return file_exists($destination);\r\n	}\r\n	else return false;\r\n}\r\n\r\nfunction glob_recursive($path, $pattern, $flags = 0)\r\n{\r\n	$files = glob($path.DIR_SEP.$pattern, $flags);\r\n	\r\n	foreach (glob($path.DIR_SEP.\"*\", GLOB_ONLYDIR|GLOB_NOSORT) as $dir)\r\n	{\r\n		$files = array_merge($files, glob_recursive($dir,$pattern, $flags));\r\n	}       \r\n	return $files;\r\n}\r\n\r\n$zip_name = $_GET[\'path\'].DIR_SEP.basename($_GET[\'path\']).\'_\'.date(\"Y-m-d_H.i.s\").\'.zip\';\r\n$result = create_zip(glob_recursive($_GET[\'path\'],\"*\"),$zip_name);\r\n\r\nif ($result) msg_user(\"Zip Creation\",\"Zip file was created successfully:<br />\".$zip_name,\'index.php?toolkit_file_manager&path=\'.$_GET[\'path\']);\r\nelse msg_user(\"Zip Creation\",\"Zip file was NOT created successfully. You either don\'t have permission or the file already exists.\",\'index.php?toolkit_file_manager&path=\'.$_GET[\'path\']);\r\n}\r\n\r\n// *********************************************** Upload a file ***********************************************\r\nelse if (isset($_GET[\'file_upload\']))\r\n{\r\n\r\n$max_up = ini_get(\'max_file_uploads\');\r\n$max_size = ini_get(\'upload_max_filesize\');\r\n\r\n$output .= <<< OUTPUT\r\n\r\n<div class=\"fluid-row\">\r\n\r\n<div class=\"span4\">\r\n</div>\r\n\r\n<div class=\"span4\">\r\n<form class=\"well\" action=\"index.php?toolkit_file_manager&file_upload&path={$path}\" onsubmit=\"return validateForm()\" method=\"post\" enctype=\"multipart/form-data\">\r\n<h3>Upload File(s)</h3>\r\n<label>File name:</label><input class=\"span12\" type=\"file\" name=\"file[]\" id=\"file\" multiple=\"multiple\" />\r\n<label>Multi-file drag and drop supported (max uploads: {$max_up}, max filesize: {$max_size})</label>\r\n<div class=\"clear\"></div>\r\n<button type=\"submit\" class=\"btn\" name=\"submit\" title=\"Click once to upload file\">Upload</button>\r\n</form>\r\n</div>\r\n\r\n<div class=\"span4\">\r\n</div>\r\n\r\n</div>\r\n<div class=\"clear\"></div>\r\n\r\nOUTPUT;\r\n}\r\n// *********************************************** Archive Extract ***********************************************\r\nelse if (isset($_GET[\'zip_extract\']))\r\n{\r\n\r\n$zip = $_GET[\'path\'] . DIR_SEP . $_GET[\'zip_extract\'];\r\n\r\n$output .= <<< OUTPUT\r\n\r\n<div class=\"fluid-row\">\r\n\r\n<div class=\"span4\">\r\n</div>\r\n\r\n<div class=\"span4\">\r\n<form class=\"well\" onsubmit=\"return validateForm()\" method=\"post\">\r\n<h3>Extract Archive</h3>\r\n<label>Archive name:</label><input class=\"span12\" type=\"text\" name=\"archive_name\" id=\"archive_name\" value=\"{$zip}\" />\r\n<p class=\"help-block\">The files and folder will be extracted to the current folder.</p>\r\n<div class=\"clear\"></div>\r\n<button type=\"submit\" class=\"btn\" title=\"Click once to extract the archive\">Extract</button>\r\n</form>\r\n</div>\r\n\r\n<div class=\"span4\">\r\n</div>\r\n\r\n</div>\r\n<div class=\"clear\"></div>\r\n\r\nOUTPUT;\r\n}\r\n\r\nfunction getPerms($file)\r\n{\r\nif (!file_exists($file)) return \"-\";\r\n\r\n$perms = fileperms($file);\r\n\r\nif (($perms & 0xC000) == 0xC000) {\r\n    // Socket\r\n    $info = \'s\';\r\n} else if (($perms & 0xA000) == 0xA000) {\r\n    // Symbolic Link\r\n    $info = \'l\';\r\n} else if (($perms & 0x8000) == 0x8000) {\r\n    // Regular\r\n    $info = \'-\';\r\n} else if (($perms & 0x6000) == 0x6000) {\r\n    // Block special\r\n    $info = \'b\';\r\n} else if (($perms & 0x4000) == 0x4000) {\r\n    // Directory\r\n    $info = \'d\';\r\n} else if (($perms & 0x2000) == 0x2000) {\r\n    // Character special\r\n    $info = \'c\';\r\n} else if (($perms & 0x1000) == 0x1000) {\r\n    // FIFO pipe\r\n    $info = \'p\';\r\n} else {\r\n    // Unknown\r\n    $info = \'u\';\r\n}\r\n\r\n// Owner\r\n$info .= (($perms & 0x0100) ? \'r\' : \'-\');\r\n$info .= (($perms & 0x0080) ? \'w\' : \'-\');\r\n$info .= (($perms & 0x0040) ?\r\n            (($perms & 0x0800) ? \'s\' : \'x\' ) :\r\n            (($perms & 0x0800) ? \'S\' : \'-\'));\r\n\r\n// Group\r\n$info .= (($perms & 0x0020) ? \'r\' : \'-\');\r\n$info .= (($perms & 0x0010) ? \'w\' : \'-\');\r\n$info .= (($perms & 0x0008) ?\r\n            (($perms & 0x0400) ? \'s\' : \'x\' ) :\r\n            (($perms & 0x0400) ? \'S\' : \'-\'));\r\n\r\n// World\r\n$info .= (($perms & 0x0004) ? \'r\' : \'-\');\r\n$info .= (($perms & 0x0002) ? \'w\' : \'-\');\r\n$info .= (($perms & 0x0001) ?\r\n            (($perms & 0x0200) ? \'t\' : \'x\' ) :\r\n            (($perms & 0x0200) ? \'T\' : \'-\'));\r\nreturn $info.\' \'.substr(sprintf(\'%o\', fileperms($file)), -4);\r\n}\r\n\r\n// *********************************************** Default List Contents ***********************************************\r\n// Get a list of all the backup files\r\n\r\n$files = glob($path.DIR_SEP.\"*\", GLOB_MARK);\r\n\r\nif (!is_dir($path))\r\n{\r\n$output .= \"Directory does not exist\";\r\nreturn;\r\n}\r\n\r\n// Output the current folder\r\n$output .= \'<b>Current Directory:</b> \';\r\n$growing_path=\'\';\r\nforeach(explode(DIR_SEP,$path) as $folder)\r\n{\r\n	if (!IS_WIN_OS && $growing_path==\'\') $output .= \'<a href=\"index.php?toolkit_file_manager&path=\'.DIR_SEP.\'\">root</a>\'.DIR_SEP;\r\n	else $output .= \'<a href=\"index.php?toolkit_file_manager&path=\'.$growing_path.$folder.\'\">\'.$folder.\'</a>\'.DIR_SEP;\r\n	$growing_path.=$folder.DIR_SEP;\r\n}\r\n\r\n$output .= \'<br /><br />\';\r\n\r\n$icons = a(img(\'Create Folder\',\'/img/silk/folder_add.png\'),\'toolkit_file_manager&folder_create&path=\'.$path).\" \";\r\n$icons .= a(img(\'Create File\',\'/img/silk/page_white_add.png\'),\'toolkit_file_manager&file_create&path=\'.$path).\" \";\r\n$icons .= a(img(\'Upload File\',\'/img/silk/page_white_get.png\'),\'toolkit_file_manager&file_upload&path=\'.$path).\" \";\r\n$icons .= a(img(\'Compress Folder\',\'/img/silk/compress.png\'),\'toolkit_file_manager&folder_compress&path=\'.$path).\" \";\r\n$icons .= a(img(\'Empty Folder\',\'/img/silk/bin_empty.png\'),\'toolkit_file_manager&folder_delete_contents&path=\'.$path).\" \";\r\n//$icons .= a(img(\'Rename Folder\',\'/img/silk/folder_wrench.png\'),\'toolkit_file_manager\').\" \";\r\n$output .= $icons;\r\n\r\n$output .= \'<br /><br />\';\r\n\r\n// Get the name of the parent folder\r\n$parent = substr($path,0,strrpos($path,DIR_SEP));\r\n//$output .= \'<a href=\"index.php?toolkit_file_manager&path=\'.$parent.\'\">Parent Dir</a><br /><br />\';\r\n\r\n// Create the arrays with HTML output\r\n$dir=array();\r\n$file=array();\r\n\r\n// Loop through each of the files and folders in the current path\r\nforeach($files as $f)\r\n{\r\n	// Grab the last character to see if it is a directory\r\n	$last = $f[strlen($f)-1];\r\n\r\n	// If the last character is a \\, then it is a directory\r\n	if ($last==DIR_SEP)\r\n	{\r\n		// Get the dir name without the full path\r\n		if (!IS_WIN_OS && $path==DIR_SEP) $d = substr($f,strlen($path));\r\n		else $d = substr($f,strlen($path)+1);\r\n		$full_name = substr($f,0,strlen($f)-1); // No trailing slash\r\n		$short_name = substr($d,0,strlen($d)-1); // No trailing slash\r\n\r\n		$icons = a(img(\'Delete\',\'/img/silk/folder_delete.png\'),\'toolkit_file_manager&folder_delete=\'.$short_name.\'&path=\'.$path).\" \";\r\n		$icons .= a(img(\'Rename or Move\',\'/img/silk/folder_wrench.png\'),\'toolkit_file_manager&folder_rename=\'.$short_name.\'&path=\'.$path).\" \";\r\n\r\n		//$try = exec(\"du -sh $full_name | cut -f1\");\r\n\r\n		if (!IS_WIN_OS) $folder_info = \" \".getPerms($full_name);\r\n		else $folder_info = \"\";\r\n\r\n		array_push($dir,$icons.\'<a href=\"index.php?toolkit_file_manager&path=\'.$full_name.\'\">\'.$short_name.\'</a> \'.$folder_info.\'<br />\');\r\n	}\r\n	// Else is a file\r\n	else\r\n	{ \r\n		if (!IS_WIN_OS && $path==DIR_SEP) $filename_no_path = substr($f,strlen($path));\r\n		else $filename_no_path = substr($f,strlen($path)+1);\r\n\r\n		$icons = a(img(\'Delete\',\'/img/silk/page_white_delete.png\'),\'toolkit_file_manager&file_delete=\'.$filename_no_path.\'&path=\'.$path).\" \";\r\n		$icons .= a(img(\'Rename\',\'/img/silk/page_white_wrench.png\'),\'toolkit_file_manager&file_rename=\'.$filename_no_path.\'&path=\'.$path).\" \";\r\n		$icons .= a(img(\'View\',\'/img/silk/page_white_magnify.png\'),\'toolkit_file_manager&file_view=\'.$filename_no_path.\'&path=\'.$path).\" \";\r\n		$icons .= a(img(\'Edit\',\'/img/silk/page_white_edit.png\'),\'toolkit_file_manager&file_edit=\'.$filename_no_path.\'&path=\'.$path).\" \";\r\n		$icons .= a(img(\'Download\',\'/img/silk/page_white_put.png\'),\'toolkit_file_manager&file_download=\'.$filename_no_path.\'&path=\'.$path).\" \";\r\n		if (strlen($f)>4 && strtolower(substr($f,strlen($f)-4))==\'.zip\') $icons .= a(img(\'Extract\',\'/img/silk/page_white_compressed.png\'),\'toolkit_file_manager&zip_extract=\'.$filename_no_path.\'&path=\'.$path).\" \";\r\n\r\n		//$icons .= a(img(\'Rename\',\'/img/silk/folder_wrench.png\'),\'toolkit_file_manager&folder_rename&path=\'.$path).\" \";\r\n\r\n		if (!IS_WIN_OS) $file_size = \" (\".round(filesize($f)/1024,2).\" KB) \".getPerms($f);\r\n		else $file_size = \" (\".round(filesize($f)/1024,2).\" KB)\";\r\n\r\n		array_push($file,$icons.$filename_no_path.$file_size.br());\r\n	}\r\n}\r\n\r\n// Sort in natural, non case-sensitive order\r\nnatcasesort($dir);\r\nnatcasesort($file);\r\n\r\n$output .= \'<b>Folders (\'.count($dir).\'):</b>\';\r\nif (count($dir)>10) $output .= \'<div class=\"two_col\">\';\r\nelse $output .= br();\r\nforeach($dir as $d) $output .= $d;\r\nif (empty($dir)) $output .= \"None<br />\";\r\nif (count($dir)>10) $output .= \'</div>\';\r\n\r\n$output .= \'<br /><b>Files (\'.count($file).\'):</b>\';\r\nif (count($file)>10) $output .= \'<div class=\"two_col\">\';\r\nelse $output .= br();\r\nforeach($file as $f) $output .= $f;\r\nif (empty($file)) $output .= \"None<br />\";\r\nif (count($file)>10) $output .= \'</div>\';\r\n\r\n$img_count = 0;\r\n$img_output = \'\';\r\n\r\nforeach($files as $f)\r\n{\r\n	$rel = str_replace(array(CMS_ROOT,\'\\\\\'),array(\'\',\'/\'),$f);\r\n	if (stripos($f,\".png\")!==false ||\r\n	stripos($f,\".gif\")!==false ||\r\n	stripos($f,\".jpg\")!==false ||\r\n	stripos($f,\".jpeg\")!==false ||\r\n	stripos($f,\".ico\")!==false ||\r\n	stripos($f,\".bmp\")!==false)\r\n	{\r\n		$img_output .= img_special($rel,\'index.php?toolkit_file_manager&show_image=\'.$f,true,\'2px 2px 2px 2px\', \"$(\'#img_path\').html(\'img(\\\'Image Text\\\',\\\'$rel\\\')\');\").\' \';\r\n		$img_count+=1;\r\n	}\r\n}\r\nif ($img_count>0)\r\n{\r\n	$output .= \'<br /><b>Preview: </b><div id=\"img_path\" name=\"img_path\" style=\"display: inline;\"></div><br />\';\r\n	$output .= $img_output;\r\n	$output .= br().br().\'<b>Items Total:</b> \'.(count($dir) + count($file));\r\n}\r\n\r\n// Refresh the page every 10 seconds\r\n//echo \'<meta http-equiv=\"Refresh\" content=\"10;url=\'.$_SERVER[\'REQUEST_URI\'].\'\">\';','2012-07-09 21:09:25',2,1,'// Create a folder\r\nif (isset($_GET[\'folder_create\']))\r\n{\r\n	// If all the variables are present\r\n	if (isset($_POST[\'folder\']) && isset($_GET[\'path\']))\r\n	{\r\n		// Form the folder name\r\n		$folder = $_GET[\'path\'].DIR_SEP.$_POST[\'folder\'];\r\n		// Make the folder - suppress errors\r\n		$result = @mkdir($folder);\r\n\r\n		// Inform user of result\r\n		if ($result) msg_user(\"Folder Creation\",$folder.\" was created successfully!\",\'index.php?toolkit_file_manager&path=\'.$_GET[\'path\']);\r\n		else msg_user(\"Folder Creation\",$folder.\" was NOT created successfully. You either don\'t have permission or the folder already exists.\",\'index.php?toolkit_file_manager&path=\'.$_GET[\'path\']);\r\n\r\n	}else msg_user(\"Folder Creation\",\"Something was missing...\",\'index.php?toolkit_file_manager&path=\'.$_GET[\'path\']);\r\n}\r\n\r\n// Create a file\r\nelse if (isset($_GET[\'file_create\']))\r\n{\r\n	// If all the variables are present\r\n	if (isset($_POST[\'file\']) && isset($_GET[\'path\']))\r\n	{\r\n		// Form the file name\r\n		$file = $_GET[\'path\'].DIR_SEP.$_POST[\'file\'];\r\n		// Make the file - suppress errors\r\n		$result = @file_put_contents($file,\'\',FILE_APPEND);\r\n\r\n		// Inform user of result\r\n		if ($result!==false) msg_user(\"File Creation\",$file.\" was created successfully!\",\'index.php?toolkit_file_manager&path=\'.$_GET[\'path\']);\r\n		else msg_user(\"File Creation\",$file.\" was NOT created successfully. You either don\'t have permission.\",\'index.php?toolkit_file_manager&path=\'.$_GET[\'path\']);\r\n\r\n	}else msg_user(\"File Creation\",\"Something was missing...\",\'index.php?toolkit_file_manager&path=\'.$_GET[\'path\']);\r\n}\r\n\r\n// Rename a folder\r\nelse if (isset($_GET[\'folder_rename\']))\r\n{\r\n	// If all the variables are present\r\n	if (isset($_POST[\'folder_old\']) && isset($_POST[\'folder_new\']) && isset($_GET[\'path\']))\r\n	{\r\n		// Form the folder name\r\n		//$folder_old = $_GET[\'path\'].DIR_SEP.$_POST[\'folder_old\'];\r\n		//$folder_new = $_GET[\'path\'].DIR_SEP.$_POST[\'folder_new\'];\r\n		$folder_old = $_POST[\'folder_old\'];\r\n		$folder_new = $_POST[\'folder_new\'];\r\n		// Make the folder - suppress errors\r\n		$result = @rename($folder_old, $folder_new);\r\n\r\n		// Inform user of result\r\n		if ($result) msg_user(\"Folder Rename\",$folder_old.\" was renamed successfully to \".$folder_new.\"!\",\'index.php?toolkit_file_manager&path=\'.$_GET[\'path\']);\r\n		else msg_user(\"Folder Rename\",$folder_old.\" was NOT renamed successfully \".$folder_new.\". You either don\'t have permission or the folder already exists.\",\'index.php?toolkit_file_manager&path=\'.$_GET[\'path\']);\r\n\r\n	}else msg_user(\"Folder Rename\",\"Something was missing...\",\'index.php?toolkit_file_manager&path=\'.$_GET[\'path\']);\r\n}\r\n\r\n// Rename a file\r\nelse if (isset($_GET[\'file_rename\']))\r\n{\r\n	// If all the variables are present\r\n	if (isset($_POST[\'file_old\']) && isset($_POST[\'file_new\']) && isset($_GET[\'path\']))\r\n	{\r\n		// Form the file name\r\n		//$file_old = $_GET[\'path\'].DIR_SEP.$_POST[\'file_old\'];\r\n		//$file_new = $_GET[\'path\'].DIR_SEP.$_POST[\'file_new\'];\r\n		$file_old = $_POST[\'file_old\'];\r\n		$file_new = $_POST[\'file_new\'];\r\n		// Make the file - suppress errors\r\n		$result = @rename($file_old, $file_new);\r\n\r\n		// Inform user of result\r\n		if ($result) msg_user(\"File Rename\",$file_old.\" was renamed successfully to \".$file_new.\"!\",\'index.php?toolkit_file_manager&path=\'.$_GET[\'path\']);\r\n		else msg_user(\"File Rename\",$file_old.\" was NOT renamed successfully \".$file_new.\". You either don\'t have permission or the file already exists.\",\'index.php?toolkit_file_manager&path=\'.$_GET[\'path\']);\r\n\r\n	}else msg_user(\"File Rename\",\"Something was missing...\",\'index.php?toolkit_file_manager&path=\'.$_GET[\'path\']);\r\n}\r\n\r\n// Delete a folder\r\nelse if (isset($_GET[\'folder_delete\']))\r\n{\r\n\r\nfunction rrmdir($dir) {\r\n    foreach(glob($dir . \'/*\') as $file) {\r\n        if(is_dir($file))\r\n            rrmdir($file);\r\n        else\r\n            unlink($file);\r\n    }\r\n    rmdir($dir);\r\n}\r\n\r\n	// If all the variables are present\r\n	if (isset($_POST[\'folder\']) && isset($_GET[\'path\']))\r\n	{\r\n		// Form the folder name\r\n		$folder = $_GET[\'path\'].DIR_SEP.$_POST[\'folder\'];\r\n		// Delete the folder - suppress errors\r\n		//$result = @rmdir($folder);\r\n		rrmdir($folder);\r\n\r\n		// Inform user of result\r\n		if (!is_dir($folder)) msg_user(\"Folder Deletion\",$folder.\" was deleted successfully!\",\'index.php?toolkit_file_manager&path=\'.$_GET[\'path\']);\r\n		else msg_user(\"Folder Deletion\",$folder.\" was NOT deleted successfully. You either don\'t have permission or the folder doesn\'t exist.\",\'index.php?toolkit_file_manager&path=\'.$_GET[\'path\']);\r\n\r\n	}else msg_user(\"Folder Deletion\",\"Something was missing...\",\'index.php?toolkit_file_manager&path=\'.$_GET[\'path\']);\r\n}\r\n\r\n// Delete all files in a folder\r\nelse if (isset($_GET[\'folder_delete_contents\']))\r\n{\r\n\r\nfunction rrmdir($dir, $parent) {\r\n    foreach(glob($dir . \'/*\') as $file) {\r\n        if(is_dir($file))\r\n            rrmdir($file, false);\r\n        else\r\n            unlink($file);\r\n    }\r\nif (!$parent) rmdir($dir);\r\n}\r\n\r\n	// If all the variables are present\r\n	if (isset($_GET[\'path\']))\r\n	{\r\n		// Form the folder name\r\n		$folder = $_GET[\'path\'];\r\n		// Delete the folder contents\r\n		rrmdir($folder, true);\r\n\r\n		$arr = glob($_GET[\'path\'] . \'/*\');\r\n\r\n		// Inform user of result\r\n		if (empty($arr)) msg_user(\"Folder Emptying\",$folder.\" was emptied successfully!\",\'index.php?toolkit_file_manager&path=\'.$_GET[\'path\']);\r\n		else msg_user(\"Folder Emptying\",$folder.\" was NOT emptied successfully. You either don\'t have permission or the folder doesn\'t exist.\",\'index.php?toolkit_file_manager&path=\'.$_GET[\'path\']);\r\n\r\n	}else msg_user(\"Folder Deletion\",\"Something was missing...\",\'index.php?toolkit_file_manager&path=\'.$_GET[\'path\']);\r\n}\r\n\r\nelse if (isset($_GET[\'file_delete\']))\r\n{\r\n	// If all the variables are present\r\n	if (isset($_POST[\'file\']) && isset($_GET[\'path\']))\r\n	{\r\n		// Form the file name\r\n		$file = $_GET[\'path\'].DIR_SEP.$_POST[\'file\'];\r\n		// Delete the file - suppress errors\r\n		$result = @unlink($file);\r\n\r\n		// Inform user of result\r\n		if ($result) msg_user(\"File Deletion\",$file.\" was deleted successfully!\",\'index.php?toolkit_file_manager&path=\'.$_GET[\'path\']);\r\n		else msg_user(\"File Deletion\",$file.\" was NOT deleted successfully. You either don\'t have permission or the file doesn\'t exist.\",\'index.php?toolkit_file_manager&path=\'.$_GET[\'path\']);\r\n\r\n	}else msg_user(\"File Deletion\",\"Something was missing...\",\'index.php?toolkit_file_manager&path=\'.$_GET[\'path\']);\r\n}\r\n\r\nelse if (isset($_GET[\'file_upload\']))\r\n{\r\n\r\n$log = \'\';\r\n	// If all the variables are present\r\n	if (count($_FILES)>0 && isset($_GET[\'path\']))\r\n	{\r\n		for($i=0;$i<count($_FILES[\"file\"][\"name\"]);$i++)\r\n		{\r\n			$file = $_GET[\'path\'].DIR_SEP. $_FILES[\"file\"][\"name\"][$i];\r\n			//if (file_exists($file))\r\n			//{\r\n				//msg_user(\"File Upload\",$_FILES[\"file\"][\"name\"][$i].\" was NOT uploaded successfully. The file already exists.\",\'index.php?toolkit_file_manager&path=\'.$_GET[\'path\']);\r\n				//$log .= $_FILES[\"file\"][\"name\"][$i].\" already exists.<br />\";\r\n			//}\r\n			//else\r\n			//{\r\n				$result = move_uploaded_file($_FILES[\"file\"][\"tmp_name\"][$i],$file);\r\n				if ($result) $log .= $_FILES[\"file\"][\"name\"][$i].\" uploaded successfully!<br />\";\r\n				else $log .= $_FILES[\"file\"][\"name\"][$i].\" upload failed, check permissions.<br />\";\r\n				//msg_user(\"File Upload\",$file.\" was uploaded successfully!\",\'index.php?toolkit_file_manager&path=\'.$_GET[\'path\']);\r\n			//}\r\n		}\r\n\r\nmsg_user(\"File Upload\",$log,\'index.php?toolkit_file_manager&path=\'.$_GET[\'path\']);\r\n\r\n	}else msg_user(\"File Upload\",\"Something was missing...\",\'index.php?toolkit_file_manager&path=\'.$_GET[\'path\']);\r\n}\r\n\r\n// Edit file\r\nelse if (isset($_GET[\'file_edit\']))\r\n{\r\n	// If all the variables are present\r\n	if (isset($_POST[\'file\']) && isset($_POST[\'file_contents\']) && isset($_POST[\'path\']))\r\n	{\r\n		// Form the file name\r\n		$file = $_POST[\'path\'].DIR_SEP.$_POST[\'file\'];\r\n		// Update the file - suppress errors\r\n		$result = @file_put_contents($file,$_POST[\'file_contents\']);\r\n\r\n		// Inform user of result\r\n		if ($result!==false) msg_user(\"File Update\",$file.\" was updated successfully!\",\'index.php?toolkit_file_manager&path=\'.$_POST[\'path\']);\r\n		else msg_user(\"File Update\",$file.\" was NOT updated successfully. You either don\'t have permission.\",\'index.php?toolkit_file_manager&path=\'.$_POST[\'path\']);\r\n\r\n	}else msg_user(\"File Update\",\"Something was missing...\",\'index.php?toolkit_file_manager&path=\'.$_POST[\'path\']);\r\n\r\n}\r\n\r\n// Edit file\r\nelse if (isset($_GET[\'zip_extract\']))\r\n{\r\n	// If all the variables are present\r\n	if (isset($_POST[\'archive_name\']) && isset($_GET[\'path\']))\r\n	{\r\n		// Form the file name\r\n		//$file = $_GET[\'path\'].DIR_SEP.$_POST[\'file\'];\r\n\r\n		$archive_name = $_POST[\'archive_name\'];\r\n		$archive_dir = dirname($archive_name);\r\n		\r\n		$zip = new ZipArchive();\r\n		//$zip->open($archive_name);\r\n		\r\n		if ($zip->open($archive_name) === TRUE)\r\n		{\r\n			// For each file in the array\r\n			for ($i=0; $i < $zip->numFiles; $i++)\r\n			{\r\n				$arr = $zip->statIndex($i);\r\n			\r\n				$last_char = $arr[\'name\'][strlen($arr[\'name\'])-1];\r\n			\r\n				if ($last_char==\'/\' || $last_char==\'\\\\\')\r\n				{\r\n					$dir = $archive_dir.DIR_SEP.$arr[\'name\'];\r\n					if (!is_dir($dir)) mkdir($dir);\r\n				}\r\n				else\r\n				{\r\n					$file = $archive_dir.DIR_SEP;\r\n					$zip->extractTo($file,$arr[\'name\']);\r\n				}\r\n			}\r\n			// Inform user of result\r\n			msg_user(\"Archive Extraction\",\"Extract was successful!\",\'index.php?toolkit_file_manager&path=\'.$_GET[\'path\']);\r\n		} else msg_user(\"Archive Extraction\",\"Extract was NOT successful. You either don\'t have permission.\",\'index.php?toolkit_file_manager&path=\'.$_GET[\'path\']);\r\n\r\n	}else msg_user(\"Archive Extraction\",\"Something was missing...\",\'index.php?toolkit_file_manager&path=\'.$_GET[\'path\']);\r\n}\r\n\r\nelse echo \"Not implemented yet\";','File Organizer','',''),(210,'access_change_email','$output .= <<< OUTPUT\r\n<div class=\"fluid-row\">\r\n\r\n<div class=\"span4\">\r\n</div>\r\n\r\n<div class=\"span4\">\r\n<form class=\"well\" onsubmit=\"return validateForm()\" method=\"post\">\r\n<label>Old Email:</label><input class=\"span12\" type=\"text\" id=\"email_old\" name=\"email_old\" value=\"{$_SESSION[\'email\']}\" />\r\n<label>New Email:</label><input class=\"span12\" type=\"text\" id=\"email_new\" name=\"email_new\" value=\"\" placeholder=\"case insensitive\" />\r\n<div class=\"clear\"></div>\r\n<button type=\"submit\" class=\"btn\" title=\"Click once to change email\">Change</button>\r\n</form>\r\n</div>\r\n\r\n<div class=\"span4\">\r\n</div>\r\n\r\n</div>\r\n\r\n<script>document.getElementById(\"email_new\").focus();</script>\r\nOUTPUT;\r\n// Need this for the heredoc','2012-06-17 05:13:52',2,1,'// Check for required Post values\r\nif (!isset($_POST[\'email_old\']) || !isset($_POST[\'email_new\'])) die(\'Invalid Post!\');\r\n\r\n// Query to update password\r\n$qry = \"UPDATE access_user SET email=? WHERE email=? AND id=? LIMIT 1\";\r\n\r\n// Fill the array with the values\r\n$params = array(trim($_POST[\'email_new\']),trim($_POST[\'email_old\']),$_SESSION[\'id\']);\r\n\r\n// Execute the query and return a success or failure\r\n$success = query_execute($qry, $params);\r\n\r\n// Output to the user\r\nif ($success) msg_user(\'Email Change\',\'Email changed successfully\');\r\nelse msg_user(\'Email Change\',\'Email changed failed - invalid email\');','Change Email','',''),(211,'access_change_username','$output .= <<< OUTPUT\r\n<div class=\"fluid-row\">\r\n\r\n<div class=\"span4\">\r\n</div>\r\n\r\n<div class=\"span4\">\r\n<form class=\"well\" onsubmit=\"return validateForm()\" method=\"post\">\r\n\r\n<label>Old Username:</label><input class=\"span12\" type=\"text\" id=\"username_old\" name=\"username_old\" value=\"{$_SESSION[\'username\']}\" />\r\n<label>New Username:</label><input class=\"span12\" type=\"text\" id=\"username_new\" name=\"username_new\" value=\"\" placeholder=\"case insensitive\" />\r\n<div class=\"clear\"></div>\r\n<button type=\"submit\" class=\"btn\" title=\"Click once to change username\">Change</button>\r\n</form>\r\n</div>\r\n\r\n<div class=\"span4\">\r\n</div>\r\n\r\n</div>\r\n\r\n<script>document.getElementById(\"username_new\").focus();</script>\r\nOUTPUT;\r\n\r\n//Need this for the heredoc','2012-06-17 05:14:59',2,1,'// Check for required Post values\r\nif (!isset($_POST[\'username_old\']) || !isset($_POST[\'username_new\'])) die(\'Invalid Post!\');\r\n\r\n// Query to update password\r\n$qry = \"UPDATE access_user SET username=? WHERE username=? AND id=? LIMIT 1\";\r\n\r\n// Fill the array with the values\r\n$params = array(trim($_POST[\'username_new\']),trim($_POST[\'username_old\']),$_SESSION[\'id\']);\r\n\r\n// Execute the query and return a success or failure\r\n$success = query_execute($qry, $params);\r\n\r\n// Output to the user\r\nif ($success) msg_user(\'Username Change\',\'username changed successfully\');\r\nelse msg_user(\'Username Change\',\'username changed failed - invalid username\');','Change Username','',''),(214,'page_changelog','<h3><u>August 8th, 2012</u></h3>\r\n<ol style=\"padding-left: 20px;\">\r\n<li>Updated page_public with all the utilized technology</li>\r\n<li>Updated the Help section of the toolkit_edit_method to include code highlighting</li>\r\n<li>Updated the cms_admin_sidebar to include CMS and Studio versions</li>\r\n</ol>\r\n<br />\r\n\r\n<h3><u>August 6th, 2012</u></h3>\r\n<ol style=\"padding-left: 20px;\">\r\n<li>Released the FusionLeaf CMS on Google Code</li>\r\n</ol>\r\n<br />\r\n\r\n<h3><u>August 2nd, 2012</u></h3>\r\n<ol style=\"padding-left: 20px;\">\r\n<li>Fixed a bug with the Quick Save feature in the Code Editor that prevented the page name from changing more than once</li>\r\n<li>Updated the security groups so Anonymous and Authenticated provide two completely separate levels of access</li>\r\n<li>Added a Help menu item with a link to the WIP Planter\'s Guide</li>\r\n</ol>\r\n\r\n<br />\r\n\r\n<h3><u>July 20th, 2012</u></h3>\r\n<ol style=\"padding-left: 20px;\">\r\n<li>Formatted the \"Debugging with PHP Variables\" so it displays better</li>\r\n</ol>\r\n\r\n<br />\r\n\r\n<h3><u>July 18th, 2012</u></h3>\r\n<ol style=\"padding-left: 20px;\">\r\n<li>Added an upgrade banner to the HTML head for Internet Explorer 6 and lower</li>\r\n</ol>\r\n\r\n<br />\r\n\r\n<h3><u>July 17th, 2012</u></h3>\r\n<ol style=\"padding-left: 20px;\">\r\n<li>Fixed the Recache icon on the Basic Toolkit page</li>\r\n<li>Updated the HTML head to exclude old items</li>\r\n<li>Updated the code editor to work with the highlighting correctly</li>\r\n<li>Updated the setup.php file</li>\r\n</ol>\r\n\r\n<br />\r\n\r\n<h3><u>July 16th, 2012</u></h3>\r\n<ol style=\"padding-left: 20px;\">\r\n<li>Fixed a logic bug in the Pager with users that were not a member of the Administrators group</li>\r\n<li>Added a quicklink to the File Organizer from the Smart File Editor</li>\r\n<li>Added a few quick commands to the Command Shell</li>\r\n<li>Fixed an issue with GET parameters in the Code Editor</li>\r\n<li>Updated the Code Editor to better support dynamic pages</li>\r\n</ol>\r\n\r\n<br />\r\n\r\n<h3><u>July 9th, 2012</u></h3>\r\n<ol style=\"padding-left: 20px;\">\r\n<li>Updated the screenshots (finally!)</li>\r\n<li>Created a Basic Toolkit page for new Administrators</li>\r\n</ol>\r\n\r\n<br />\r\n\r\n<h3><u>July 8th, 2012</u></h3>\r\n<ol style=\"padding-left: 20px;\">\r\n<li>Removed a bunch of unused settings and made it easier to change the Site and System settings</li>\r\n<li>Added a section to the Planter\'s Guide</li>\r\n</ol>\r\n\r\n<br />\r\n\r\n<h3><u>July 5th, 2012</u></h3>\r\n<ol style=\"padding-left: 20px;\">\r\n<li>Started writing the Planter\'s Guide (a walkthrough of all the features)</li>\r\n</ol>\r\n\r\n<br />\r\n\r\n<h3><u>July 4th, 2012</u></h3>\r\n<ol style=\"padding-left: 20px;\">\r\n<li>Updated Bootswatch Theme manager to support previews</li>\r\n</ol>\r\n\r\n<br />\r\n\r\n<h3><u>July 2nd, 2012</u></h3>\r\n<ol style=\"padding-left: 20px;\">\r\n<li>Updated cache clear settings so AJAX updates properly with Memcached and XCache</li>\r\n</ol>\r\n\r\n<br />\r\n\r\n<h3><u>July 1st, 2012</u></h3>\r\n<ol style=\"padding-left: 20px;\">\r\n<li>Added support for Memcache PHP extension</li>\r\n<li>Added support for <a href=\"http://memcached.org\">Memcached 1.2.6</a> and <a href=\"http://www.couchbase.com\">Memcached Membase_1.7.1.1</a></li>\r\n<li>Added support for APC (Alternative PHP Cache), but disabled but default</li>\r\n<li>Added support for <a href=\"http://xcache.lighttpd.net\">XCache</a></li>\r\n<li>Released FusionLeaf Studio v1.0.3 on <a href=\"http://code.google.com/p/fusionleaf\">Google Code</a> with Memcached</li>\r\n</ol>\r\n\r\n<br />\r\n\r\n<h3><u>June 29th, 2012</u></h3>\r\n<ol style=\"padding-left: 20px;\">\r\n<li>Add support for PDO (requires PHP to use mysqlnd)</li>\r\n</ol>\r\n\r\n<br />\r\n\r\n<h3><u>June 26th, 2012</u></h3>\r\n<ol style=\"padding-left: 20px;\">\r\n<li>Added a right click context menu to the dynamic HTML builder for Add/Edit/Delete</li>\r\n<li>Fixed all anchors so the page does not move or scroll when modifying elements in the dynamic HTML builder</li>\r\n<li>Added background colors to all the elements on hover in the dynamic HTML builder</li>\r\n<li>Updated the error handler to support headers missing HTTP_USER_AGENT</li>\r\n</ol>\r\n\r\n<br />\r\n\r\n<h3><u>June 25th, 2012</u></h3>\r\n<ol style=\"padding-left: 20px;\">\r\n<li>Added a delete button to the dynamic HTML builder</li>\r\n<li>Fixed PRE formatting in the dynamic HTML builder</li>\r\n</ol>\r\n\r\n<br />\r\n\r\n<h3><u>June 24th, 2012</u></h3>\r\n<ol style=\"padding-left: 20px;\">\r\n<li>Added more HTML tags to the dynamic HTML builder</li>\r\n<li>Optimized the way the dynamic HTML builder saves elements in the database</li>\r\n<li>Updated the dynamic HTML builder so those not in the Administrators group can see the page, but don\'t have access to \r\n	any of the advanced features</li>\r\n<li>Added debugging information to the dynamic HTML builder so SQL queries can be counted and optimized</li>\r\n<li>Added 2 callable methods that count the number of queries when using AJAX</li>\r\n</ol>\r\n\r\n<br />\r\n\r\n<h3><u>June 23rd, 2012</u></h3>\r\n<ol style=\"padding-left: 20px;\">\r\n<li>Added the most common HTML tags to the dynamic HTML builder</li>\r\n<li>Enhanced the drag/drop ordering in the dynamic HTML builder</li>\r\n<li>Fixed a bug with File_Array class so it doesn\'t save an unchanged file on each page</li>\r\n<li>Fixed a vulnerability which allows access to the site setup page through a Denial of Service attack</li>\r\n</ol>\r\n\r\n<br />\r\n\r\n<h3><u>June 22nd, 2012</u></h3>\r\n<ol style=\"padding-left: 20px;\">\r\n<li>Created a dynamic and scalable way to build HTML pages with modals and drag/drop</li>\r\n</ol>\r\n\r\n<br />\r\n\r\n<h3><u>June 21st, 2012</u></h3>\r\n<ol style=\"padding-left: 20px;\">\r\n<li>Added enhancements from <a href=\"https://github.com/lecar-red/bootstrapx-clickover\">BootstrapX Clickover</a></li>\r\n<li>Removed all the z-index</li>\r\n<li>Created a new function called smartDB() which allows quick database updating from any page</li>\r\n<li>Updated page titles to use smartDB</li>\r\n</ol>\r\n\r\n<br />\r\n\r\n<h3><u>June 20th, 2012</u></h3>\r\n<ol style=\"padding-left: 20px;\">\r\n<li>FusionLeaf Studio v1.0.2.0 released on <a href=\"http://code.google.com/p/fusionleaf\">Google Code</a>!</li>\r\n</ol>\r\n\r\n<br />\r\n\r\n<h3><u>June 18th, 2012</u></h3>\r\n<ol style=\"padding-left: 20px;\">\r\n<li>Improved error handling to prevent page redirects and show error messages</li>\r\n<li>Fixed a bug with the Search page showing the incorrect number of entries on a page</li>\r\n</ol>\r\n\r\n<br />\r\n\r\n<h3><u>June 17th, 2012</u></h3>\r\n<ol style=\"padding-left: 20px;\">\r\n<li>Removed the antiquated Admin settings menu and added them to the Settings Manager</li>\r\n<li>Reduced the number of queries per page load by 75% from 40 queries to 10 queries</li>\r\n<li>Removed all regex called during page loads</li>\r\n<li>Improved PHP support for inline and shorthand echo PHP code</li>\r\n<li>Improved the Page Not Found handling</li>\r\n</ol>\r\n\r\n<br />\r\n\r\n<h3><u>June 16th, 2012</u></h3>\r\n<ol style=\"padding-left: 20px;\">\r\n<li>Converted entire website to CSS from <a href=\"http://twitter.github.com/bootstrap\">Bootstrap, from Twitter</a> (took a whole week)</li>\r\n<li>Added all the <a href=\"http://bootswatch.com\">Bootswatch themes</a> for Bootstrap</li>\r\n<li>Removed all the old CSS and obsolete Color Manager</li>\r\n<li>Updated the public homepage</li>\r\n<li>Removed the extra space at the beginning of indexinc.php which removed the download requirement for while(@ob_end_clean());</li>\r\n</ol>\r\n\r\n<br />\r\n\r\n<h3><u>June 8th, 2012</u></h3>\r\n<ol style=\"padding-left: 20px;\">\r\n<li>Discovered the wonderful world of <a href=\"http://twitter.github.com/bootstrap\">Bootstrap, from Twitter</a></li>\r\n<li>Learned the ways of <a href=\"http://lesscss.org\">LESS</a></li>\r\n<li>Incorporated the likes of <a href=\"http://leafo.net/lessphp\">LessPHP</a></li>\r\n<li>Enhanced the code with <a href=\"http://code.google.com/p/google-code-prettify\">Google-Code-Prettify</a></li>\r\n<li>...Redesigned the interface</li>\r\n<li>Updated jQuery to used the class name \"active\" instead of \"current\" to work with Bootstrap</li>\r\n</ol>\r\n\r\n<br />\r\n\r\n<h3><u>June 7th, 2012</u></h3>\r\n<ol style=\"padding-left: 20px;\">\r\n<li>Added custom default session.name for Setup file</li>\r\n<li>Updated FLS so it can run on Windows without hosts file modifications</li>\r\n</ol>\r\n\r\n<br />\r\n\r\n<h3><u>June 6th, 2012</u></h3>\r\n<ol style=\"padding-left: 20px;\">\r\n<li>Added support for the <a href=\"http://www.phpcaptcha.org\">Securimage PHP Captcha</a></li>\r\n<li>Reverted to default session.save_path for better compatibility</li>\r\n<li>Reverted to default session.name for better compatibility</li>\r\n</ol>\r\n\r\n<br />\r\n\r\n<h3><u>June 4th, 2012</u></h3>\r\n<ol style=\"padding-left: 20px;\">\r\n<li>Added the ability to save and load themes</li>\r\n<li>Added a Light and Dark theme</li>\r\n<li>Updated the CSS for the Session Manager</li>\r\n</ol>\r\n\r\n<br />\r\n\r\n<h3><u>June 3rd, 2012</u></h3>\r\n<ol style=\"padding-left: 20px;\">\r\n<li>Added a manager for site settings and layout</li>\r\n<li>Added support for sending Tweets via <a href=\"http://code.google.com/p/twitter-php\">Twitter-PHP</a></li>\r\n<li>Added <a href=\"http://www.komodomedia.com/blog/2008/12/social-media-mini-iconpack\">Social Network Icon Pack</a> by <a href=\"http://www.komodomedia.com\">Komodo Media, Rogie King</a></li>\r\n<li>Fixed a bug in the Code Editor so original text can be saved once the Quick Save button is pressed</li>\r\n<li>Fixed a few bugs in the Database Manager so indexes and primary keys can be changed without errors</li>\r\n<li>Fixed a bug in the Database Manager so column deletes in empty tables displays the correct message to the user</li>\r\n<li>Updated the Error Log to wrap text that is outside of the page</li>\r\n<li>Updated the Session Manager to wrap text and display current user session at the top</li>\r\n</ol>\r\n\r\n<br />\r\n\r\n<h3><u>June 1st, 2012</u></h3>\r\n<ol style=\"padding-left: 20px;\">\r\n<li>Updated error catching so fatal errors are handled by shutdown function, all others by the error handler</li>\r\n<li>Fixed the high CPU usage problem with the FusionLeaf Studio application</li>\r\n<li>Added custom port support to the FusionLeaf Studio application</li>\r\n<li>Improved problem detection in the FusionLeaf Studio application</li>\r\n</ol>\r\n\r\n<br />\r\n\r\n<h3><u>May 30th, 2012</u></h3>\r\n<ol style=\"padding-left: 20px;\">\r\n<li>Updated error catching...again</li>\r\n<li>Updated file manager to handle files that do not exist</li>\r\n<li>Added a simple command shell</li>\r\n</ol>\r\n\r\n<br />\r\n\r\n<h3><u>May 29th, 2012</u></h3>\r\n<ol style=\"padding-left: 20px;\">\r\n<li>Fixed the PHP Syntax Check feature which broke with the updated error catching</li>\r\n</ol>\r\n\r\n<br />\r\n\r\n<h3><u>May 28th, 2012</u></h3>\r\n<ol style=\"padding-left: 20px;\">\r\n<li>Updated the file manager to only show the Preview section if images are present</li>\r\n</ol>\r\n\r\n<br />\r\n\r\n<h3><u>May 26th, 2012</u></h3>\r\n<ol style=\"padding-left: 20px;\">\r\n<li>Improved the error catching so it protects against endless redirects</li>\r\n<li>Added all the error codes so each error is described better</li>\r\n</ol>\r\n\r\n<br />\r\n\r\n<h3><u>May 25th, 2012</u></h3>\r\n<ol style=\"padding-left: 20px;\">\r\n<li>Fixed a problem with the search showing mismatched results</li>\r\n<li>Improved the error reporting so it catches more</li>\r\n<li>Made significant changes to the FusionLeaf Studio application like simple load balancing and FastCGI respawn control</li>\r\n<li>Added a screenshots page for FusionLeaf Studio</li>\r\n</ol>\r\n\r\n<br />\r\n\r\n<h3><u>May 24th, 2012</u></h3>\r\n<ol style=\"padding-left: 20px;\">\r\n<li>Improved the password encryption algorithm</li>\r\n<li>Improved the error reporting</li>\r\n<li>Updated the public home page as well as the dropdown menu</li>\r\n<li>Improved cleanup for deleted groups</li>\r\n<li>Updated the output for \"Page Not Found\" and \"Access Denied to Page\"</li>\r\n</ol>\r\n\r\n<br />\r\n\r\n<h3><u>May 23rd, 2012</u></h3>\r\n<ol style=\"padding-left: 20px;\">\r\n<li>Updated the menu system to use the new Menu Builder</li>\r\n<li>Improved the backups to include a zip with user specific files</li>\r\n</ol>\r\n\r\n<br />\r\n\r\n<h3><u>May 22nd, 2012</u></h3>\r\n<ol style=\"padding-left: 20px;\">\r\n<li>Restructured FusionLeaf Studio to support file paths with spaces</li>\r\n<li>Updated the backups to include a zip with user specific files along with a database backup</li>\r\n</ol>\r\n\r\n<br />\r\n\r\n<h3><u>May 21st, 2012</u></h3>\r\n<ol style=\"padding-left: 20px;\">\r\n<li>Updated the session manager to use relative offsets for the dropdown menus</li>\r\n<li>Updated the session manager to better support Windows PHP sessions</li>\r\n<li>Added an Nginx version check to the Toolkit</li>\r\n<li>Upgraded the FLS database to MySQL v5.5.24 from v5.5.22</li>\r\n<li>Upgraded the FLS framework to PHP v5.4.3 from v5.4.0</li>\r\n<li>Upgraded the FLS webserver to Nginx v1.2.0 from v1.0.14</li>\r\n<li>Updated the file manager to show two columns in supported browsers when displaying over 10 folders or files</li>\r\n<li>Updated the security manager so it is more intuitive</li>\r\n<li>Updated the Admin menu to support friendly labels</li>\r\n<li>Added Change Control (logging) for over 130 events</li>\r\n<li>Added Change Control (logging) for over 70 SQL write queries</li>\r\n</ol>\r\n\r\n<br />\r\n\r\n<h3><u>May 18th, 2012</u></h3>\r\n<ol style=\"padding-left: 20px;\">\r\n<li>Reorganized the Toolkit</li>\r\n<li>Added version checks for PHP and MySQL to the Toolkit</li>\r\n<li>Changed the page of screenshots to a dynamic gallery</li>\r\n<li>Added a session manager</li>\r\n<li>Added the ability to add/remove line numbers</li>\r\n<li>Fixed the Quick Save behavior on a new page</li>\r\n<li>Changed a few of the main page icons</li>\r\n<li>Added code to protect against PHP session hijacking</li>\r\n<li>Add an image preview feature to the file manager</li>\r\n</ol>\r\n\r\n<br />\r\n\r\n<h3><u>May 17th, 2012</u></h3>\r\n<ol style=\"padding-left: 20px;\">\r\n<li>Consolidated the access pages into a single security manager page</li>\r\n</ol>\r\n\r\n<br />\r\n\r\n<h3><u>May 16th, 2012</u></h3>\r\n<ol style=\"padding-left: 20px;\">\r\n<li>Condensed the error information for simpler error catching</li>\r\n<li>Added support for catching syntax errors and display instructions to end users</li>\r\n</ol>\r\n\r\n<br />\r\n\r\n<h3><u>May 15th, 2012</u></h3>\r\n<ol style=\"padding-left: 20px;\">\r\n<li>Completely redesigned the file structure to support unlimited websites</li>\r\n<li>Updated the Nginx configuration to allow for website creation on the fly</li>\r\n</ol>\r\n\r\n<br />\r\n\r\n<h3><u>May 14th, 2012</u></h3>\r\n<ol style=\"padding-left: 20px;\">\r\n<li>Added a Quick Save option to the edit feature of the file manager</li>\r\n</ol>\r\n\r\n<br />\r\n\r\n<h3><u>May 13th, 2012</u></h3>\r\n<ol style=\"padding-left: 20px;\">\r\n<li>Improved support for <a href=\"http://alan.blog-city.com/jquerylinedtextarea.htm\">JQuery Lined TextArea</a></li>\r\n<li>Updated the index pages title, keywords, and descriptions</li>\r\n</ol>\r\n\r\n<br />\r\n\r\n<h3><u>May 11th, 2012</u></h3>\r\n<ol style=\"padding-left: 20px;\">\r\n<li>Added support for <a href=\"http://alan.blog-city.com/jquerylinedtextarea.htm\">JQuery Lined TextArea</a></li>\r\n</ol>\r\n\r\n<br />\r\n\r\n<h3><u>May 10th, 2012</u></h3>\r\n<ol style=\"padding-left: 20px;\">\r\n<li>Updated the SCRIPT_FILENAME, SCRIPT_NAME and PHP_SELF variables to reflect the correct path in FusionLeaf Studio for Windows</li>\r\n<li>Added support for <a href=\"http://phphtmledit.com\">Cute Editor for PHP</a></li>\r\n</ol>\r\n\r\n<br />\r\n\r\n<h3><u>May 9th, 2012</u></h3>\r\n<ol style=\"padding-left: 20px;\">\r\n<li>Added a Quick Save feature for the Code Editor using AJAX</li>\r\n<li>Updated the style sheet to better support background colors</li>\r\n<li>Updated the style sheet to supports themes</li>\r\n<li>Added 3 themes</li>\r\n<li>Isolated border radius, shadows, and opacity to variables to they can be easily turned on or off</li>\r\n<li>Stylesheet and javascript changes now apply instantly</li>\r\n<li>Updated the database manager to better support the timestamp data type</li>\r\n</ol>\r\n\r\n<br />\r\n\r\n<h3><u>May 8th, 2012</u></h3>\r\n<ol style=\"padding-left: 20px;\">\r\n<li>Updated the error logging to support non-administrative users</li>\r\n<li>Updated the login page to track invalid attempts and block remote IP after 5 incorrect passwords</li>\r\n</ol>\r\n\r\n<br />\r\n\r\n<h3><u>May 7th, 2012</u></h3>\r\n<ol style=\"padding-left: 20px;\">\r\n<li>Updated the Nginx configuration and CMS to support short URLs</li>\r\n<li>Updated page not found redirectors</li>\r\n</ol>\r\n\r\n<br />\r\n\r\n<h3><u>May 6th, 2012</u></h3>\r\n<ol style=\"padding-left: 20px;\">\r\n<li>Updated the user, group, and access lists to support deletions of rogue database entries</li>\r\n</ol>\r\n\r\n<br />\r\n\r\n<h3><u>May 5th, 2012</u></h3>\r\n<ol style=\"padding-left: 20px;\">\r\n<li>Updated Query Tester to accept \"data manipulation\" queries and be more flexible</li>\r\n<li>Added page titles, keywords, and descriptions to the code editor</li>\r\n<li>Reorganized the backbone of the CMS to support more dynamic content</li>\r\n<li>Worked with a SEO specialist to optimize for search engines</li>\r\n<li>Fixed Linux compatibility issue with the error log file</li>\r\n<li>Added a folder \"empty\" feature to the file manager</li>\r\n</ol>\r\n\r\n<br />\r\n\r\n<h3><u>May 4th, 2012</u></h3>\r\n<ol style=\"padding-left: 20px;\">\r\n<li>Updated the order of evaluating the helper methods</li>\r\n<li>Updated all menu items to use the new menu function</li>\r\n<li>Added dropdown menus to the MySQL tables</li>\r\n</ol>\r\n\r\n<br />\r\n\r\n<h3><u>May 3rd, 2012</u></h3>\r\n<ol style=\"padding-left: 20px;\">\r\n<li>Added ability to view/add/delete/rename MySQL tables</li>\r\n<li>Added ability to add MySQL columns</li>\r\n<li>Added ability to add/edit/delete MySQL rows</li>\r\n<li>Updated backup algorithm to email both MySQL database restore information and index.php</li>\r\n</ol>\r\n\r\n<br />\r\n\r\n<h3><u>April 29th, 2012</u></h3>\r\n<ol style=\"padding-left: 20px;\">\r\n<li>Updated the Toolkit images from backslashes to slashes</li>\r\n<li>Added rounded support for older versions of Firefox</li>\r\n</ol>\r\n\r\n<br />\r\n\r\n<h3><u>April 28th, 2012</u></h3>\r\n<ol style=\"padding-left: 20px;\">\r\n<li>Updated dropdown menu to a clean theme</li>\r\n<li>Started building a MySQL front end</li>\r\n</ol>\r\n\r\n<br />\r\n\r\n<h3><u>April 27th, 2012</u></h3>\r\n<ol style=\"padding-left: 20px;\">\r\n<li>Updated website to a clean theme</li>\r\n</ol>\r\n\r\n<br />\r\n\r\n<h3><u>April 26th, 2012</u></h3>\r\n<ol style=\"padding-left: 20px;\">\r\n<li>Update site timezone to UTC instead of GMT</li>\r\n<li>Updated Toolkit main page to include direct links to Sitemap.xml and Robots.txt/li>\r\n<li>Added page which automatically updates sitemap.xml with URL, frequency, and last modified stamp</li>\r\n</ol>\r\n\r\n<br />\r\n\r\n<h3><u>April 25th, 2012</u></h3>\r\n<ol style=\"padding-left: 20px;\">\r\n<li>Updated all the screenshots</li>\r\n<li>Updated opacity CSS to a PHP variable for easy enabling/disabling</li>\r\n</ol>\r\n\r\n<br />\r\n\r\n<h3><u>April 23rd, 2012</u></h3>\r\n<ol style=\"padding-left: 20px;\">\r\n<li>Updated HTML head information</li>\r\n<li>Added robots.txt and a sitemap.xml</li>\r\n<li>Rearranged the cached file structure for better organization</li>\r\n<li>Updated error pages</li>\r\n<li>Set all images relative to the root</li>\r\n<li>Updated all code to use $output instead of \"echo\" to ensure pages output only once</li>\r\n<li>Updated File Manager to focus the first textbox with javascript</li>\r\n</ol>\r\n\r\n<br />\r\n\r\n<h3><u>April 22nd, 2012</u></h3>\r\n<ol style=\"padding-left: 20px;\">\r\n<li>Added shadows for supported browsers</li>\r\n<li>Added a Search and Replace Page</li>\r\n<li>Updated forms to focus the first textbox with javascript</li>\r\n<li>FusionLeaf Studio (FLS) can now be built with a single click (includes everything from the live website)</li>\r\n</ol>\r\n\r\n<br />\r\n\r\n<h3><u>April 19th, 2012</u></h3>\r\n<ol style=\"padding-left: 20px;\">\r\n<li>Updated the home page</li>\r\n<li>Updated the screenshots page</li>\r\n<li>Fixed IE 8/9 issue with dropdown menus and z-index</li>\r\n<li>Added a Twitter and Facebook button to the home page</li>\r\n<li>Updated Google Analytics so it is turned off in Dev or Localhost environment</li>\r\n<li>Updated File Manager for better Windows compatibility</li>\r\n<li>Increased recaching security</li>\r\n</ol>\r\n\r\n<br />\r\n\r\n<h3><u>April 18th, 2012</u></h3>\r\n<ol style=\"padding-left: 20px;\">\r\n<li>Added the new logo</li>\r\n<li>Changed all the corners to rounded</li>\r\n<li>Added Google Analytics</li>\r\n<li>Consolidated a few pages</li>\r\n<li>Added 7 new videos</li>\r\n</ol>','2012-08-08 19:02:03',2,0,'','Changelog','changelog','FusionLeaf is under constant improvement. Check out the changelog for updates!'),(225,'toolkit_replace','if (isset($_GET[\'search\'])) $search = urldecode($_GET[\'search\']);\r\nelse $search = \'\';\r\n\r\n$output .= <<< OUTPUT\r\n\r\n<div class=\"row-fluid\">\r\n<form class=\"well\" onsubmit=\"return validateForm()\" method=\"post\">\r\n<label>Search:</label><input class=\"span4\" type=\"text\" id=\"search\" name=\"search\" value=\"{$search}\" placeholder=\"Search for...\" />\r\n<p>Replace:</p><input class=\"span4\" type=\"text\" id=\"replace\" name=\"replace\" value=\"\" placeholder=\"Replace with...\" />\r\n<p class=\"help-block\">Find every instance of the search string and replace it with another. Applies to Page names, text in Page/Post tabs, \r\nand text in index.php. Case-sensitive.</p>\r\n<button type=\"submit\" class=\"btn\" title=\"Click once to replace all instances\">Replace</button>\r\n</form>\r\n</div>\r\n\r\n<script>document.getElementById(\"replace\").focus();</script>\r\n\r\nOUTPUT;\r\n\r\n// Need this for the heredoc','2012-06-12 04:04:48',2,1,'$FIND = $_POST[\'search\'];\r\n$REPLACE = $_POST[\'replace\'];\r\n\r\n$file_org = file_get_contents(\"index.php\");\r\n$file_new = str_replace($FIND,$REPLACE,$file_org);\r\nfile_put_contents(\"index.php\",$file_new);\r\n\r\n$qry = \"UPDATE php_methods SET name = REPLACE(name,\'$FIND\',\'$REPLACE\')\"; simpleDB($qry,true,true);\r\n$qry = \"UPDATE php_methods SET method = REPLACE(method,\'$FIND\',\'$REPLACE\')\"; simpleDB($qry,true,true);\r\n$qry = \"UPDATE php_methods SET post = REPLACE(post,\'$FIND\',\'$REPLACE\')\"; simpleDB($qry,true,true);\r\n$qry = \"RENAME TABLE $FIND TO $REPLACE\"; simpleDB($qry,false,true);\r\n\r\nmsg_user(\"Search and Replace\",\"Done!\",\'index.php?recache\');','Replace','',''),(218,'div_footer','//$copy_name=smartDB(array(\'id\'=>18,\'current_value\'=>$constant[\'COPYRIGHT_NAME\']),\'current_value\',\'site_setting\');\r\n//$copy_date=smartDB(array(\'id\'=>19,\'current_value\'=>$constant[\'COPYRIGHT_DATE\']),\'current_value\',\'site_setting\');\r\n\r\n$copy_link = quickSiteSetting();\r\n\r\n$output .= <<< OUTPUT\r\n<div class=\"container-fluid\">\r\n	<hr>\r\n	<footer>\r\n		<p class=\"pull-left\">{$constant[\'BRANDING_NAME\']} {$copy_link}</p>\r\n		<div class=\"pull-right\">{$constant[\'COPYRIGHT_NAME\']} &#169; {$constant[\'COPYRIGHT_DATE\']} {$copy_link}</div>\r\n	</footer>\r\n</div>\r\n\r\n<script src=\"/inc/bootstrap/js/bootstrap.js\"></script>\r\n<script src=\"/inc/bootstrapx/bootstrapx-clickover.js\"></script>\r\n<script type=\"text/javascript\">\r\n	$(function() { \r\n		$(\'[rel=\"clickover\"]\').clickover(); \r\n		$(\'[rel=\"popover\"]\').popover(); \r\n	})\r\n</script>\r\nOUTPUT;\r\n\r\n// Need this for the heredoc','2012-07-27 03:26:27',2,1,'','','',''),(329,'toolkit_dynamic','//dynamicPage(\'dyn_\'.\'test\',\'/page_dynamic_demo\');\r\n//function dynamicPage($table_dynamic,$page_name)\r\n{\r\n//global $output;\r\n// Build a table if it does not exist\r\n//$table_dynamic = \'dyn_\'.\'test\';\r\n//$page_name = \'/toolkit_dynamic\';\r\n\r\nif (!isset($page_name)) $page_name = \'/toolkit_dynamic\';\r\nif (!isset($table_dynamic)) $table_dynamic = \'dyn_\'.\'test\';\r\n\r\n// Check to see if the table exists\r\n$qry = \'SELECT table_name FROM information_schema.tables WHERE table_schema=? AND table_name=?\';\r\n$result = query($qry, array(DB_DATABASE,$table_dynamic));\r\n\r\n// Build a table if a table does not exist\r\nif (!$result) $result = query_execute(\"CREATE TABLE $table_dynamic LIKE dyn_template\", array());\r\n// Exit if there is a table problem\r\n//if (!$result) { $output .= \'Table problem!\'; return; }\r\n\r\nif (isGroupMember(2) && count($_GET)==1)\r\n{ \r\n$output .= <<< OUTPUT\r\n\r\n<style>\r\n\r\ncomment { display: none; }\r\n\r\n#page_body h1:hover,\r\n#page_body h2:hover,\r\n#page_body h3:hover,\r\n#page_body h4:hover,\r\n#page_body h5:hover,\r\n#page_body h6:hover,\r\n#page_body p:hover,\r\n#page_body span:hover,\r\n#page_body li:hover,\r\n#page_body dd:hover,\r\n#page_body dt:hover,\r\n#page_body blockquote:hover,\r\n#page_body pre:hover { background-color: #F2DEDE; cursor:move; }\r\n\r\n#page_body small:hover,\r\n#page_body img:hover { background-color: #DFF0D8; cursor:move; }\r\n\r\n//.btn:hover, a:hover { background-color: inherit !important; cursor:move; }\r\n\r\n#page_body { padding-top: 15px; padding-bottom: 15px; border-top: 1px dashed black; border-bottom: 1px dashed black; }\r\n\r\n#page_body div:hover { background-color: #FCF8E3; cursor:move; }\r\n\r\n#page_body ul:hover,\r\n#page_body ol:hover,\r\n#page_body dl:hover { background-color: #D9EDF7; cursor:move; }\r\n\r\n#page_body div,\r\n#page_body ul,\r\n#page_body ol,\r\n#page_body dl { padding-top:15px; padding-bottom:15px; }\r\n\r\n#page_body div div:hover { background-color: #D9EDF7; cursor:move; }\r\n\r\n</style>\r\n\r\n<div class=\"btn-toolbar\">\r\n<!--<div class=\"btn-group\">\r\n<a class=\"btn\" href=\"javascript:void(0)\" onclick=\"$(\'#page_body div\').css(\'border\',\'1px dashed silver\');\">Border Add</a>\r\n<a class=\"btn\" href=\"javascript:void(0)\" onclick=\"$(\'#page_body div\').css(\'border\',\'0px solid silver\');\">Remove</a>\r\n</div>\r\n\r\n<div class=\"btn-group\">\r\n<a class=\"btn\" href=\"javascript:void(0)\" onclick=\"$(\'.dynamic-markup\').remove();$().removeAttr(\'data-parentid\');\">Remove all Markup</a>\r\n</div>-->\r\n\r\n<div class=\"btn-group\" data-toggle=\"buttons-radio\">\r\n<button name=\"btnShow\" id=\"btnShow\" class=\"btn active\" \r\n	onclick=\"$(\'.dynamic-markup\').css(\'display\',\'inline-block\');\">Show Markup</button>\r\n<button name=\"btnHide\" id=\"btnHide\" class=\"btn\" \r\n	onclick=\"$(\'.dynamic-markup\').css(\'display\',\'none\');\">Hide Markup</button>\r\n</div>\r\n\r\n<div class=\"btn-group\">\r\n<a class=\"btn\" href=\"{$page_name}?plain\">Preview as User</a>\r\n<a class=\"btn\" href=\"{$page_name}?code\">View HTML Markup</a>\r\n<a class=\"btn\" href=\"{$page_name}?modify\">Modify</a>\r\n</div>\r\n\r\n</div>\r\n\r\n<div class=\"alert alert-success\" style=\"position:fixed;right:20px;bottom:0px;z-index:1031;\">\r\n  <button class=\"close\" data-dismiss=\"alert\">Ã—</button>\r\n  <span>Currently over: </span><span name=\"overitem\" id=\"overitem\"></span>\r\n</div>\r\n\r\n<ul class=\"nav\" id=\"testdrop2\" style=\"display:inline-block; position: absolute;\">\r\n<li class=\"dropdown\" id=\"testdrop\">\r\n<a href=\"javascript:void(0)\" class=\"dropdown-toggle\" data-toggle=\"dropdown\" style=\"display: inline-block;\"></a>\r\n<ul class=\"dropdown-menu\" id=\"right_click_menu\" name=\"right_click_menu\">\r\n<li style=\"text-align: center;\"><a href=\"javascript:void(0)\"><b name=\"bold_name\" id=\"bold_name\">div</b></a></li>\r\n<li class=\"divider-vertical\" style=\"border-bottom:1px solid silver;\"></li>\r\n<li name=\"onlyfortext\" id=\"onlyfortext\"><a href=\"javascript:void(0)\" onclick=\"show_edit_text()\"><i class=\"icon-text-width\"></i> Edit text</a></li>\r\n<li><a href=\"javascript:void(0)\" onclick=\"show_add()\"><i class=\"icon-plus-sign\"></i> Add a child element</a></li>\r\n<li name=\"notforroot\" id=\"notforroot\"><a href=\"javascript:void(0)\" onclick=\"show_edit()\"><i class=\"icon-pencil\"></i> Edit current element</a></li>\r\n<li><a href=\"javascript:void(0)\" onclick=\"reducePosition()\"><i class=\"icon-arrow-left\"></i> Reorder Left/Up</a></li>\r\n<li><a href=\"javascript:void(0)\" onclick=\"advancePosition()\"><i class=\"icon-arrow-right\"></i> Reorder Right/Down</a></li>\r\n<li><a href=\"javascript:void(0)\" onclick=\"show_delete()\"><i class=\"icon-trash\"></i> Delete current element</a></li>\r\n\r\n<!-- The extra menu items will go here -->\r\n\r\n</ul></li></ul>\r\n\r\n<input type=\"hidden\" name=\"click_myid\" id=\"click_myid\" value=\"\" />\r\n<input type=\"hidden\" name=\"click_textid\" id=\"click_textid\" value=\"\" />\r\n<input type=\"hidden\" name=\"click_parentid\" id=\"click_parentid\" value=\"\" />\r\n<input type=\"hidden\" name=\"click_tagtype\" id=\"click_tagtype\" value=\"\" />\r\n\r\n<script>\r\nfunction reducePosition()\r\n{\r\n	var myid = $(\"#click_myid\").val();\r\n	$.post(\'{$page_name}\',{ operation:\"reduce_position\", myid:myid }, function(data) {\r\n		loadPage();\r\n	});\r\n}\r\nfunction advancePosition()\r\n{\r\n	var myid = $(\"#click_myid\").val();\r\n	$.post(\'{$page_name}\',{ operation:\"advance_position\", myid:myid }, function(data) {\r\n		loadPage();\r\n	});\r\n}\r\n\r\nfunction convertCode()\r\n{\r\n\r\n	var newcode = $(\"#new_code\").val();\r\n	$.post(\'{$page_name}\',{ operation:\"convert_code\", newcode:newcode }, function(data) {\r\n		alert(data);\r\n	});\r\n}\r\n\r\nfunction show_modal(par,elem)\r\n{\r\n	$.post(\'{$page_name}\',{ operation:\"build\", parent_id:par, parent_element:elem }, function(data) {\r\n		$(\"#dispose_mod\").empty();\r\n		$(\"#dispose_mod\").append(data);\r\n		$(\'#myModal\').modal(\'show\');\r\n	});\r\n}\r\nfunction show_add()\r\n{\r\n	var par = $(\"#click_myid\").val();\r\n	//var elem = $(\"#click_parentid\").val();\r\n	var elem = $(\"#click_tagtype\").val();\r\n	$.post(\'{$page_name}\',{ operation:\"build\", parent_id:par, parent_element:elem }, function(data) {\r\n		$(\"#dispose_mod\").empty();\r\n		$(\"#dispose_mod\").append(data);\r\n		$(\'#myModal\').modal(\'show\');\r\n	});\r\n}\r\nfunction show_edit()\r\n{\r\n	var scrollTop = $(document).scrollTop();\r\n	var scrollLeft = $(document).scrollLeft();\r\n	var par = $(\"#click_myid\").val();\r\n	var parid = $(\"#click_parentid\").val();\r\n	var elem = $(\"#click_tagtype\").val();\r\n\r\n	$.post(\'{$page_name}\',{ operation:\"build_edit\", parent_id:par, realparentid:parid, parent_element:elem }, function(data) {\r\n		$(\"#dispose_mod\").empty();\r\n		$(\"#dispose_mod\").append(data);\r\n		$(\'#myModal\').modal(\'show\');\r\n		$(document).scrollTop(scrollTop);\r\n		$(document).scrollLeft(scrollLeft);\r\n	});\r\n}\r\nfunction show_edit_text()\r\n{\r\n	var scrollTop = $(document).scrollTop();\r\n	var scrollLeft = $(document).scrollLeft();\r\n	var par = $(\"#click_myid\").val();\r\n	var parid = $(\"#click_parentid\").val();\r\n	var elem = $(\"#click_tagtype\").val();\r\n	var textid = $(\"#click_textid\").val();\r\n\r\n	$.post(\'{$page_name}\',{ operation:\"build_edit\", parent_id:par, realparentid:parid, parent_element:elem,textid:textid }, function(data) {\r\n		$(\"#dispose_mod\").empty();\r\n		$(\"#dispose_mod\").append(data);\r\n		$(\'#myModal\').modal(\'show\');\r\n		$(document).scrollTop(scrollTop);\r\n		$(document).scrollLeft(scrollLeft);\r\n	});\r\n}\r\nfunction show_delete()\r\n{\r\n	var myid = $(\"#click_myid\").val();\r\n	//var elem = $(\"#click_parentid\").val();\r\n	//var elem = $(\"#click_tagtype\").val();\r\n\r\n	$.post(\'{$page_name}\',{ operation:\"delete\", myid:myid\r\n		}, function(data) {\r\n		if (data==\'true\')\r\n		{\r\n			$(\'#delete_myid\').val(myid);\r\n			$(\'#myModalDelete\').modal(\'show\');\r\n		}\r\n		else\r\n		{\r\n			$.post(\'{$page_name}\',{ operation:\"delete_parent\", myid:myid}, function(data) {\r\n				loadPage();\r\n			});\r\n		}\r\n	});\r\n}\r\nfunction show_modal_edit(par,elem)\r\n{\r\n	$.post(\'{$page_name}\',{ operation:\"build_edit\", parent_id:par, parent_element:elem }, function(data) {\r\n		$(\"#dispose_mod\").empty();\r\n		$(\"#dispose_mod\").append(data);\r\n		$(\'#myModal\').modal(\'show\');\r\n	});\r\n}\r\nfunction show_modal_delete(par,elem)\r\n{\r\n	$.post(\'{$page_name}\',{ operation:\"delete\", parent_id:par, parent_element:elem }, function(data) {\r\n		$(\"#dispose_mod\").empty();\r\n		$(\"#dispose_mod\").append(data);\r\n		$(\'#myModal\').modal(\'show\');\r\n	});\r\n}\r\nfunction loadPage()\r\n{\r\n	var scrollTop = $(window).scrollTop();\r\n	var scrollLeft = $(window).scrollLeft();\r\n\r\n	$.post(\'{$page_name}\',{ operation:\"load\" }, function(data) {\r\n		$(\"#page_body\").empty();\r\n		$(\"#page_body\").append(data);\r\n	\r\n		// Keep track of the markup setting\r\n		if ($(\'#btnShow\').attr(\'class\')==\'btn active\') $(\'#btnShow\').click();\r\n		if ($(\'#btnHide\').attr(\'class\')==\'btn active\') $(\'#btnHide\').click();\r\n	\r\n		$( \".draggable\" ).draggable();\r\n		\r\n\r\n$(\"#page_body\").mousemove(function(e) {\r\nvar elem = document.elementFromPoint((e.pageX-window.pageXOffset), (e.pageY-window.pageYOffset));\r\n    $(\'#overitem\').html(elem.tagName + \' - \' + elem.getAttribute(\'class\'));\r\n});\r\n\r\n		$( \".draggable\" ).droppable({\r\n			greedy: true\r\n			,tolerance: \"pointer\"\r\n			,hoverClass: \"drophover\"\r\n			,over: function( event, ui ) {\r\n				$(\'.icon-arrow-left\').remove();\r\n				//$(this).prepend(\'<i class=\"icon-arrow-right\"></i>\');\r\n				$(this).prepend(\'<i class=\"icon-arrow-left\"></i>\');\r\n				$(this).css(\'cursor\',\'move\');\r\n				$(\'#overitem\').html(ui.draggable.attr(\"data-myid\"));\r\n			}\r\n			,out: function( event, ui ) {\r\n				$(\'.icon-arrow-left\').remove();\r\n				//$(\'.icon-arrow-right\').remove();\r\n			}\r\n			,activate: function( event, ui ) {\r\n				$(ui.draggable).css(\"z-index\",\"3000\");\r\n				$(ui.draggable).css(\"visibility\",\"hidden\");\r\n				//$(\'.icon-plus-sign\').css(\'display\',\'none\');\r\n			}\r\n			,deactivate: function( event, ui ) {\r\n				$(ui.draggable).css(\"z-index\",\"inherit\");\r\n				$(this).css(\'cursor\',\'auto\');\r\n				$(\'.icon-arrow-left\').remove();\r\n				//$(\'.icon-arrow-right\').remove();\r\n			}\r\n			,drop: function( event, ui ) {\r\n		\r\n			// Dragged object id\r\n			var myid = $(ui.draggable).attr(\"data-myid\");\r\n			// Dragged object parent id\r\n			var myparentid = $(ui.draggable).attr(\"data-parentid\");\r\n			// Dragged object position\r\n			var myposition = $(ui.draggable).attr(\"data-position\");\r\n		\r\n			// Dropped on object id\r\n			var droppedOnID = $(this).attr(\"data-myid\");\r\n			// Dropped on object parent id\r\n			var droppedOnParentID = $(this).attr(\"data-parentid\");\r\n			// Dropped on object position\r\n			var droppedOnPosition = $(this).attr(\"data-position\");\r\n\r\n			if (myparentid==droppedOnID) { loadPage(); } //alert(\'dropped on parent\');\r\n			// If the DroppedOn is the brother, re-position the elements\r\n			else if (myparentid==droppedOnParentID)\r\n			{\r\n				$.post(\'{$page_name}\',{ operation:\"testnest\", myid:myid, \r\n					myposition:myposition, droppedOnID:droppedOnID, droppedOnPosition:droppedOnPosition\r\n					}, function(data) {\r\n					if (data==\'true\')\r\n					{\r\n						$(\'#reorder_myid\').val(myid);\r\n						$(\'#reorder_myposition\').val(myposition);\r\n						$(\'#reorder_droppedOnID\').val(droppedOnID);\r\n						$(\'#reorder_droppedOnPosition\').val(droppedOnPosition);\r\n\r\n						$(\'#myModalOption\').modal(\'show\');\r\n						//loadPage();\r\n					}\r\n					else\r\n					{\r\n						$.post(\'{$page_name}\',{ operation:\"reorder\", myid:myid, \r\n							myposition:myposition, droppedOnID:droppedOnID,\r\n							droppedOnPosition:droppedOnPosition, droppedOnParentID:droppedOnParentID\r\n							}, function(data) {\r\n							loadPage();\r\n						});\r\n					}\r\n				});\r\n			}\r\n			// If the DroppedOn is a different parent, re-parent the element\r\n			else if (myparentid!=droppedOnParentID)\r\n			{\r\n				$.post(\'{$page_name}\',{ operation:\"testnest2\", myid:myid, \r\n					myposition:myposition, droppedOnID:droppedOnID,\r\n					droppedOnPosition:droppedOnPosition,droppedOnParentID:droppedOnParentID,\r\n					myparentid:myparentid\r\n					}, function(data) {\r\n						//alert(data);\r\n					if (data==\'true\')\r\n					{\r\n						$(\'#reorder_myid\').val(myid);\r\n						$(\'#reorder_myposition\').val(myposition);\r\n						$(\'#reorder_droppedOnID\').val(droppedOnID);\r\n						$(\'#reorder_droppedOnPosition\').val(droppedOnPosition);\r\n						$(\'#reorder_droppedOnParentID\').val(droppedOnParentID);\r\n\r\n						$(\'#myModalOption\').modal(\'show\');\r\n						//loadPage();\r\n					}\r\n					else\r\n					{\r\n\r\n						$.post(\'{$page_name}\',{ operation:\"parentswap\", myid:myid, \r\n							droppedOnID:droppedOnID, droppedOnParentID:droppedOnParentID, droppedOnPosition:droppedOnPosition\r\n							}, function(data) {\r\n							loadPage();\r\n						});\r\n					}\r\n				});\r\n\r\n			}\r\n			}\r\n		});\r\n\r\n		$.post(\'{$page_name}\',{ operation:\"stats\" }, function(data) {\r\n			$(\"#page_body\").append(data);\r\n		});\r\n\r\n		//$(document).scrollTop(window);\r\n		//$(document).scrollLeft(window);\r\n	});\r\n}\r\n\r\nfunction reorder()\r\n{\r\n	var myid = $(\'#reorder_myid\').val();\r\n	var myposition = $(\'#reorder_myposition\').val();\r\n	var droppedOnID = $(\'#reorder_droppedOnID\').val();\r\n	var droppedOnPosition = $(\'#reorder_droppedOnPosition\').val();\r\n	var droppedOnParentID = $(\'#reorder_droppedOnParentID\').val();\r\n\r\n	$.post(\'{$page_name}\',{ operation:\"reorder\", myid:myid, parentpos:\"true\",\r\n		myposition:myposition, droppedOnID:droppedOnID,\r\n		droppedOnPosition:droppedOnPosition,droppedOnParentID:droppedOnParentID\r\n		}, function(data) {\r\n		loadPage();\r\n	});\r\n}\r\nfunction nest()\r\n{\r\n	var myid = $(\'#reorder_myid\').val();\r\n	var myposition = $(\'#reorder_myposition\').val();\r\n	var droppedOnID = $(\'#reorder_droppedOnID\').val();\r\n	var droppedOnPosition = $(\'#reorder_droppedOnPosition\').val();\r\n	var droppedOnParentID = $(\'#reorder_droppedOnParentID\').val();\r\n\r\n	$.post(\'{$page_name}\',{ operation:\"parentswap\", myid:myid, \r\n		droppedOnID:droppedOnID,droppedOnParentID:droppedOnParentID,droppedOnPosition:droppedOnPosition\r\n		}, function(data) {\r\n		loadPage();\r\n	});\r\n}\r\nfunction delete_element(myid,mytag)\r\n{\r\n	$.post(\'{$page_name}\',{ operation:\"delete\", myid:myid\r\n		}, function(data) {\r\n		if (data==\'true\')\r\n		{\r\n			$(\'#delete_myid\').val(myid);\r\n			$(\'#myModalDelete\').modal(\'show\');\r\n		}\r\n		else\r\n		{\r\n			$.post(\'{$page_name}\',{ operation:\"delete_parent\", myid:myid}, function(data) {\r\n				loadPage();\r\n			});\r\n		}\r\n	});\r\n}\r\nfunction delete_element_children()\r\n{\r\n	var myid = $(\'#delete_myid\').val();\r\n	$.post(\'{$page_name}\',{ operation:\"delete_all\", myid:myid},function(data) {\r\n		loadPage();\r\n	});\r\n}\r\n\r\nfunction showBootstrapMenu(elem_class)\r\n{\r\n	// Grab all the variables for the parent element\r\n	var myid = $(\"#click_myid\").val();\r\n	var parentid = $(\"#click_parentid\").val();\r\n	var elementtype = $(\"#click_tagtype\").val();\r\n	if (elem_class==null) element_class = \'\';\r\n	else element_class = elem_class;\r\n\r\n	// Get the bootstrap possible classes and elements\r\n	$.post(\'{$page_name}\',{ operation:\"bootstrap_menu\", myid:myid, parentid:parentid, elementtype:elementtype,elemclass:element_class },function(data) {\r\n		//loadPage();\r\n		$(\'#right_click_menu\').append(data);\r\n	});\r\n}\r\nfunction auto_add(tag,a_class,myid,parentid,text_value)\r\n{\r\n	$.post(\"{$page_name}\", { operation:\"dbadd\",\'data-myid\':myid,\r\n	\'data-parentid\':parentid,\'tag\':tag,\'class\':a_class,\'text\':text_value },function(data) {\r\n		loadPage();\r\n	});\r\n}\r\nfunction auto_toggle(tag,a_class,myid,parentid)\r\n{\r\n	$.post(\"{$page_name}\", { operation:\"dbtoggle\",\'myid\':myid,\r\n	\'data-parentid\':parentid,\'tag\':tag,\'toggle_class\':a_class },function(data) {\r\n		loadPage();\r\n	});\r\n}\r\nfunction auto_radio(tag,a_class,myid,parentid)\r\n{\r\n	$.post(\"{$page_name}\", { operation:\"dbradio\",\'myid\':myid,\r\n	\'data-parentid\':parentid,\'tag\':tag,\'toggle_class\':a_class },function(data) {\r\n		loadPage();\r\n	});\r\n}\r\n\r\n$(document).ready(function() {\r\n	loadPage();\r\n\r\n	$(\'.container-fluid\').bind(\"contextmenu\", function(e) {\r\n		$(\'#testdrop\').removeClass(\'open\');\r\n		return true;\r\n	});\r\n	\r\n	// Disable right click for the whole page\r\n	$(\'#page_body\').bind(\"contextmenu\", function(e) {\r\n	\r\n		// Need to set the menu to 0 so it doesn\'t get picked up by the elementFromPoint\r\n		$(\'#testdrop\').removeClass(\'open\');\r\n		// Remove all the old items\r\n		$(\'.bootstrap_delete\').remove();\r\n		$(\'#onlyfortext\').css({\"display\": \"none\"});\r\n		$(\'#testdrop2\').css({\r\n		top: (0)+\'px\',\r\n		left: (0)+\'px\'\r\n		});\r\n\r\n		var elem = document.elementFromPoint((e.pageX-window.pageXOffset), (e.pageY-window.pageYOffset));\r\n		$(\"#click_myid\").val(\'\');\r\n		$(\"#click_parentid\").val(\'\');\r\n		$(\"#click_tagtype\").val(\'\');\r\n		$(\"#click_textid\").val(\'\');\r\n\r\n		$(\'#testdrop2\').css({\r\n		top: (e.pageY-62)+\'px\',\r\n		left: (e.pageX-5)+\'px\'\r\n		});\r\n\r\n		var shouldExist = elem.getAttribute(\'data-myid\');\r\n\r\n		if (elem!=null && shouldExist!=null)\r\n		{\r\n			// If a user clicks on a span tab for an empty element (like a DIV or UL),\r\n			// make sure the parent menu appears\r\n			var actual_parent_type = elem.getAttribute(\'data-element-type\');\r\n\r\n			if (actual_parent_type!=null)\r\n			{\r\n				$(\"#bold_name\").html(actual_parent_type.toUpperCase());\r\n				$(\"#click_tagtype\").val(actual_parent_type.toLowerCase());\r\n			}\r\n			else\r\n			{\r\n				$(\"#bold_name\").html(elem.tagName);\r\n				$(\"#click_tagtype\").val(elem.tagName.toLowerCase());\r\n			}\r\n\r\n			$(\"#click_myid\").val(elem.getAttribute(\'data-myid\'));\r\n			$(\"#click_parentid\").val(elem.getAttribute(\'data-parentid\'));\r\n\r\n			if (actual_parent_type==\'text\')\r\n			{\r\n				myid = elem.getAttribute(\'data-myid\');\r\n				//alert(\'text!\');\r\n				$(\"#click_textid\").val(myid);\r\n				$(\"#click_myid\").val(elem.getAttribute(\'data-parentid\'));\r\n				$(\'#onlyfortext\').css({\"display\": \"\"});\r\n\r\n				$.post(\"{$page_name}\", { operation:\"quick_parent\",\'myid\':myid },function(data) {\r\n					$(\"#bold_name\").html(data);\r\n					$(\"#click_tagtype\").val(data.toLowerCase());\r\n					$(\'#testdrop\').addClass(\'open\');\r\n				});\r\n			}\r\n			else\r\n			{\r\n				if (elem.getAttribute(\'data-myid\')==\'root\')\r\n				{\r\n					$(\'#notforroot\').css({\"display\": \"none\"});\r\n					$(\"#bold_name\").html(\'Root \' + elem.tagName);\r\n				}\r\n				else $(\'#notforroot\').css({\"display\": \"\"});\r\n				showBootstrapMenu(elem.getAttribute(\'class\'));\r\n				$(\'#testdrop\').addClass(\'open\');\r\n			}\r\n		\r\n			return false;\r\n		}else return false;\r\n	});\r\n});\r\n\r\n</script>\r\n\r\n<div name=\"page_body\" id=\"page_body\" data-myid=\"root\" data-parentid=\"root\" class=\"draggable\">\r\n</div>\r\n\r\n<div name=\"dispose_mod\" id=\"dispose_mod\"></div>\r\n\r\n<div class=\"modal hide\" id=\"myModalOption\">\r\n  <div class=\"modal-header\">\r\n    <button type=\"button\" class=\"close\" data-dismiss=\"modal\">Ã—</button>\r\n    <h3>Reorder or Nest?</h3>\r\n  </div>\r\n  <div class=\"modal-body\">\r\n    <p>Would you like to reorder the dragged element or nest the dragged element?</p>\r\n  </div>\r\n  <div class=\"modal-footer\">\r\n\r\n<input type=\"hidden\" name=\"reorder_myid\" id=\"reorder_myid\" value=\"\" />\r\n<input type=\"hidden\" name=\"reorder_myposition\" id=\"reorder_myposition\" value=\"\" />\r\n<input type=\"hidden\" name=\"reorder_droppedOnID\" id=\"reorder_droppedOnID\" value=\"\" />\r\n<input type=\"hidden\" name=\"reorder_droppedOnPosition\" id=\"reorder_droppedOnPosition\" value=\"\" />\r\n<input type=\"hidden\" name=\"reorder_droppedOnParentID\" id=\"reorder_droppedOnParentID\" value=\"\" />\r\n\r\n    <a href=\"javascript:void(0)\" class=\"btn btn-primary\" data-dismiss=\"modal\" onclick=\"reorder()\">Reorder</a>\r\n    <a href=\"javascript:void(0)\" class=\"btn btn-primary\" data-dismiss=\"modal\" onclick=\"nest()\">Nest</a>\r\n    <a href=\"javascript:void(0)\" class=\"btn\" data-dismiss=\"modal\">Close</a>\r\n\r\n  </div>\r\n</div>\r\n\r\n\r\n\r\n\r\n<div class=\"modal hide\" id=\"myModalDelete\">\r\n  <div class=\"modal-header\">\r\n    <button type=\"button\" class=\"close\" data-dismiss=\"modal\">Ã—</button>\r\n    <h3>Delete Parent and Children?</h3>\r\n  </div>\r\n  <div class=\"modal-body\">\r\n    <p>Would you like to delete the parent element and all children?</p>\r\n  </div>\r\n  <div class=\"modal-footer\">\r\n\r\n<input type=\"hidden\" name=\"delete_myid\" id=\"delete_myid\" value=\"\" />\r\n\r\n    <a href=\"javascript:void(0)\" class=\"btn btn-primary\" data-dismiss=\"modal\" onclick=\"delete_element_children()\">Delete Parent and Children</a>\r\n    <a href=\"javascript:void(0)\" class=\"btn\" data-dismiss=\"modal\">Close</a>\r\n\r\n  </div>\r\n</div>\r\n\r\nOUTPUT;\r\n\r\n}\r\nelse\r\n{\r\n	$back = \'\';\r\n	$code = \'\';\r\n	if (isGroupMember(2)) $back = \'<a style=\"float:right;\" href=\"\'.$page_name.\'\">Back to Dynamic HTML Editor</a><br />\';\r\n\r\n	$plain = \',plain:\"\"\';\r\n	if (isset($_GET[\'code\'])) $code = \',code:\"\"\';\r\n	if (isset($_GET[\'modify\'])) {$code = \',code:\"\"\'; $plain=\',modify:\"\"\'; }\r\n\r\n	$output .= <<< OUTPUT\r\n	<script>\r\n	function loadPage()\r\n	{\r\n		$.post(\'{$page_name}\',{ operation:\"load\"{$plain}{$code} }, function(data) {\r\n			$(\"#page_body\").empty();\r\n			$(\"#page_body\").append(\'{$back}\'+data);\r\n		});\r\n	}\r\n\r\n	function convertCode()\r\n	{\r\n		var newcode = $(\"#new_code\").val();\r\n		$.post(\'{$page_name}\',{ operation:\"convert_code\", newcode:newcode }, function(data) {\r\n			alert(data);\r\n		});\r\n	}\r\n\r\n$(document).ready(function() {\r\n	loadPage();\r\n});\r\n	</script>\r\n	\r\n	<div name=\"page_body\" id=\"page_body\">\r\n	</div>\r\nOUTPUT;\r\n\r\n}\r\n}','2012-07-09 08:16:55',2,1,'// Bootstrap Builder\r\n$arr_bootstrap = array();\r\n\r\narray_push($arr_bootstrap,array(\r\n	\'element\'=>\'div\'\r\n	,\'class\'=>\'row-fluid\'\r\n	,\'toggle\'=>false\r\n	,\'radio\'=>false\r\n	,\'description\'=>\'\'\r\n	,\'requires_class\'=>\'root\'\r\n));\r\n\r\narray_push($arr_bootstrap,array(\r\n	\'element\'=>\'div\'\r\n	,\'class\'=>\'well\'\r\n	,\'toggle\'=>true\r\n	,\'radio\'=>false\r\n	,\'description\'=>\'Dark Background\'\r\n	,\'requires_class\'=>\'row-fluid,span\'\r\n));\r\n\r\n\r\narray_push($arr_bootstrap,array(\r\n	\'element\'=>\'div\'\r\n	,\'class\'=>\'span2,span4,span6,span8,span10,span12\'\r\n	,\'toggle\'=>false\r\n	,\'radio\'=>false\r\n	,\'description\'=>\'\'\r\n	,\'requires_class\'=>\'row-fluid\'\r\n));\r\n\r\narray_push($arr_bootstrap,array(\r\n	\'element\'=>\'a\'\r\n	,\'class\'=>\'btn\'\r\n	,\'toggle\'=>false\r\n	,\'radio\'=>false\r\n	,\'description\'=>\'Button\'\r\n	,\'requires_class\'=>\'row-fluid,span,btn-group,btn-toolbar\'\r\n));\r\n\r\narray_push($arr_bootstrap,array(\r\n	\'element\'=>\'div\'\r\n	,\'class\'=>\'btn-group\'\r\n	,\'toggle\'=>false\r\n	,\'radio\'=>false\r\n	,\'description\'=>\'Button group\'\r\n	,\'requires_class\'=>\'row-fluid,span,btn-group,btn-toolbar\'\r\n));\r\narray_push($arr_bootstrap,array(\r\n	\'element\'=>\'div\'\r\n	,\'class\'=>\'btn-toolbar\'\r\n	,\'toggle\'=>false\r\n	,\'radio\'=>false\r\n	,\'description\'=>\'Button toolbar\'\r\n	,\'requires_class\'=>\'row-fluid,span,btn-group,btn-toolbar\'\r\n));\r\n\r\narray_push($arr_bootstrap,array(\r\n	\'element\'=>\'a\'\r\n	,\'class\'=>\'btn-primary,btn-info,btn-success,btn-warning,btn-danger,btn-inverse\'\r\n	,\'toggle\'=>false\r\n	,\'radio\'=>true\r\n	,\'description\'=>\'Button type\'\r\n	,\'requires_class\'=>\'btn\'\r\n));\r\nif (isset($_POST[\'operation\']) && $_POST[\'operation\']==\'bootstrap_menu\')\r\n{\r\n	if ($_POST[\'myid\']==\'root\') $class = \'root\';\r\n	else $class = $_POST[\'elemclass\'];\r\n	$possible = \'\';\r\n	$possible_toggle = \'\';\r\n	foreach ($arr_bootstrap as $row)\r\n	{\r\n		$check_parents = explode(\',\',$row[\'requires_class\']);\r\n		foreach ($check_parents as $par)\r\n		{\r\n			$available_children = explode(\',\',$row[\'class\']);\r\n			if (stripos($class,$par)!==false)\r\n			{\r\n				foreach ($available_children as $r)\r\n				{\r\n					$my_id = $unique_id = md5(uniqid(rand(), TRUE));\r\n					if ($row[\'toggle\'])\r\n					{\r\n						$onClick = \'onclick=\"auto_toggle(\\\'\'.$row[\'element\'].\'\\\',\\\'\'.$r.\'\\\',\\\'\'.$_POST[\'myid\'].\r\n							\'\\\',\\\'\'.$_POST[\'myid\'].\'\\\')\"\';\r\n						$possible_toggle .= \'<li><a class=\"bootstrap_delete\" href=\"javascript:void(0)\" \'.\r\n							$onClick.\'>\'.$r.\'</a></li>\';\r\n					}\r\n					else if ($row[\'radio\'])\r\n					{\r\n						$onClick = \'onclick=\"auto_radio(\\\'\'.$row[\'element\'].\'\\\',\\\'\'.$r.\'\\\',\\\'\'.$_POST[\'myid\'].\r\n							\'\\\',\\\'\'.$_POST[\'myid\'].\'\\\')\"\';\r\n						$possible_toggle .= \'<li><a class=\"bootstrap_delete\" href=\"javascript:void(0)\" \'.\r\n							$onClick.\'>\'.$r.\'</a></li>\';\r\n					}\r\n					else\r\n					{\r\n						$onClick = \'onclick=\"auto_add(\\\'\'.$row[\'element\'].\'\\\',\\\'\'.$r.\'\\\',\\\'\'.$my_id.\r\n							\'\\\',\\\'\'.$_POST[\'myid\'].\'\\\',\\\'\'.$row[\'description\'].\'\\\')\"\';\r\n						$possible .= \'<li><a class=\"bootstrap_delete\" href=\"javascript:void(0)\" \'.\r\n							$onClick.\'>\'.$r.\'</a></li>\';\r\n					}\r\n				}\r\n			}\r\n		}\r\n	}\r\n\r\n\r\nif ($possible!=\'\')\r\n{\r\n$return_menu_top = <<< OUTPUT\r\n<li class=\"bootstrap_delete\" style=\"text-align: center;\"><a href=\"javascript:void(0)\"><b>Bootstrap</b></a></li>\r\n<li class=\"bootstrap_delete\" style=\"border-bottom:1px solid silver;\"></li>\r\n{$possible}\r\nOUTPUT;\r\n}else $return_menu_top=\'\';\r\n\r\nif ($possible_toggle!=\'\')\r\n{\r\n$return_menu_bottom = <<< OUTPUT\r\n<li class=\"bootstrap_delete\" style=\"text-align: center;\"><a href=\"javascript:void(0)\"><b>Bootstrap Toggle</b></a></li>\r\n<li class=\"bootstrap_delete\" style=\"border-bottom:1px solid silver;\"></li>\r\n{$possible_toggle}\r\nOUTPUT;\r\n}else $return_menu_bottom=\'\';\r\n\r\n	die($return_menu_top.$return_menu_bottom);\r\n}\r\n\r\n\r\n//dynamicPost(\'dyn_\'.\'test\',\'/page_dynamic_demo\');\r\n//dynamicPost(\'dyn_\'.\'test\',\'/toolkit_dynamic\');\r\n//function dynamicPost($table_dynamic,$page_name)\r\n{\r\n// Set the session variable so the number of SQL queries can be counted after a page load\r\nstartSQLCounter();\r\n\r\nif (!isset($page_name)) $page_name = \'/toolkit_dynamic\';\r\nif (!isset($table_dynamic)) $table_dynamic = \'dyn_\'.\'test\';\r\n\r\n$arr_defined = array();			// Array with HTML element definitions\r\n$array_of_index = array();		// Array with numbers of keys to arr_defined\r\n$arr_standard = array();		// Array with standard HTML attributes\r\n\r\narray_push($arr_standard,array(\'class\',\'bootstrap style\'));\r\narray_push($arr_standard,array(\'id\',\'unique ID\'));\r\narray_push($arr_standard,array(\'name\',\'unique name\'));\r\narray_push($arr_standard,array(\'style\',\'CSS separated by semi-colons\'));\r\narray_push($arr_standard,array(\'title\',\'hover over text\'));\r\narray_push($arr_standard,array(\'data-child-side\',\'out-left,in-left,in-right,out-right\'));\r\n\r\n$typical_tags = array(\'a\',\'img\',\'span\');\r\n\r\nfunction array_add($arr,$add)\r\n{\r\n	foreach(array_reverse($add) as $row) { array_unshift($arr,$row); }\r\n	return $arr;\r\n}\r\nfunction array_remove($arr,$remove)\r\n{\r\n	foreach($remove as $row) { unset($arr[$row]); }\r\n	return $arr;\r\n}\r\n\r\n// text - text (made up)\r\narray_push($arr_defined,array(\r\n	\'element\'=>\'text\'\r\n	,\'description\'=>\'Text\'\r\n	,\'required_attribute\'=>array(\'text\')\r\n	,\'required_attribute_description\'=>array(\'text to display\')\r\n	,\'optional_attribute\'=>array()\r\n	,\'optional_attribute_description\'=>array()\r\n	,\'selfclosing\'=>false\r\n	,\'children\'=>array()\r\n));\r\n\r\n// p - paragraph\r\narray_push($arr_defined,array(\r\n	\'element\'=>\'p\'\r\n	,\'description\'=>\'Text\'\r\n	,\'required_attribute\'=>array(\'text\')\r\n	,\'required_attribute_description\'=>array(\'text to display\')\r\n	,\'optional_attribute\'=>array()\r\n	,\'optional_attribute_description\'=>array()\r\n	,\'selfclosing\'=>false\r\n	,\'children\'=>$typical_tags\r\n));\r\n\r\n// br - break\r\narray_push($arr_defined,array(\r\n	\'element\'=>\'br\'\r\n	,\'description\'=>\'Line Break\'\r\n	,\'required_attribute\'=>array()\r\n	,\'required_attribute_description\'=>array()\r\n	,\'optional_attribute\'=>array()\r\n	,\'optional_attribute_description\'=>array()\r\n	,\'selfclosing\'=>true\r\n	,\'children\'=>array()\r\n));\r\n\r\n\r\n// cdata-section - some random section that appears around JavaScript\r\narray_push($arr_defined,array(\r\n	\'element\'=>\'cdata-section\'\r\n	,\'description\'=>\'CDATA section\'\r\n	,\'required_attribute\'=>array()\r\n	,\'required_attribute_description\'=>array()\r\n	,\'optional_attribute\'=>array()\r\n	,\'optional_attribute_description\'=>array()\r\n	,\'selfclosing\'=>false\r\n	,\'notag\'=>true\r\n	,\'children\'=>array()\r\n));\r\n\r\n// div - division\r\narray_push($arr_defined,array(\r\n	\'element\'=>\'div\'\r\n	,\'description\'=>\'Division or section\'\r\n	,\'required_attribute\'=>array()\r\n	,\'required_attribute_description\'=>array()\r\n	,\'optional_attribute\'=>array()\r\n	,\'optional_attribute_description\'=>array()\r\n	,\'selfclosing\'=>false\r\n	,\'children\'=>array_add($typical_tags,array(\'div\',\'p\',\'h1\',\'h2\',\'h3\',\'h4\',\'h5\',\'h6\',\'ul\',\'ol\',\'li\',\'dl\',\'blockquote\',\'pre\',\'small\'))\r\n));\r\n\r\n// img - image\r\narray_push($arr_defined,array(\r\n	\'element\'=>\'img\'\r\n	,\'description\'=>\'Image\'\r\n	,\'required_attribute\'=>array(\'alt\',\'src\')\r\n	,\'required_attribute_description\'=>array(\'alternative text\',\'image URL\')\r\n	,\'optional_attribute\'=>array()\r\n	,\'optional_attribute_description\'=>array()\r\n	,\'selfclosing\'=>true\r\n	,\'children\'=>array()\r\n));\r\n\r\n// a - anchor\r\narray_push($arr_defined,array(\r\n	\'element\'=>\'a\'\r\n	,\'description\'=>\'Link or anchor\'\r\n	,\'required_attribute\'=>array(\'text\',\'href\')\r\n	,\'required_attribute_description\'=>array(\'text to display\',\'URL of the link\')\r\n	,\'optional_attribute\'=>array()\r\n	,\'optional_attribute_description\'=>array()\r\n	,\'selfclosing\'=>false\r\n	,\'children\'=>array_remove($typical_tags,array(\'a\'))\r\n));\r\n\r\n// span - part of a text\r\narray_push($arr_defined,array(\r\n	\'element\'=>\'span\'\r\n	,\'description\'=>\'Part of a text\'\r\n	,\'required_attribute\'=>array(\'text\')\r\n	,\'required_attribute_description\'=>array(\'text to display\')\r\n	,\'optional_attribute\'=>array()\r\n	,\'optional_attribute_description\'=>array()\r\n	,\'selfclosing\'=>false\r\n	,\'children\'=>array_remove($typical_tags,array(\'span\'))\r\n));\r\n\r\n// h1 - heading\r\narray_push($arr_defined,array(\r\n	\'element\'=>\'h1\'\r\n	,\'description\'=>\'Heading 1\'\r\n	,\'required_attribute\'=>array(\'text\')\r\n	,\'required_attribute_description\'=>array(\'text to display\')\r\n	,\'optional_attribute\'=>array()\r\n	,\'optional_attribute_description\'=>array()\r\n	,\'selfclosing\'=>false\r\n	,\'children\'=>$typical_tags\r\n));\r\n// h2 - heading\r\narray_push($arr_defined,array(\r\n	\'element\'=>\'h2\'\r\n	,\'description\'=>\'Heading 2\'\r\n	,\'required_attribute\'=>array(\'text\')\r\n	,\'required_attribute_description\'=>array(\'text to display\')\r\n	,\'optional_attribute\'=>array()\r\n	,\'optional_attribute_description\'=>array()\r\n	,\'selfclosing\'=>false\r\n	,\'children\'=>$typical_tags\r\n));\r\n// h3 - heading\r\narray_push($arr_defined,array(\r\n	\'element\'=>\'h3\'\r\n	,\'description\'=>\'Heading 3\'\r\n	,\'required_attribute\'=>array(\'text\')\r\n	,\'required_attribute_description\'=>array(\'text to display\')\r\n	,\'optional_attribute\'=>array()\r\n	,\'optional_attribute_description\'=>array()\r\n	,\'selfclosing\'=>false\r\n	,\'children\'=>$typical_tags\r\n));\r\n// h4 - heading\r\narray_push($arr_defined,array(\r\n	\'element\'=>\'h4\'\r\n	,\'description\'=>\'Heading 4\'\r\n	,\'required_attribute\'=>array(\'text\')\r\n	,\'required_attribute_description\'=>array(\'text to display\')\r\n	,\'optional_attribute\'=>array()\r\n	,\'optional_attribute_description\'=>array()\r\n	,\'selfclosing\'=>false\r\n	,\'children\'=>$typical_tags\r\n));\r\n// h5 - heading\r\narray_push($arr_defined,array(\r\n	\'element\'=>\'h5\'\r\n	,\'description\'=>\'Heading 5\'\r\n	,\'required_attribute\'=>array(\'text\')\r\n	,\'required_attribute_description\'=>array(\'text to display\')\r\n	,\'optional_attribute\'=>array()\r\n	,\'optional_attribute_description\'=>array()\r\n	,\'selfclosing\'=>false\r\n	,\'children\'=>$typical_tags\r\n));\r\n// h6 - heading\r\narray_push($arr_defined,array(\r\n	\'element\'=>\'h6\'\r\n	,\'description\'=>\'Heading 6\'\r\n	,\'required_attribute\'=>array(\'text\')\r\n	,\'required_attribute_description\'=>array(\'text to display\')\r\n	,\'optional_attribute\'=>array()\r\n	,\'optional_attribute_description\'=>array()\r\n	,\'selfclosing\'=>false\r\n	,\'children\'=>$typical_tags\r\n));\r\n\r\n// ul - unordered list\r\narray_push($arr_defined,array(\r\n	\'element\'=>\'ul\'\r\n	,\'description\'=>\'Unordered list\'\r\n	,\'required_attribute\'=>array()\r\n	,\'required_attribute_description\'=>array()\r\n	,\'optional_attribute\'=>array()\r\n	,\'optional_attribute_description\'=>array()\r\n	,\'selfclosing\'=>false\r\n	,\'children\'=>array_add($typical_tags,array(\'li\'))\r\n));\r\n\r\n// ol - ordered list\r\narray_push($arr_defined,array(\r\n	\'element\'=>\'ol\'\r\n	,\'description\'=>\'Ordered list\'\r\n	,\'required_attribute\'=>array()\r\n	,\'required_attribute_description\'=>array()\r\n	,\'optional_attribute\'=>array()\r\n	,\'optional_attribute_description\'=>array()\r\n	,\'selfclosing\'=>false\r\n	,\'children\'=>array_add($typical_tags,array(\'li\'))\r\n));\r\n\r\n// dl - definition list\r\narray_push($arr_defined,array(\r\n	\'element\'=>\'dl\'\r\n	,\'description\'=>\'Definition list\'\r\n	,\'required_attribute\'=>array()\r\n	,\'required_attribute_description\'=>array()\r\n	,\'optional_attribute\'=>array()\r\n	,\'optional_attribute_description\'=>array()\r\n	,\'selfclosing\'=>false\r\n	,\'children\'=>array_add($typical_tags,array(\'dt\',\'dd\'))\r\n));\r\n\r\n// li - list item\r\narray_push($arr_defined,array(\r\n	\'element\'=>\'li\'\r\n	,\'description\'=>\'List item\'\r\n	,\'required_attribute\'=>array(\'text\')\r\n	,\'required_attribute_description\'=>array(\'text to display\')\r\n	,\'optional_attribute\'=>array()\r\n	,\'optional_attribute_description\'=>array()\r\n	,\'selfclosing\'=>false\r\n	,\'children\'=>$typical_tags\r\n));\r\n\r\n// dt - definition list item\r\narray_push($arr_defined,array(\r\n	\'element\'=>\'dt\'\r\n	,\'description\'=>\'Definition list item\'\r\n	,\'required_attribute\'=>array(\'text\')\r\n	,\'required_attribute_description\'=>array(\'text to display\')\r\n	,\'optional_attribute\'=>array()\r\n	,\'optional_attribute_description\'=>array()\r\n	,\'selfclosing\'=>false\r\n	,\'children\'=>$typical_tags\r\n));\r\n\r\n// dd - description of list item\r\narray_push($arr_defined,array(\r\n	\'element\'=>\'dd\'\r\n	,\'description\'=>\'Description of list item\'\r\n	,\'required_attribute\'=>array(\'text\')\r\n	,\'required_attribute_description\'=>array(\'text to display\')\r\n	,\'optional_attribute\'=>array()\r\n	,\'optional_attribute_description\'=>array()\r\n	,\'selfclosing\'=>false\r\n	,\'children\'=>$typical_tags\r\n));\r\n\r\n// blockquote - blockquote\r\narray_push($arr_defined,array(\r\n	\'element\'=>\'blockquote\'\r\n	,\'description\'=>\'Text\'\r\n	,\'required_attribute\'=>array(\'text\')\r\n	,\'required_attribute_description\'=>array(\'text to display\')\r\n	,\'optional_attribute\'=>array()\r\n	,\'optional_attribute_description\'=>array()\r\n	,\'selfclosing\'=>false\r\n	,\'children\'=>array_add($typical_tags,array(\'small\'))\r\n));\r\n\r\n// pre - pre\r\narray_push($arr_defined,array(\r\n	\'element\'=>\'pre\'\r\n	,\'description\'=>\'Preformatted text\'\r\n	,\'required_attribute\'=>array(\'text\')\r\n	,\'required_attribute_description\'=>array(\'text to display\')\r\n	,\'optional_attribute\'=>array()\r\n	,\'optional_attribute_description\'=>array()\r\n	,\'selfclosing\'=>false\r\n	,\'children\'=>$typical_tags\r\n));\r\n\r\n// small - smaller text\r\narray_push($arr_defined,array(\r\n	\'element\'=>\'small\'\r\n	,\'description\'=>\'Smaller text\'\r\n	,\'required_attribute\'=>array(\'text\')\r\n	,\'required_attribute_description\'=>array(\'text to display\')\r\n	,\'optional_attribute\'=>array()\r\n	,\'optional_attribute_description\'=>array()\r\n	,\'selfclosing\'=>false\r\n	,\'children\'=>$typical_tags\r\n));\r\n\r\n\r\n// Load an array with keys to the defined array of element definitions\r\nforeach($arr_defined as $row) { array_push($array_of_index,$row[\'element\']); }\r\n\r\n// Get the defined element array for one element\r\nfunction getElementArray($element)\r\n{\r\n	global $array_of_index;\r\n	global $arr_defined;\r\n\r\n	// Get the key in the defined array for the parent element\r\n	$key = array_search($element,$array_of_index);\r\n	// If the parent element is not defined, just use the DIV element\r\n	if ($key===false) array_search(\'div\',$array_of_index);\r\n	\r\n	// The array that represents the parent element\r\n	$current_parent_array = $arr_defined[$key];\r\n\r\n	return $current_parent_array;\r\n}\r\n\r\n// Adds an input box for an HTML attribute\r\nfunction build_attrib($name,$placeholder,$value=\'\')\r\n{\r\n	$cap = ucfirst($name);\r\n	$safe_value = htmlentities($value, ENT_QUOTES, \'UTF-8\');\r\n	return <<< OUTPUT\r\n	<div class=\"control-group\">\r\n		<label class=\"control-label\" for=\"{$name}\">{$cap}</label>\r\n		<div class=\"controls\"><input type=\"text\" name=\"{$name}\" id=\"{$name}\" \r\n		placeholder=\"{$placeholder}\" value=\"{$safe_value}\"></div>\r\n	</div>\r\nOUTPUT;\r\n}\r\n\r\nfunction getContentArray($myid)\r\n{\r\n	global $table_dynamic;\r\n	// Grab the Dragged element\r\n	$result = query(\'SELECT my_id,content,parent FROM \'.$table_dynamic.\' WHERE my_id=? LIMIT 1\', array($myid));\r\n	\r\n	if ($result)\r\n	{\r\n		// Convert the json encoded string to an array\r\n		$a = json_decode($result[0][\'content\'],true);\r\n		$a[\'data-myid\']=$result[0][\'my_id\'];\r\n		$a[\'data-parentid\']=$result[0][\'parent\'];\r\n\r\n	}\r\n	else $a = false;\r\n	return $a;\r\n}\r\nfunction getElementType($myid)\r\n{\r\n	$a = getContentArray($myid);\r\n	// Get the HTML element type\r\n	return $a[\'tag\'];\r\n}\r\n\r\nfunction getChildren($result, $parentid, $isPre, $prepend)\r\n{\r\n	global $table_dynamic;\r\n	$admin=isGroupMember(2);\r\n\r\n	if (isset($_POST[\'plain\'])) $admin=false;\r\n	//if (isset($_POST[\'modify\'])) $admin=false;\r\n\r\n	$return = \'\';\r\n	$position = 0;\r\n	foreach($result as $row)\r\n	{\r\n		// Skip to the next if the seek element is the parent or itself\r\n		if ($row[\'parent\']!=$parentid || $row[\'my_id\']==$parentid) continue;\r\n\r\n		// Update the position of the element\r\n		$qry = \'UPDATE \'.$table_dynamic.\' SET position=? WHERE id=?\';\r\n		$params = array($position,$row[\'id\']);\r\n		if ($admin) query_execute($qry, $params);\r\n\r\n		// Convert the json encoded string to an array\r\n		$a = json_decode($row[\'content\'],true);\r\n		// Add the position as an html attribute\r\n		if ($admin) $a[\'data-position\']=$position;\r\n\r\n		// Get the HTML element type\r\n		$element = $a[\'tag\'];\r\n\r\n// Don\'t output comments\r\nif ($element==\'comment\') continue;\r\n\r\n		// Get the HTML element template\r\n		$template = getElementArray($element);\r\n\r\n		$loc = \'in-right\';\r\n		if (isset($a[\'data-child-side\']))\r\n		{\r\n			$loc = $a[\'data-child-side\'];\r\n			unset($a[\'data-child-side\']);\r\n		}\r\n\r\n		// Add each attribute to a string\r\n		$attrib = \'\';\r\n		foreach($a as $key=>$val)\r\n		{\r\n			// Only add attributes and exclude \'text\'\r\n			//if (strpos($key,\'\')===0 && $key != \'text\' && strlen($val)>0)\r\n			if ($key != \'text\' && $key != \'tag\')\r\n			{\r\n				$val2 = \'\';\r\n				if ($key==\'class\' && $admin && !isset($_POST[\'modify\'])) $val2=\' draggable\';\r\n				$attrib .= \' \'.$key.\'=\"\'.$val.$val2.\'\"\';\r\n			}\r\n		}\r\n\r\n		// Ensure the ID and Parent ID are on the element\r\n		if ($admin) $attrib .= \' data-myid=\"\'.$row[\'my_id\'].\'\" data-parentid=\"\'.$row[\'parent\'].\'\"\';\r\n\r\n		// Ensure the draggable class is added to each element\r\n		if ($admin && !isset($_POST[\'modify\'])) if (strpos($attrib,\'class=\')===false) $attrib .= \' class=\"draggable\"\';\r\n\r\n		// Output the element based on the type\r\n		//if ($element==\'text\') $return .= \'<span>\'.$a[\'text\'].\'</span>\';\r\n		if ($element==\'text\')\r\n		{\r\n\r\n			// Set the element text attribute since it is between the element tags\r\n			if (isset($a[\'text\']) && strlen($a[\'text\'])>0) $text = $a[\'text\'];\r\n			else\r\n			{\r\n				$text=\'\';\r\n			}\r\n\r\n			if ($admin && !isset($_POST[\'modify\']) && $text!=\'\') $return .= \'<text class=\"dynamic-text-markup\" data-element-type=\"\'.$element.\'\" data-myid=\"\'.$row[\'my_id\'].\'\" \r\n			data-parentid=\"\'.$row[\'parent\'].\'\">\'.$text.\'</text>\';\r\n\r\n			//if ($admin) $return .= smartDB($row,\'column\',$table_dynamic);\r\n\r\n			else $return .= $text;\r\n		}\r\n		else if (isset($template[\'notag\']) && $template[\'notag\']) $return .= $a[\'text\'];\r\n		else if ($template[\'selfclosing\']) $return .= \"<$element$attrib/>\";\r\n		else\r\n		{\r\n			if ($isPre || $element==\'pre\' || $element==\'textarea\') $newline = \'\';\r\n			else $newline = NEWLINE;\r\n//if (isset($_POST[\'modify\'])) $newline = \'\';\r\n\r\n			// Retrieve all the children tags\r\n//if (isset($_POST[\'modify\'])) $children = getChildren($result, $row[\'my_id\'],($newline!=NEWLINE),\'\');\r\n			//else\r\n$children = getChildren($result, $row[\'my_id\'],($newline!=NEWLINE),$prepend.\'	\');\r\n\r\n//if (isset($_POST[\'modify\'])) $prepend=\'\';\r\n\r\n			$text = \'\';\r\n			// Set the element text attribute since it is between the element tags\r\n			if (isset($a[\'text\']) && strlen($a[\'text\'])>0) $text = $a[\'text\'];\r\n			else if ($children==\'\')\r\n			{\r\n				if ($admin && !isset($_POST[\'modify\'])) $text=\'<span class=\"dynamic-markup\" data-element-type=\"\'.$element.\'\" data-myid=\"\'.$row[\'my_id\'].\'\" \r\n				data-parentid=\"\'.$row[\'parent\'].\'\">***** Empty \'.strtoupper($element).\' *****</span>\';\r\n			}\r\n\r\n			$end_line = \'\';\r\n\r\n			if ($isPre || $element==\'pre\' || $element==\'textarea\')\r\n			{\r\n				$newline = NEWLINE;\r\n				$prepend = \'\';\r\n			}\r\n			\r\n			// Make sure children start on a new line\r\n			if ($children!=\'\' && strpos(trim($children),\'<\')===0)\r\n			{\r\n				//$children = $newline.$prepend.$children;\r\n				$children = $prepend.$children;\r\n				$end_line = \"$newline$prepend\";\r\n			}\r\n\r\n			if ($loc==\'out-left\') $variation = \"$children$newline<$element$attrib>$text$end_line</$element>\";\r\n			else if ($loc==\'in-left\')  $variation = $newline.$prepend.\"<$element$attrib>$children$text$end_line</$element>\";\r\n			else if ($loc==\'in-right\') $variation = \"$newline$prepend<$element$attrib>$text$children$end_line</$element>\";\r\n			else if ($loc==\'out-right\') $variation = $newline.$prepend.\"<$element$attrib>$text</$element>$end_line$children\";\r\n			$return .= $variation;\r\n		}\r\n		// Update the position to the next one\r\n		$position+=1;\r\n	}\r\nreturn $return;\r\n}\r\n\r\n// Determines if the dragged element can be dropped on the DroppedOn element\r\nfunction isAllowedDrop($myid, $droppedOnID)\r\n{\r\n	if ($droppedOnID==\'root\') return true;\r\n	// Get the Dragged element\r\n	$element = getElementType($myid);\r\n\r\n	// Grab the DroppedOn element\r\n	$element2 = getElementType($droppedOnID);\r\n\r\n	// Get the HTML element template for the DroppedOn element\r\n	$template = getElementArray($element2);\r\n\r\n	// If the Dragged element is an allowed child of the DroppedOn element, return true\r\n	if (in_array($element,$template[\'children\'])!==false) return true;\r\n	else return false;\r\n}\r\n\r\nif (isset($_POST[\'operation\']) && $_POST[\'operation\']==\'load\')\r\n{\r\n	// MySQL query with LIMIT to improve performance\r\n	$qry = \'SELECT my_id,content,parent,id,position FROM \'.$table_dynamic.\' ORDER BY position\';\r\n\r\n	// Pass a variable via GET or POST\r\n	$params = array();\r\n	\r\n	// Execute the query with the query and parameters\r\n	$result = query($qry, $params);\r\n\r\n	// Build the element tree\r\n	$return = getChildren($result,\'root\',false,\'\');\r\n\r\n	if (isset($_POST[\'modify\']))\r\n	{\r\n		$return = \'<textarea name=\"new_code\" id=\"new_code\" wrap=\"off\" style=\"width: 100%;height: 500px;\">\'.htmlentities($return, ENT_QUOTES, \'UTF-8\').\'</textarea>\';\r\n		$return .= <<< OUTPUT\r\n<div class=\"btn-group\">\r\n<a class=\"btn\" href=\"javascript:void(0)\" onclick=\"convertCode()\">Update</a>\r\n</div>\r\nOUTPUT;\r\n	}\r\n	else if (isset($_POST[\'code\']))\r\n	{\r\n		$return = \'<textarea wrap=\"off\" style=\"width: 100%; height:500px;\">\'.htmlentities($return, ENT_QUOTES, \'UTF-8\').\'</textarea>\';\r\n	}\r\n\r\n	die($return);\r\n}\r\n\r\n// Prevent everyone but admins from posting to unsafe functions\r\nif (!isGroupMember(2)) die(\'You do not have access to this function.\');\r\n\r\n\r\nif (isset($_POST[\'operation\']) && $_POST[\'operation\']==\'delete\')\r\n{\r\n	// Determine if the element you want to delete has children\r\n	$result = query(\'SELECT my_id FROM \'.$table_dynamic.\' WHERE parent=? LIMIT 1\', array($_POST[\'myid\']));\r\n	if ($result) die(\"true\");\r\n	else die(\"false\");\r\n}\r\nelse if (isset($_POST[\'operation\']) && $_POST[\'operation\']==\'delete_parent\')\r\n{\r\n	query_execute(\'DELETE FROM \'.$table_dynamic.\' WHERE my_id=? LIMIT 1\', array($_POST[\'myid\']));\r\n	die();\r\n}\r\nelse if (isset($_POST[\'operation\']) && $_POST[\'operation\']==\'delete_all\')\r\n{\r\n	function delete_children($id,$table_dynamic)\r\n	{		\r\n		// Get all the children of the passed parent\r\n		$result = query(\'SELECT my_id FROM \'.$table_dynamic.\' WHERE parent=?\', array($id));\r\n		// Foreach child\r\n		foreach($result as $row)\r\n		{\r\n			// Find there children\r\n			delete_children($row[\'my_id\'],$table_dynamic);\r\n	\r\n			// Delete the current child\r\n			query_execute(\'DELETE FROM \'.$table_dynamic.\' WHERE my_id=? LIMIT 1\', array($row[\'my_id\']));	\r\n		}\r\n		// Delete the parent\r\n		query_execute(\'DELETE FROM \'.$table_dynamic.\' WHERE my_id=? LIMIT 1\', array($id));\r\n	}\r\n	delete_children($_POST[\'myid\'],$table_dynamic);\r\n}\r\nelseif (isset($_POST[\'operation\']) && ($_POST[\'operation\']==\'build\' || $_POST[\'operation\']==\'build_edit\'))\r\n{\r\n// Types of tags\r\n//$arr_main = array(\'a\',\'img\',\'p\',\'pre\',\'div\',\'h1\',\'h2\',\'h3\',\'h4\',\'h5\',\'h6\',\'ul\',\'li\');\r\n\r\n$return = \'\';						// Output of the modal to return using DIE\r\n$parent_children = \'<option value=\"\"></option>\';	// Build the option selector for the children\r\n\r\n\r\n$unique_id = md5(uniqid(rand(), TRUE));\r\n$parent_id = $_POST[\'parent_id\'];\r\n$parent_type = $_POST[\'parent_element\'];\r\n\r\n$opt1 = \'Add an Element to \'.$_POST[\'parent_element\'];\r\n$script = \'\';\r\n$btn = \'<a href=\"javascript:void(0)\" class=\"btn btn-primary\" onclick=\"addElement()\">Add</a>\';\r\n$post_type = \'dbadd\';\r\n$my_type = \'\';\r\n$change_inputs = \'Add\';\r\n\r\nif ($_POST[\'operation\']==\'build_edit\')\r\n{\r\n	$opt1 = \'Edit Element\';\r\n$change_inputs = \'Edit\';\r\n	$content = getContentArray($_POST[\'parent_id\']);\r\n	$unique_id = $content[\'data-myid\'];\r\n	$parent_id = $content[\'data-parentid\'];\r\n	$my_type = $content[\'tag\'];\r\n	$parent_content = getContentArray($parent_id);\r\n	if (!$parent_content) $parent_type = \'div\';\r\n	else $parent_type = $parent_content[\'tag\'];\r\n\r\n$script = \'<script>changeInputs\'.$change_inputs.\'();</script>\';\r\n//$btn = \'<button class=\"btn btn-primary\" onclick=\"updateElement();\">Update</button>\';\r\n$btn = \'<a class=\"btn btn-primary\" href=\"javascript:void(0)\" onclick=\"updateElement();\">Update</a>\';\r\n$post_type = \'dbupdate\';\r\n}\r\n\r\n$current_parent_array = getElementArray($parent_type);\r\n\r\n// For each allowed child of the parent\r\nforeach($current_parent_array[\'children\'] as $row)\r\n{\r\n	// Determine if the child is in the defined array of HTML elements\r\n	$k = array_search($row,$array_of_index);\r\n\r\nif ($row==$my_type && $_POST[\'operation\']==\'build_edit\') $selected=\' selected=\"selected\" \';\r\nelse $selected = \'\';\r\n\r\n	// If the element is FOUND, output the description\r\n	if ($k!==false) $parent_children .= \'<option \'.$selected.\' value=\"\'.$row.\'\">\'.$arr_defined[$k][\'description\'].\' (\'.$row.\')</option>\';\r\n	// Else the element is NOT found so do not output the description name because you do not have it\r\n	else $parent_children .= \'<option \'.$selected.\' value=\"\'.$row.\'\">\'.$row.\'</option>\';\r\n}\r\n\r\n$return = <<< OUTPUT\r\n<div class=\"modal hide\" id=\"myModal\">\r\n	<div class=\"modal-header\">\r\n		<button type=\"button\" class=\"close\" data-dismiss=\"modal\">Ã—</button>\r\n		<h3>{$opt1}</h3>\r\n	</div>\r\n	\r\n	<form class=\"form-horizontal\" name=\"dyn_form\" id=\"dyn_form\">\r\n		<input type=\"hidden\" name=\"operation\" id=\"operation\" value=\"{$post_type}\">\r\n		<input type=\"hidden\" name=\"data-parentid\" id=\"data-parentid\" value=\"{$parent_id}\">\r\n		<input type=\"hidden\" name=\"data-myid\" id=\"data-myid\" value=\"{$unique_id}\">\r\n	  	<div class=\"modal-body\">\r\n			<fieldset>\r\n				<div class=\"control-group\">\r\n					<label class=\"control-label\" for=\"tag\">Tag</label>\r\n					<div class=\"controls\">\r\n					<select name=\"tag\" id=\"tag\" onchange=\"changeInputs{$change_inputs}()\">\r\n						{$parent_children}\r\n					</select>\r\n					</div>\r\n				</div>\r\n\r\n<div name=\"dispose_mod_input_top\" id=\"dispose_mod_input_top\"></div>\r\n\r\n<div name=\"dispose_mod_input_bottom\" id=\"dispose_mod_input_bottom\"></div>\r\n				\r\n			</fieldset>\r\n	  	</div> <!-- modal-body -->\r\n		<div class=\"modal-footer\">\r\n			{$btn}\r\n			<button type=\"reset\" class=\"btn btn-danger\">Reset</button>\r\n			<a href=\"javascript:void(0)\" class=\"btn\" data-dismiss=\"modal\">Close</a>\r\n		</div>\r\n	</form>\r\n</div>\r\n\r\n<script>\r\nfunction changeInputsAdd()\r\n{\r\n	var val = $(\'#tag\').val();\r\n	var val2 = $(\'#data-myid\').val();\r\n	$.post(\'{$page_name}\',{ operation:\"change_top\", selected_element:val, myid:val2 }, function(data) {\r\n		$(\"#dispose_mod_input_top\").empty();\r\n		$(\"#dispose_mod_input_top\").append(data);\r\n		//$(\'#myModal\').modal(\'show\');\r\n	});\r\n	$.post(\'{$page_name}\',{ operation:\"change_bottom\", selected_element:val, myid:val2 }, function(data) {\r\n		$(\"#dispose_mod_input_bottom\").empty();\r\n		$(\"#dispose_mod_input_bottom\").append(data);\r\n		$(\'#myModal\').modal(\'show\');\r\n	});\r\n}\r\nfunction changeInputsEdit()\r\n{\r\n	var val = $(\'#tag\').val();\r\n	var val2 = $(\'#data-myid\').val();\r\n	$.post(\'{$page_name}\',{ operation:\"change_top\", selected_element:val, myid:val2, isedit:\'true\' }, function(data) {\r\n		$(\"#dispose_mod_input_top\").empty();\r\n		$(\"#dispose_mod_input_top\").append(data);\r\n		//$(\'#myModal\').modal(\'show\');\r\n	});\r\n	$.post(\'{$page_name}\',{ operation:\"change_bottom\", selected_element:val, myid:val2, isedit:\'true\' }, function(data) {\r\n		$(\"#dispose_mod_input_bottom\").empty();\r\n		$(\"#dispose_mod_input_bottom\").append(data);\r\n		$(\'#myModal\').modal(\'show\');\r\n	});\r\n}\r\nfunction addElement()\r\n{\r\n	$.post(\"{$page_name}\", $(\"#dyn_form\").serialize(),function(data) {\r\n		//window.location = \"{$_SERVER[\'REQUEST_URI\']}\";\r\n		// Set the unique ID to a new one so multiple elements can be created without closing the modal\r\n		$(\'#data-myid\').val(data);\r\n		loadPage();\r\n	});\r\n}\r\nfunction updateElement()\r\n{\r\n	var scrollTop = $(document).scrollTop();\r\n	var scrollLeft = $(document).scrollLeft();\r\n	$.post(\"{$page_name}\", $(\"#dyn_form\").serialize(),function(data) {\r\n		loadPage();\r\n		//$(document).scrollTop(scrollTop);\r\n		//$(document).scrollLeft(scrollLeft);\r\n		//return false;\r\n	});\r\n		//return false;\r\n}\r\n</script>\r\n\r\n{$script}\r\n\r\nOUTPUT;\r\n//if ($my_type==\'text\') $disable_all = true;\r\nif (isset($_POST[\'textid\']))\r\n{\r\n\r\n$unique_id = $_POST[\'textid\'];\r\n$mycontent = getContentArray($unique_id);\r\n$parent_id = $mycontent[\'data-parentid\'];\r\n$text = htmlentities($mycontent[\'text\'], ENT_QUOTES, \'UTF-8\');\r\n\r\n$return = <<< OUTPUT\r\n<div class=\"modal hide\" id=\"myModal\">\r\n	<div class=\"modal-header\">\r\n		<button type=\"button\" class=\"close\" data-dismiss=\"modal\">Ã—</button>\r\n		<h3>{$opt1}</h3>\r\n	</div>\r\n	\r\n	<form class=\"form-horizontal\" name=\"dyn_form\" id=\"dyn_form\">\r\n		<input type=\"hidden\" name=\"operation\" id=\"operation\" value=\"{$post_type}\">\r\n		<input type=\"hidden\" name=\"data-parentid\" id=\"data-parentid\" value=\"{$parent_id}\">\r\n		<input type=\"hidden\" name=\"data-myid\" id=\"data-myid\" value=\"{$unique_id}\">\r\n		<input type=\"hidden\" name=\"tag\" id=\"tag\" value=\"text\">\r\n	  	<div class=\"modal-body\">\r\n			<fieldset>\r\n\r\n\r\n<div name=\"dispose_mod_input_top\" id=\"dispose_mod_input_top\">\r\n\r\n<div class=\"control-group\">\r\n		<label class=\"control-label\" for=\"text\">Text</label>\r\n		<div class=\"controls\"><input type=\"text\" name=\"text\" id=\"text\" placeholder=\"text to display\" value=\"{$text}\"></div>\r\n	</div>\r\n</div>\r\n\r\n<div name=\"dispose_mod_input_bottom\" id=\"dispose_mod_input_bottom\"></div>\r\n				\r\n			</fieldset>\r\n	  	</div> <!-- modal-body -->\r\n		<div class=\"modal-footer\">\r\n			{$btn}\r\n			<button type=\"reset\" class=\"btn btn-danger\">Reset</button>\r\n			<a href=\"javascript:void(0)\" class=\"btn\" data-dismiss=\"modal\">Close</a>\r\n		</div>\r\n	</form>\r\n</div>\r\n\r\n<script>\r\nfunction updateElement()\r\n{\r\n	var scrollTop = $(document).scrollTop();\r\n	var scrollLeft = $(document).scrollLeft();\r\n	$.post(\"{$page_name}\", $(\"#dyn_form\").serialize(),function(data) {\r\n		loadPage();\r\n		//$(document).scrollTop(scrollTop);\r\n		//$(document).scrollLeft(scrollLeft);\r\n		//return false;\r\n	});\r\n		//return false;\r\n}\r\n</script>\r\n\r\nOUTPUT;\r\n}\r\n\r\n	die($return);\r\n}\r\nelse if (isset($_POST[\'operation\']) && $_POST[\'operation\']==\'change_top\')\r\n{\r\n\r\n	$content_array = getContentArray($_POST[\'myid\']);\r\n\r\n	$current_selected_array = getElementArray($_POST[\'selected_element\']);\r\n	$return = \'\';\r\n\r\n	for ($i=0;$i<count($current_selected_array[\'required_attribute\']);$i++)\r\n	{\r\n		$attrib = $current_selected_array[\'required_attribute\'][$i];\r\n\r\n		if (isset($_POST[\'isedit\']) && $attrib==\'text\') continue;\r\n\r\n		if ($content_array && isset($content_array[\'\'.$attrib])) $value = $content_array[\'\'.$attrib];\r\n		else $value=\'\';\r\n		$return .= build_attrib($attrib,\r\n			$current_selected_array[\'required_attribute_description\'][$i],$value);\r\n	}\r\n	die($return);\r\n}\r\nelse if (isset($_POST[\'operation\']) && $_POST[\'operation\']==\'change_bottom\')\r\n{\r\n	$current_selected_array = getElementArray($_POST[\'selected_element\']);\r\n	$content_array = getContentArray($_POST[\'myid\']);\r\n	global $arr_standard;\r\n	$return = \'\';\r\n	\r\n	$return .= \'<a href=\"javascript:void(0)\" data-toggle=\"collapse\" data-target=\"#collapse_advanced\" style=\"cursor:pointer;\">Optional</a>\r\n		<div class=\"collapse in\" id=\"collapse_advanced\" name=\"collapse_advanced\">\';\r\n\r\n	for ($i=0;$i<count($current_selected_array[\'optional_attribute\']);$i++)\r\n	{\r\n		$attrib = $current_selected_array[\'optional_attribute\'][$i];\r\n\r\n		if (isset($_POST[\'isedit\']) && $attrib==\'text\') continue;\r\n\r\n		if ($content_array && isset($content_array[\'\'.$attrib])) $value = $content_array[\'\'.$attrib];\r\n		else $value=\'\';\r\n		$return .= build_attrib($attrib,\r\n			$current_selected_array[\'optional_attribute_description\'][$i],$value);\r\n	}\r\n\r\n	foreach($arr_standard as $row)\r\n	{\r\n		if ($content_array && isset($content_array[\'\'.$row[0]])) $value = $content_array[\'\'.$row[0]];\r\n		else $value=\'\';\r\n		$return .= build_attrib($row[0],$row[1],$value);\r\n	}\r\n\r\n	$return .= \'</div> <! -- collapse_advanced -->\';\r\n	die($return);\r\n}\r\nelse if (isset($_POST[\'operation\']) && $_POST[\'operation\']==\'dbadd\')\r\n{\r\n	// Remove these from the content array\r\n	$myid = $_POST[\'data-myid\'];\r\n	$parentid = $_POST[\'data-parentid\'];\r\n\r\n	// Don\'t need these variables\r\n	unset($_POST[\'operation\']);\r\n	unset($_POST[\'data-myid\']);\r\n	unset($_POST[\'data-parentid\']);\r\n\r\n	if ($_POST[\'tag\']!=\'text\' && isset($_POST[\'text\']) && strlen($_POST[\'text\'])>0)\r\n	{\r\n		$new_text = array();\r\n		$new_text[\'tag\']=\'text\';\r\n		$new_text[\'text\']=$_POST[\'text\'];\r\n	\r\n		$qry = \'INSERT INTO \'.$table_dynamic.\' (content,parent,my_id,position) VALUES(?,?,?,10000)\';\r\n		$params = array(json_encode(array_filter($new_text)),$myid,md5(uniqid(rand(), TRUE)));\r\n		$result = query_execute($qry, $params);\r\n\r\n		unset($_POST[\'text\']);\r\n	}\r\n\r\n	$qry = \'INSERT INTO \'.$table_dynamic.\' (content,parent,my_id,position) VALUES(?,?,?,10000)\';\r\n	$params = array(json_encode(array_filter($_POST)),$parentid,$myid);\r\n	\r\n	// Execute the query with the query and parameters\r\n	$result = query_execute($qry, $params);\r\n	// Return a new unique ID for a new element\r\n	die(md5(uniqid(rand(), TRUE)));\r\n}\r\nelse if (isset($_POST[\'operation\']) && $_POST[\'operation\']==\'dbtoggle\')\r\n{\r\n	$content = getContentArray($_POST[\'myid\']);\r\n	if (!isset($content[\'class\'])) $content[\'class\'] = $_POST[\'toggle_class\'];\r\n	else if (stripos($content[\'class\'],$_POST[\'toggle_class\'])!==false)\r\n	{\r\n		$content[\'class\'] = trim(str_ireplace($_POST[\'toggle_class\'],\'\',$content[\'class\']));\r\n	}\r\n	else $content[\'class\'] .= \' \'.$_POST[\'toggle_class\'];\r\n\r\n	// Don\'t need these variables\r\n	unset($content[\'operation\']);\r\n	unset($content[\'data-myid\']);\r\n	unset($content[\'data-parentid\']);\r\n\r\n	// MySQL query with LIMIT to improve performance\r\n	$qry = \'UPDATE \'.$table_dynamic.\' SET content=? WHERE my_id=?\';\r\n	$params = array(json_encode(array_filter($content)),$_POST[\'myid\']);\r\n	$result = query_execute($qry, $params);\r\n\r\n	die($_POST[\'myid\']);\r\n}\r\nelse if (isset($_POST[\'operation\']) && $_POST[\'operation\']==\'dbradio\')\r\n{\r\n	$content = getContentArray($_POST[\'myid\']);\r\n	if (!isset($content[\'class\'])) $content[\'class\'] = $_POST[\'toggle_class\'];\r\n	else if (stripos($content[\'class\'],$_POST[\'toggle_class\'])!==false)\r\n	{\r\n		$content[\'class\'] = trim(str_ireplace($_POST[\'toggle_class\'],\'\',$content[\'class\']));\r\n	}\r\n	else\r\n	{\r\n		foreach($arr_bootstrap as $row)\r\n		{\r\n			if (stripos($row[\'class\'],$_POST[\'toggle_class\'])!==false)\r\n			{\r\n				$all = explode(\',\',$row[\'class\']);\r\n				foreach($all as $indiv)\r\n				{\r\n					$content[\'class\'] = trim(str_ireplace($indiv,\'\',$content[\'class\']));\r\n				}\r\n			}\r\n		}\r\n		$content[\'class\'] .= \' \'.$_POST[\'toggle_class\'];\r\n	}\r\n\r\n	// Don\'t need these variables\r\n	unset($content[\'operation\']);\r\n	unset($content[\'data-myid\']);\r\n	unset($content[\'data-parentid\']);\r\n\r\n	// MySQL query with LIMIT to improve performance\r\n	$qry = \'UPDATE \'.$table_dynamic.\' SET content=? WHERE my_id=?\';\r\n	$params = array(json_encode(array_filter($content)),$_POST[\'myid\']);\r\n	$result = query_execute($qry, $params);\r\n\r\n	die($_POST[\'myid\']);\r\n}\r\nelse if (isset($_POST[\'operation\']) && $_POST[\'operation\']==\'dbupdate\')\r\n{\r\n	// Remove these from the content array\r\n	$myid = $_POST[\'data-myid\'];\r\n	$parentid = $_POST[\'data-parentid\'];\r\n\r\n	// Don\'t need these variables\r\n	unset($_POST[\'operation\']);\r\n	unset($_POST[\'data-myid\']);\r\n	unset($_POST[\'data-parentid\']);\r\n\r\n	// MySQL query with LIMIT to improve performance\r\n	$qry = \'UPDATE \'.$table_dynamic.\' SET content=? WHERE my_id=?\';\r\n	\r\n	// Pass a variable via GET or POST\r\n	$params = array(json_encode(array_filter($_POST)),$myid);\r\n	\r\n	// Execute the query with the query and parameters\r\n	$result = query_execute($qry, $params);\r\n	die(\'saved\');\r\n}\r\nelse if (isset($_POST[\'operation\']) && $_POST[\'operation\']==\'testnest\')\r\n{\r\n	if (isAllowedDrop($_POST[\'myid\'],$_POST[\'droppedOnID\'])) die(\'true\');\r\n	else die(\'false\');\r\n}\r\nelse if (isset($_POST[\'operation\']) && $_POST[\'operation\']==\'testnest2\')\r\n{\r\n	$content = getContentArray($_POST[\'droppedOnParentID\']);\r\n\r\n	if (!isAllowedDrop($_POST[\'myid\'],$_POST[\'droppedOnID\'])\r\n		&& isAllowedDrop($_POST[\'myid\'],$_POST[\'droppedOnParentID\'])\r\n		&& $_POST[\'myparentid\'] == $content[\'data-parentid\']) die(\'true\');\r\n	else die(\'false\');\r\n}\r\nelse if (isset($_POST[\'operation\']) && $_POST[\'operation\']==\'reorder\')\r\n{\r\n	$pos1 = $_POST[\'droppedOnPosition\'];\r\n	$pos2 = $_POST[\'myposition\'];\r\n	if (isset($_POST[\'parentpos\']))\r\n	{\r\n		$result = query(\'SELECT position FROM \'.$table_dynamic.\' WHERE my_id=? LIMIT 1\', array($_POST[\'droppedOnParentID\']));\r\n		\r\n		if ($result) $pos1 = $result[0][\'position\'];\r\n	}\r\n\r\n	// If the brother is beside, just change places\r\n	if (abs($pos2-$pos1)==1)\r\n	{\r\n		// Update the position of the second element\r\n		$qry = \'UPDATE \'.$table_dynamic.\' SET position=? WHERE my_id=?\';\r\n		$params = array($pos2,$_POST[\'droppedOnID\']);\r\n		query_execute($qry, $params);\r\n	}\r\n\r\n	if (($pos2-$pos1)<0) $pos1+=.5;\r\n	else $pos1-=.5;\r\n\r\n	// Update the position of the first element\r\n	$qry = \'UPDATE \'.$table_dynamic.\' SET position=? WHERE my_id=?\';\r\n	$params = array($pos1,$_POST[\'myid\']);\r\n	query_execute($qry, $params);\r\n}\r\nelse if (isset($_POST[\'operation\']) && $_POST[\'operation\']==\'parentswap\')\r\n{\r\n	//die(print_array($_POST));\r\n	if (isAllowedDrop($_POST[\'myid\'],$_POST[\'droppedOnID\']))\r\n	{\r\n		$content = getContentArray($_POST[\'myid\']);\r\n		if (isset($content[\'data-parentid\'])) unset($content[\'data-parentid\']);\r\n		if (isset($content[\'data-myid\'])) unset($content[\'data-myid\']);\r\n\r\n		// Update the parent of the element\r\n		$qry = \'UPDATE \'.$table_dynamic.\' SET parent=?,position=?,content=? WHERE my_id=?\';\r\n		$params = array($_POST[\'droppedOnID\'],.5,json_encode($content),$_POST[\'myid\']);\r\n		query_execute($qry, $params);\r\n	}\r\n	else if (isAllowedDrop($_POST[\'myid\'],$_POST[\'droppedOnParentID\']))\r\n	{\r\n		$content = getContentArray($_POST[\'myid\']);\r\n		if (isset($content[\'data-parentid\'])) unset($content[\'data-parentid\']);\r\n		if (isset($content[\'data-myid\'])) unset($content[\'data-myid\']);\r\n\r\n		// Update the parent of the element\r\n		$qry = \'UPDATE \'.$table_dynamic.\' SET parent=?,position=?,content=? WHERE my_id=?\';\r\n		$params = array($_POST[\'droppedOnParentID\'],($_POST[\'droppedOnPosition\']-.5),json_encode($content),$_POST[\'myid\']);\r\n		query_execute($qry, $params);\r\n	}\r\n	die();\r\n}\r\nelse if (isset($_POST[\'operation\']) && $_POST[\'operation\']==\'stats\' && constant_test(\'SETTING_PHP_VARIABLES\',\'show\') && isGroupMember(1))\r\n{\r\n	// Output the SQL count and page load time\r\n	if (function_exists(\'PtimerStop\')) die(outputSQLCounter().PtimerStop());\r\n}\r\nelse if (isset($_POST[\'operation\']) && $_POST[\'operation\']==\'quick_parent\')\r\n{\r\n	$content = getContentArray($_POST[\'myid\']);\r\n	$content2 = getContentArray($content[\'data-parentid\']);\r\n	die($content2[\'tag\']);\r\n}\r\nelse if (isset($_POST[\'operation\']) && $_POST[\'operation\']==\'convert_code\')\r\n{\r\n	$a = substr_count($_POST[\'newcode\'],\'<\');\r\n	$b = substr_count($_POST[\'newcode\'],\'>\');\r\n	if ($a!=$b) die(\'HTML is invalid\');\r\n\r\n	$doc = new DOMDocument();\r\n	$doc->loadHTML($_POST[\'newcode\']);\r\n	\r\n	$all_elements = array();\r\n	function findThem($kids, $real_parentid, $trim=true)\r\n	{\r\n		global $all_elements;\r\n		$temp_position = 0;\r\n		$return_text = \'\';\r\n		foreach ($kids->childNodes as $node)\r\n		{\r\n			$element = array();\r\n	\r\n			// Determine if the node doesn\'t have any children so it should have text (hopefully)\r\n			$node_text = trim($node->nodeValue);\r\n			if (!$node->hasChildNodes() && strlen($node_text)>0)\r\n			{$trim=false;\r\n				if ($trim)\r\n				{\r\n					//$element[\'text\'] = str_replace(array(NEWLINE,\'	\'),array(\'\',\'\'),trim($node->nodeValue));\r\n					$element[\'text\'] = trim($node->nodeValue);\r\n				}\r\n				else $element[\'text\'] = $node->nodeValue;\r\n			}\r\n	\r\n			// Gather all the attributes\r\n			if ($node->hasAttributes())\r\n			{\r\n				// Gather all the hard attributes\r\n				foreach ($node->attributes as $attr)\r\n				{\r\n					$name = $attr->nodeName;\r\n					$value = $attr->nodeValue;\r\n					$element[$name] = $value;\r\n				}\r\n			}\r\n	\r\n			//if ($node->hasAttributes() || trim($node->nodeValue)>0 || $node->hasChildNodes() || isset($element[\'text\']))\r\n			if (strlen($node_text)>0 || $node->hasAttributes() || $node->nodeName==\'br\')\r\n			{\r\n				// Set the required attributes\r\n				$element[\'tag\'] = trim($node->nodeName,\'#\');\r\n		\r\n				// If MYID doesn\'t exist, create one\r\n				if (!$node->hasAttributes() || !$node->hasAttribute(\'data-myid\')) $element[\'data-myid\'] = md5(uniqid(rand(), TRUE));\r\n		\r\n				// Ensure the PARENTID is correct\r\n				//if (!$node->hasAttribute(\'data-parentid\')) $element[\'data-parentid\'] = $real_parentid;\r\n				$element[\'data-parentid\'] = $real_parentid;\r\n	\r\n				// Ensure the POSITION is correct\r\n				//if (!$node->hasAttribute(\'data-position\')) $element[\'data-position\'] = $temp_position;\r\n				$element[\'data-position\'] = $temp_position;\r\n				$temp_position+=1;\r\n			}\r\n	\r\n			// If the node has children (including just text), get them\r\n			if ($node->hasChildNodes())\r\n			{\r\n				if (!isset($element[\'data-myid\'])) $my_temp_id = $real_parentid;\r\n				else $my_temp_id = $element[\'data-myid\'];\r\n	\r\n				// Grab the text\r\n				//$text_return = \r\n				findThem($node, $my_temp_id,($element[\'tag\']!=\'pre\' && $element[\'tag\']!=\'textarea\'));\r\n				//if (strlen(trim($text_return))>0) $element[\'text\']=$text_return;\r\n			}\r\n			// If the array has stuff in it, save it\r\n			if (!empty($element)) array_push($all_elements, $element);\r\n		}\r\n			if ($return_text != \'\') return $return_text;\r\n	}\r\n	\r\n	$root = $doc->documentElement;\r\n	findThem($root->firstChild,\'root\');\r\n	\r\n	query_execute(\"DELETE FROM $table_dynamic\", array());\r\n	\r\n	foreach ($all_elements as $row)\r\n	{\r\n		// Remove these from the content array\r\n		$myid = $row[\'data-myid\'];\r\n		$parentid = $row[\'data-parentid\'];\r\n		$position = $row[\'data-position\'];\r\n	\r\n		// Don\'t need these variables\r\n		unset($row[\'operation\']);\r\n		unset($row[\'data-myid\']);\r\n		unset($row[\'data-parentid\']);\r\n		unset($row[\'data-position\']);\r\n	\r\n		$qry = \'INSERT INTO \'.$table_dynamic.\' (content,parent,my_id,position) VALUES(?,?,?,?)\';\r\n		$params = array(json_encode(array_filter($row)),$parentid,$myid,$position);\r\n		\r\n		// Execute the query with the query and parameters\r\n		$result = query_execute($qry, $params);\r\n	}\r\ndie(\'HTML parsed and database updated\');\r\n}\r\nelse if (isset($_POST[\'operation\']) && $_POST[\'operation\']==\'reduce_position\')\r\n{\r\n	// Update the position of the element\r\n	$qry = \'UPDATE \'.$table_dynamic.\' SET position=position-1.5 WHERE my_id=?\';\r\n	$params = array($_POST[\'myid\']);\r\n	$result = query_execute($qry, $params);\r\ndie($result);\r\n}\r\nelse if (isset($_POST[\'operation\']) && $_POST[\'operation\']==\'advance_position\')\r\n{\r\n	// Update the position of the element\r\n	$qry = \'UPDATE \'.$table_dynamic.\' SET position=position+1.5 WHERE my_id=?\';\r\n	$params = array($_POST[\'myid\']);\r\n	query_execute($qry, $params);\r\n}\r\nelse\r\n{\r\n	//die(\'<p class=\"dynamic-markup\">***** End of Dynamic Page *****</p>\');\r\n	die(\'\');\r\n}\r\n}','Dynamic HTML','',''),(330,'page_dynamic_demo','dyn_demo','2012-07-09 08:06:30',2,10,'','Dynamic Demo','',''),(228,'page_not_found','<table width=\"100%\" height=\"100%\">\r\n<tr>\r\n<td align=\"center\" valign=\"middle\" style=\"padding: 50px;\">\r\nThe page you seek is <?=random_phrase()?>unavailable.<br/>\r\nPlease travel back to the last place you <a href=\"javascript: history.go(-1)\">visited</a> or return to the <a href=\"/\">home page</a>.\r\n</td>\r\n</tr>\r\n</table>','2012-06-17 05:58:05',2,0,'','Page Not Found','',''),(230,'misc_php_info','die(phpinfo());','2012-05-05 18:34:09',2,1,'','','',''),(231,'toolkit_update_sitemap','if (file_exists(\'sitemap.xml\')) $text = file_get_contents(\'sitemap.xml\');\r\nelse $text = \'\';\r\n\r\npreg_match_all(\'/\'.RECORDED_DOMAIN.\'\\/([^<]+)<\\/loc>/\',$text,$matches);\r\n\r\n$current_site_pages = array();\r\n\r\nif (isset($matches[1]))\r\n{\r\n	foreach($matches[1] as $match) $current_site_pages[] = $match;\r\n}\r\n\r\n// Show all the methods not in the Trash\r\n$arr = db_method_list();\r\n$list = \'\';\r\n$counter = 0;\r\n\r\nforeach($arr as $element)\r\n{\r\n	$name = $element[\'name\'];\r\n	$id = $element[\'id\'];\r\n	//$mysql_time = date(\'Y-m-d H:i:s\', $element[\'modified\']);\r\n	$mod = strtotime($element[\'modified\']);\r\n\r\n	$qry = \"SELECT id FROM access_list WHERE method_id=? AND group_id=? LIMIT 1\";\r\n	$params = array($id,3);\r\n	$result = query($qry, $params);\r\n\r\n	if (count($result)==1)\r\n	{\r\n		if (in_array($name, $current_site_pages))$checked = \'checked=\"checked\"\';\r\n		else $checked=\'\';\r\n		$list .= \'<input type=\"checkbox\" name=\"\'.$name.\'\" value=\"\'.$mod.\'\" \'.$checked.\' /> \'.$name.br();\r\n		$counter+=1;\r\n	}\r\n}\r\n\r\n//$output .= br().br().\"Publically Available Pages (\".$counter.\"):\";\r\n\r\n$output .= <<< OUTPUT\r\n\r\n<div class=\"fluid-row\">\r\n\r\n<div class=\"span4\">\r\n</div>\r\n\r\n<div class=\"span4\">\r\n<form class=\"well\" onsubmit=\"return validateForm()\" method=\"post\">\r\n<h3>Update Sitemap.xml</h3><br />\r\n{$list}\r\n<div class=\"clear\"></div><br />\r\n<button type=\"submit\" class=\"btn\" title=\"Click once to update sitemap\">Update</button>\r\n</form>\r\n</div>\r\n\r\n<div class=\"span4\">\r\n</div>\r\n\r\n</div>\r\n\r\nOUTPUT;\r\n\r\n//Need this','2012-07-17 22:50:26',2,1,'// Add all the pages\r\n$arr_page = array();\r\n$arr_page[] = array(SITE_FULL,false);\r\n\r\n// Loop through all the checkboxes\r\nif (isset($_POST))\r\n{\r\n	foreach($_POST as $key=>$val)\r\n	{\r\n		if ($key!=\"submit\") array_push($arr_page, array(SITE_FULL.\'/\'.$key, $val));\r\n	}\r\n}\r\n\r\n// Set the current date and frequency\r\n$change_freq = array(\'always\',\'hourly\',\'daily\',\'weekly\',\'monthly\',\'yearly\',\'never\');\r\n\r\n// Output the sitemap\r\n$output2 = <<< OUTPUT\r\n<?xml version=\"1.0\" encoding=\"UTF-8\"?>\r\n<urlset\r\n      xmlns=\"http://www.sitemaps.org/schemas/sitemap/0.9\"\r\n      xmlns:xsi=\"http://www.w3.org/2001/XMLSchema-instance\"\r\n      xsi:schemaLocation=\"http://www.sitemaps.org/schemas/sitemap/0.9\r\n            http://www.sitemaps.org/schemas/sitemap/0.9/sitemap.xsd\">\r\nOUTPUT;\r\n\r\nforeach ($arr_page as $page)\r\n{\r\n//$date_updated = date_format(date_create(), \'Y-m-d\\TH:i:s\').\'Z\';\r\n//$date_updated = date_format(date_create($page[1]), \'Y-m-d\\TH:i:s\').\'Z\';\r\nif (!$page[1]) $date_updated = date_format(date_create(), \'Y-m-d\\TH:i:s\').\'Z\';\r\nelse $date_updated = date(\'Y-m-d\\TH:i:s\',(int)$page[1]).\'Z\';\r\n\r\n$output2 .= NEWLINE;\r\n$output2 .= <<< OUTPUT\r\n<url>\r\n  <loc>{$page[0]}</loc>\r\n  <lastmod>{$date_updated}</lastmod>\r\n  <changefreq>{$change_freq[2]}</changefreq>\r\n</url>\r\nOUTPUT;\r\n}\r\n\r\n$output2 .= NEWLINE.\"</urlset>\";\r\n\r\n//$output .= \'<pre>\';\r\n//$output .= htmlentities($output2, ENT_QUOTES, \"UTF-8\");\r\n//$output .= \'</pre>\';\r\n\r\n$result = file_put_contents(\'sitemap.xml\',$output2);\r\n\r\nif ($result) msg_user(\"Sitemap.xml Update\",\"Sitemap was updated successfully for \".count($arr_page).\" page(s).\");\r\nelse msg_user(\"Sitemap.xml Update\",\"Sitemap was NOT updated successfully.\");','Sitemap Generator','',''),(272,'toolkit_security_manager','// ********************************************* User Options *********************************************\r\nif (isset($_GET[\'user_info\']))\r\n{\r\n	$return=a(\'Back to security manager\',\'toolkit_security_manager\').br().br();\r\n\r\n	$qry = \'SELECT username,\r\n	password,\r\n	email,\r\n	first_login,\r\n	last_login,\r\n	total_login,\r\n	modified,\r\n	modifier,\r\n	created,\r\n	creator FROM access_user WHERE id=? LIMIT 1\';\r\n	$params = array($_GET[\'user_info\']);\r\n	$result = query($qry, $params);\r\n\r\n	function pretty_date($val)\r\n	{\r\n		if ($val==\'0000-00-00 00:00:00\') return \'Never logged in\';\r\n		$cdate = new DateTime($val);\r\n		date_timezone_set($cdate, timezone_open(\'US/Eastern\'));\r\n		return date_format($cdate, \'M d, Y \\a\\t h:i A\');\r\n	}\r\n	function get_user($val)\r\n	{\r\n		$qry = \"SELECT username FROM access_user WHERE id=? LIMIT 1\";\r\n		$params = array($val);\r\n		$result = query($qry, $params);\r\n		if (count($result)==1) return $result[0][\'username\'];\r\n		else return \'Unknown user\';\r\n	}\r\n\r\n	if (isset($_GET[\'user_delete\']))\r\n	{\r\n		$return .= <<< OUTPUT\r\n<div class=\"fluid-row\">\r\n\r\n<div class=\"span4\">\r\n</div>\r\n\r\n<div class=\"span4\">\r\n<form class=\"well\" onsubmit=\"return validateForm()\" method=\"post\">\r\n		<h3>Delete User</h3>\r\n		<label>Username:</label><input class=\"span12\" type=\"text\" id=\"user\" name=\"user\" readonly=\"readonly\" value=\"{$_GET[\'user_name\']}\" />\r\n		<input class=\"span12\" type=\"hidden\" id=\"user_id\" name=\"user_id\" value=\"{$_GET[\'user_delete\']}\" />\r\n<div class=\"clear\"></div>\r\n<button type=\"submit\" class=\"btn\" title=\"Click once to delete\">Delete</button>\r\n</form>\r\n</div>\r\n\r\n<div class=\"span4\">\r\n</div>\r\n\r\n</div>\r\n<div class=\"clear\"></div>\r\nOUTPUT;\r\n	}\r\n	else if (isset($_GET[\'remove_member\']))\r\n	{\r\n		$qry = \"DELETE FROM access_membership WHERE group_id=? AND user_id=?\";\r\n		$params = array($_GET[\'remove_member\'],$_GET[\'user_info\']);\r\n		$result2 = query_execute($qry, $params);\r\n		if ($result2) msg_user(\"User Removed from Group\",$result[0][\'username\'].\" is no longer a member of the group, \".$_GET[\'member_name\'],\"index.php?toolkit_security_manager&user_info=\".$_GET[\'user_info\']);\r\n		else msg_user(\"User Problem\",$result[0][\'username\'].\" could not be removed as a member of the group, \".$_GET[\'member_name\'],\"index.php?toolkit_security_manager&user_info=\".$_GET[\'user_info\']);\r\n	}\r\n	else if (isset($_GET[\'add_member\']))\r\n	{\r\n		$qry = \"INSERT INTO access_membership (user_id,group_id,modified,modifier) VALUES(?,?,?,CURRENT_TIMESTAMP)\";\r\n		$params = array($_GET[\'user_info\'], $_GET[\'add_member\'], $_SESSION[\'id\']);\r\n		$result2 = query_execute($qry, $params);\r\n		if ($result2) msg_user(\"User Added to Group\",$result[0][\'username\'].\" is now a member of the group, \".$_GET[\'member_name\'],\"index.php?toolkit_security_manager&user_info=\".$_GET[\'user_info\']);\r\n		else msg_user(\"User Problem\",$result[0][\'username\'].\" could not be added as a member of the group, \".$_GET[\'member_name\'],\"index.php?toolkit_security_manager&user_info=\".$_GET[\'user_info\']);\r\n	}\r\n	else if (isset($_GET[\'user_edit\']))\r\n	{\r\n		$return .= <<< OUTPUT\r\n<div class=\"fluid-row\">\r\n\r\n<div class=\"span4\">\r\n</div>\r\n\r\n<div class=\"span4\">\r\n<form class=\"well\" onsubmit=\"return validateForm()\" method=\"post\">\r\n		<h3>Edit User</h3>\r\n		<label>Username:</label><input class=\"span12\" type=\"text\" id=\"user\" name=\"user\" value=\"{$result[0][\'username\']}\" />\r\n		<label>Old Password:</label><input class=\"span12\" type=\"password\" id=\"pass\" name=\"pass\" readonly=\"readonly\" value=\"{$result[0][\'password\']}\" />\r\n		<label>New Password:</label><input class=\"span12\" type=\"password\" id=\"pass_new\" name=\"pass_new\" value=\"{$result[0][\'password\']}\" />\r\n		<label>Email:</label><input class=\"span12\" type=\"text\" id=\"email\" name=\"email\" value=\"{$result[0][\'email\']}\" />\r\n<div class=\"clear\"></div>\r\n<button type=\"submit\" class=\"btn\" title=\"Click once to update\">Update</button>\r\n</form>\r\n</div>\r\n\r\n<div class=\"span4\">\r\n</div>\r\n\r\n</div>\r\n<div class=\"clear\"></div>\r\n\r\n		<script>document.getElementById(\"user\").focus();</script>\r\nOUTPUT;\r\n	}\r\n\r\n	$return.=\'<b>Manage User:</b> \'.$result[0][\'username\'].br();\r\n	$return.=\'<b>Email:</b> \'.$result[0][\'email\'].br().br();\r\n\r\n	$return.=a(img(\'Edit User\',\'/img/silk/user_edit.png\'),\'toolkit_security_manager&user_info=\'.$_GET[\'user_info\'].\'&user_edit=\'.$_GET[\'user_info\'].\'&user_name=\'.$result[0][\'username\']).\' \';\r\n	$return.=a(img(\'User Information\',\'/img/silk/user_comment.png\'),\'toolkit_security_manager&user_info=\'.$_GET[\'user_info\'].\'&user_comment=\'.$_GET[\'user_info\'].\'&user_name=\'.$result[0][\'username\']).\' \';\r\n	$return.=a(img(\'Delete User\',\'/img/silk/user_delete.png\'),\'toolkit_security_manager&user_info=\'.$_GET[\'user_info\'].\'&user_delete=\'.$_GET[\'user_info\'].\'&user_name=\'.$result[0][\'username\']).\' \';\r\n	$return.=br();\r\n\r\n	if (isset($_GET[\'user_comment\']))\r\n	{\r\n		$return .= br();\r\n		$return.=\'<b>Total Logins:</b> \'.$result[0][\'total_login\'].br();\r\n	\r\n		$return.=\'<b>First Login:</b> \'.pretty_date($result[0][\'first_login\']).br();\r\n		$return.=\'<b>Last Login:</b> \'.pretty_date($result[0][\'last_login\']).br();\r\n		$return.=\'<b>Modified:</b> \'.pretty_date($result[0][\'modified\']).br();\r\n		$return.=\'<b>Modifier:</b> \'.get_user($result[0][\'modifier\']).br();\r\n		$return.=\'<b>Created:</b> \'.pretty_date($result[0][\'created\']).br();\r\n		$return.=\'<b>Creator:</b> \'.get_user($result[0][\'creator\']).br();\r\n	}\r\n\r\n	$qry = \"SELECT access_group.name, access_group.id, access_group.description FROM access_group\r\n		INNER JOIN access_membership WHERE access_membership.group_id=access_group.id AND\r\n		access_membership.user_id=?\";\r\n\r\n	$params = array($_GET[\'user_info\']);\r\n	$result = query($qry, $params);\r\n\r\n	$is_Admin = false;\r\n	$access_list = array();\r\n\r\n	$member_groups=br().\'<b>This user is a member of the following groups (\'.count($result).\'):</b>\'.br();\r\n	if (count($result)==0) $member_groups .= \'None of them\'.br();\r\n	$result3 = $result;\r\n	foreach($result as $row)\r\n	{\r\n		$member_groups.=a(img(\'Remove from Group\',\'/img/silk/delete.png\'),\'toolkit_security_manager&user_info=\'.$_GET[\'user_info\'].\'&remove_member=\'.$row[\'id\'].\'&member_name=\'.$row[\'name\']).\' \';\r\n		$member_groups.=a(img(\'Manage Group\',\'/img/silk/group_key.png\'),\'toolkit_security_manager&access_group_manage=\'.$row[\'id\'].\'&name=\'.$row[\'name\']).\' \';\r\n		$member_groups.=$row[\'name\'].\' - \'.$row[\'description\'].br();\r\n\r\n		if ($row[\'name\']==\'Administrators\') $is_Admin=true;\r\n		else if (!$is_Admin)\r\n		{\r\n			$qry = \"SELECT php_methods.name FROM php_methods\r\n			INNER JOIN access_list WHERE access_list.method_id = php_methods.id AND \r\n			access_list.group_id=?\";\r\n		\r\n			$params = array($row[\'id\']);\r\n			$result2 = query($qry, $params);\r\n\r\n			foreach($result2 as $row2) $access_list[$row2[\'name\']]=$row[\'name\'];\r\n		}\r\n	}\r\n\r\n	$member_pages = br().\'<b>This user has access to the following pages (\'.count($access_list).\'):</b>\'.br();\r\n	if ($is_Admin) $member_pages .= \'Everything\'.br();\r\n	else if (count($access_list)==0) $member_pages .= \'None of them\'.br();\r\n	else\r\n	{\r\n		foreach($access_list as $key=>$val) $member_pages.=$key.\' (\'.$val.\')\'.br();\r\n	}\r\n\r\n	$result = query(\"SELECT name, id, description FROM access_group\", array());\r\n	$available_groups = br().\'<b>Available groups (\'.(count($result)-count($result3)-2).\'):</b>\'.br();\r\n	foreach ($result as $row)\r\n	{\r\n		$found = false;\r\n		foreach($result3 as $row2)\r\n		{\r\n			if ($row2[\'id\']==$row[\'id\']) $found = true;\r\n		}\r\n		if (!$found && $row[\'name\']!=\'Authenticated\' && $row[\'name\']!=\'Anonymous\')\r\n		{\r\n			$available_groups.=a(img(\'Add to Group\',\'/img/silk/add.png\'),\'toolkit_security_manager&user_info=\'.$_GET[\'user_info\'].\'&add_member=\'.$row[\'id\'].\'&member_name=\'.$row[\'name\']).\' \';\r\n			$available_groups.=a(img(\'Manage Group\',\'/img/silk/group_key.png\'),\'toolkit_security_manager&access_group_manage=\'.$row[\'id\'].\'&name=\'.$row[\'name\']).\' \';\r\n			$available_groups.=$row[\'name\'].\' - \'.$row[\'description\'].br();\r\n		}\r\n	}\r\n\r\n\r\n	$output.=$return;\r\n	$output.=$member_pages;\r\n	$output.=$member_groups;\r\n	$output.=$available_groups;\r\n	return;\r\n}\r\n// ********************************************* Group Options *********************************************\r\nelse if (isset($_GET[\'access_group_manage\']) && $_GET[\'access_group_manage\']>0)\r\n{\r\n	$return=a(\'Back to security manager\',\'toolkit_security_manager\').br().br();\r\n	// If the user has been added and passed via GET\r\n	if (isset($_GET[\'adduser\']) && $_GET[\'adduser\']>0)\r\n	{\r\n		// Determine if the user is already a member of the group\r\n		$qry = \"SELECT access_membership.id, access_user.username FROM access_membership\r\n				INNER JOIN access_user WHERE group_id=? AND user_id=? AND\r\n				user_id=access_user.id LIMIT 1\";\r\n		$params = array($_GET[\'access_group_manage\'],$_GET[\'adduser\']);\r\n		$result = query($qry, $params);\r\n\r\n		$qry = \"SELECT username FROM access_user WHERE id=? LIMIT 1\";\r\n		$params = array($_GET[\'adduser\']);\r\n		$result2 = query($qry, $params);\r\n		if (count($result2)==1) $user = $result2[0][\'username\'];\r\n		else $user = \'User\';\r\n\r\n		// If the user is already a member of the group\r\n		if (count($result)==1)\r\n		{\r\n			$user = $result[0][\'username\'];\r\n			msg_user(\"No Change\",\"$user is already a member of the group, \",\"index.php?toolkit_security_manager&access_group_manage=\".$_GET[\'access_group_manage\']);\r\n			return;\r\n		}\r\n		//Else the user is not a member of the group so add\r\n		else\r\n		{\r\n			$qry = \"INSERT INTO access_membership (user_id,group_id,modified,modifier)\r\n				VALUES(?,?,CURRENT_TIMESTAMP,?)\";\r\n			$params = array($_GET[\'adduser\'],$_GET[\'access_group_manage\'],$_SESSION[\'id\']);\r\n			$result = query_execute($qry, $params);\r\n			if ($result) msg_user(\"User Added to Group\",\"$user is now a member of the group\",\"index.php?toolkit_security_manager&access_group_manage=\".$_GET[\'access_group_manage\']);\r\n			else msg_user(\"User Problem\",\"$user could not be made a member of the group\",\"index.php?toolkit_security_manager&access_group_manage=\".$_GET[\'access_group_manage\']);\r\n			return;\r\n		}\r\n	}\r\n	// If the user has been removed and passed via GET\r\n	else if (isset($_GET[\'deluser\']) && $_GET[\'deluser\']>0)\r\n	{\r\n		// Determine if the user is already a member of the group\r\n		$qry = \"SELECT access_membership.id, access_user.username FROM access_membership\r\n				INNER JOIN access_user WHERE group_id=? AND user_id=? AND\r\n				user_id=access_user.id LIMIT 1\";\r\n		$params = array($_GET[\'access_group_manage\'],$_GET[\'deluser\']);\r\n		$result = query($qry, $params);\r\n\r\n		$qry = \"SELECT username FROM access_user WHERE id=? LIMIT 1\";\r\n		$params = array($_GET[\'deluser\']);\r\n		$result2 = query($qry, $params);\r\n		if (count($result2)==1) $user = $result2[0][\'username\'];\r\n		else $user = \'User\';\r\n\r\n		// If the user is already a member of the group\r\n		if (count($result)!=1)\r\n		{\r\n			msg_user(\"No Change\",\"$user is not a member of the group\",\"index.php?toolkit_security_manager&access_group_manage=\".$_GET[\'access_group_manage\']);\r\n			//return;\r\n		}\r\n		//Else the user is not a member of the group so add\r\n		else\r\n		{\r\n			$qry = \"DELETE FROM access_membership WHERE group_id=? AND user_id=?\";\r\n			$params = array($_GET[\'access_group_manage\'],$_GET[\'deluser\']);\r\n			$result = query_execute($qry, $params);\r\n			if ($result) msg_user(\"User Removed from Group\",\"$user is no longer a member of the group\",\"index.php?toolkit_security_manager&access_group_manage=\".$_GET[\'access_group_manage\']);\r\n			else msg_user(\"User Problem\",\"$user could not be removed as a member of the group\",\"index.php?toolkit_security_manager&access_group_manage=\".$_GET[\'access_group_manage\']);\r\n			//return;\r\n		}\r\n	}\r\n	// If the user has been added and passed via GET\r\n	else if (isset($_GET[\'addmethod\']) && $_GET[\'addmethod\']>0)\r\n	{\r\n		$qry = \"SELECT name FROM access_group WHERE id=? LIMIT 1\";\r\n		$params = array($_GET[\'access_group_manage\']);\r\n		$result = query($qry, $params);\r\n		if (count($result)==1) $group_name = $result[0][\'name\'];\r\n		else $group_name = \'Group\';\r\n\r\n		$qry = \"SELECT id FROM access_list WHERE method_id=? AND group_id=?\";\r\n		$params = array($_GET[\'addmethod\'],$_GET[\'access_group_manage\']);\r\n		$result = query($qry, $params);\r\n		if (count($result)>0)\r\n		{\r\n			msg_user(\"Access Change Skipped\",\"$group_name already has access to: \".$_GET[\'name\'],\"index.php?toolkit_security_manager&access_group_manage=\".$_GET[\'access_group_manage\']);\r\n			//return;\r\n		}\r\n		else\r\n		{\r\n			$qry = \"INSERT INTO access_list(method_id,group_id,modified,modifier) VALUES(?,?,CURRENT_TIMESTAMP,?)\";\r\n			$params = array($_GET[\'addmethod\'],$_GET[\'access_group_manage\'],$_SESSION[\'id\']);\r\n			$result = query_execute($qry, $params);\r\n			if ($result) msg_user(\"Access Granted\",\"$group_name can now access: \".$_GET[\'name\'],\"index.php?toolkit_security_manager&access_group_manage=\".$_GET[\'access_group_manage\']);\r\n			else msg_user(\"Access Problem\",\"$group_name could not be added to the access list for: \".$_GET[\'name\'],\"index.php?toolkit_security_manager&access_group_manage=\".$_GET[\'access_group_manage\']);\r\n			//return;\r\n		}\r\n	}\r\n	// If the user has been added and passed via GET\r\n	else if (isset($_GET[\'delmethod\']) && $_GET[\'delmethod\']>0)\r\n	{\r\n		$qry = \"SELECT name FROM access_group WHERE id=? LIMIT 1\";\r\n		$params = array($_GET[\'access_group_manage\']);\r\n		$result = query($qry, $params);\r\n		if (count($result)==1) $group_name = $result[0][\'name\'];\r\n		else $group_name = \'Group\';\r\n\r\n		$qry = \"DELETE FROM access_list WHERE method_id=? AND group_id=? LIMIT 1\";\r\n		$params = array($_GET[\'delmethod\'],$_GET[\'access_group_manage\']);\r\n		$result = query_execute($qry, $params);\r\n		if ($result) msg_user(\"Access Removed\",\"$group_name can no longer access: \".$_GET[\'name\'],\"index.php?toolkit_security_manager&access_group_manage=\".$_GET[\'access_group_manage\']);\r\n		else msg_user(\"Access Problem\",\"$group_name could not be removed from the access list for: \".$_GET[\'name\'],\"index.php?toolkit_security_manager&access_group_manage=\".$_GET[\'access_group_manage\']);\r\n		//return;\r\n	}\r\n	// ********************************************* Group Delete *********************************************\r\n	else if (isset($_GET[\'group_delete\']))\r\n	{\r\n		$return .= <<< OUTPUT\r\n<div class=\"fluid-row\">\r\n\r\n<div class=\"span4\">\r\n</div>\r\n\r\n<div class=\"span4\">\r\n<form class=\"well\" onsubmit=\"return validateForm()\" method=\"post\">\r\n		<h3>Delete Group</h3>\r\n		<label>Group:</label><input class=\"span12\" type=\"text\" id=\"group\" name=\"group\" readonly=\"readonly\" value=\"{$_GET[\'name\']}\" />\r\n		<input type=\"hidden\" id=\"group_id\" name=\"group_id\" value=\"{$_GET[\'group_delete\']}\" />\r\n<div class=\"clear\"></div>\r\n<button type=\"submit\" class=\"btn\" title=\"Click once to delete\">Delete</button>\r\n</form>\r\n</div>\r\n\r\n<div class=\"span4\">\r\n</div>\r\n\r\n</div>\r\n<div class=\"clear\"></div>\r\n	\r\nOUTPUT;\r\n	}\r\n	else if (isset($_GET[\'group_edit\']))\r\n	{\r\n		$return .= <<< OUTPUT\r\n<div class=\"fluid-row\">\r\n\r\n<div class=\"span4\">\r\n</div>\r\n\r\n<div class=\"span4\">\r\n<form class=\"well\" onsubmit=\"return validateForm()\" method=\"post\">\r\n		<h3>Edit Group</h3>\r\n		<label>Group Name:</label><input class=\"span12\" type=\"text\" id=\"name\" name=\"name\" value=\"{$_GET[\'name\']}\" />\r\n		<label>Description:</label><input class=\"span12\" type=\"text\" id=\"description\" name=\"description\" value=\"{$_GET[\'description\']}\" />\r\n<div class=\"clear\"></div>\r\n<button type=\"submit\" class=\"btn\" title=\"Click once to update\">Update</button>\r\n</form>\r\n</div>\r\n\r\n<div class=\"span4\">\r\n</div>\r\n\r\n</div>\r\n<div class=\"clear\"></div>\r\n		\r\n		<script>document.getElementById(\"name\").focus();</script>\r\n	\r\nOUTPUT;\r\n	}\r\n\r\n	$qry = \"SELECT id,name,description FROM access_group WHERE id=?\";\r\n	$params = array($_GET[\'access_group_manage\']);\r\n	$result = query($qry, $params);\r\n\r\n	if (count($result)!=1) { header(\"Location: index.php?toolkit_security_manager&access_group_manage\"); return; }\r\n	\r\n	$group_name = $result[0][\'name\'];\r\n\r\n	$return.=\'<b>Manage Group:</b> \'.$result[0][\'name\'].br().\'<b>Description:</b> \'.$result[0][\'description\'].br();\r\n	if ($group_name != \'Administrators\' && $group_name != \'Authenticated\' && $group_name != \'Anonymous\')\r\n	{\r\n		$return .= br();\r\n		$return .= a(img(\'Edit Group\',\'/img/silk/group_edit.png\'),\'toolkit_security_manager&access_group_manage=\'.$_GET[\'access_group_manage\'].\'&group_edit=\'.$result[0][\'id\'].\'&name=\'.$result[0][\'name\'].\'&description=\'.$result[0][\'description\']).\' \';\r\n		$return .= a(img(\'Delete Group\',\'/img/silk/group_delete.png\'),\'toolkit_security_manager&access_group_manage=\'.$_GET[\'access_group_manage\'].\'&group_delete=\'.$result[0][\'id\'].\'&name=\'.$result[0][\'name\']).\' \';\r\n		$return .= br();\r\n	}\r\n\r\n	// ********************* Member Users *********************\r\n	$member_users=\'\';\r\n	// Get all member users\r\n	$qry = \"SELECT user_id, access_user.username FROM access_membership INNER JOIN access_user WHERE group_id=? AND access_membership.user_id=access_user.id ORDER BY access_user.username ASC\";\r\n	$params = array($_GET[\'access_group_manage\']);\r\n	$result = query($qry, $params);\r\n	\r\n	$member_users .=\'<br /><b>This group has the following members (\'.count($result).\'):</b>\'.br();\r\n	if (count($result)>10) $member_users .=\'<div class=\"three_col\">\';\r\n	if (count($result)>0)\r\n	{	\r\n		foreach($result as $row)\r\n		{\r\n			// Output the id and name from SQL\r\n			$member_users.=a(img(\'Remove member\',\'/img/silk/delete.png\'),\'toolkit_security_manager&access_group_manage=\'.$_GET[\'access_group_manage\'].\'&deluser=\'.$row[\'user_id\']).\' \';\r\n			$member_users.=a(img(\'Edit User\',\'/img/silk/user_edit.png\'),\'toolkit_security_manager&user_info=\'.$row[\'user_id\']).\' \';\r\n			$member_users.=$row[\'username\'].br();\r\n		}\r\n	}\r\n	else if($group_name==\'Anonymous\') $member_users .=\'Anyone without a login for this website\'.br();\r\n	else if($group_name==\'Authenticated\') $member_users .=\'Anyone with a login for this website\'.br();\r\n	else $member_users .=\'None\'.br();\r\n	if (count($result)>10) $member_users .=\'</div>\';\r\n\r\n	// ********************* Available Users *********************\r\n	$available_users=\'\';\r\n	if ($group_name!=\'Anonymous\' && $group_name!=\'Authenticated\')\r\n	{	\r\n		$qry = \"SELECT id,username FROM access_user ORDER BY username ASC\";\r\n		$params = array();\r\n		$result2 = query($qry, $params);\r\n\r\n		if ((count($result2)-count($result))!=0)\r\n		{\r\n			$available_users .=br().\'<b>Available users (\'.(count($result2)-count($result)).\'):</b>\'.br();\r\n			if ((count($result2)-count($result))>10) $available_users .=\'<div class=\"three_col\">\';\r\n		}\r\n		\r\n		foreach($result2 as $row)\r\n		{\r\n			$found = false;\r\n			foreach($result as $row2)\r\n			{\r\n				if ($row[\'username\']==$row2[\'username\']) $found = true;\r\n			}\r\n			if (!$found)\r\n			{\r\n				// Output the id and name from SQL\r\n				//$return.=\'<br />\'.$row[\'username\'].\' <a href=\"index.php?toolkit_security_manager&access_group_manage=\'.$_GET[\'access_group_manage\'].\'&adduser=\'.$row[\'id\'].\'\">add</a>\';\r\n				$available_users.=a(img(\'Add member\',\'/img/silk/add.png\'),\'toolkit_security_manager&access_group_manage=\'.$_GET[\'access_group_manage\'].\'&adduser=\'.$row[\'id\']).\' \';\r\n				$available_users.=a(img(\'Edit User\',\'/img/silk/user_edit.png\'),\'toolkit_security_manager&user_info=\'.$row[\'id\']).\' \';\r\n				$available_users.=$row[\'username\'].br();\r\n			}\r\n		}\r\n		if ((count($result2)-count($result))>10) $available_users .=\'</div>\';\r\n	}\r\n\r\n	// ********************* Member Pages *********************\r\n	$member_pages=\'\';\r\n	$qry = \"SELECT php_methods.name, php_methods.id FROM php_methods\r\n	INNER JOIN access_list WHERE access_list.method_id = php_methods.id AND \r\n	access_list.group_id=?\";\r\n	$params = array($_GET[\'access_group_manage\']);\r\n	$result2 = query($qry, $params);\r\n\r\n	if ($_GET[\'access_group_manage\']==1)\r\n	{\r\n		$member_pages .= br().\'<b>This group has access to the following pages:</b>\'.br();\r\n		if (count($result2)>10) $member_pages .=\'<div class=\"three_col\">\';\r\n		$member_pages .= \'All of them\'.br();\r\n	}\r\n	else if (count($result2)==0)\r\n	{\r\n		$member_pages .= br().\'<b>This group has access to the following pages (0):</b>\'.br();\r\n		if (count($result2)>10) $member_pages .=\'<div class=\"three_col\">\';\r\n		$member_pages .= \'None of them\'.br();\r\n	}\r\n	else\r\n	{\r\n		$member_pages .= br().\'<b>This group has access to the following pages (\'.count($result2).\'):</b>\'.br();\r\n		if (count($result2)>10) $member_pages .=\'<div class=\"three_col\">\';\r\n		foreach($result2 as $row2)\r\n		{\r\n			$member_pages .= a(img(\'Remove access\',\'/img/silk/delete.png\'),\'toolkit_security_manager&access_group_manage=\'.$_GET[\'access_group_manage\'].\'&delmethod=\'.$row2[\'id\'].\'&name=\'.$row2[\'name\']).\' \';\r\n			$member_pages .= a(img(\'Manage Page\',\'/img/silk/group_key.png\'),\'toolkit_security_manager&access_list_manage=\'.$row2[\'id\']).\' \';\r\n			$member_pages .= $row2[\'name\'].br();\r\n		}\r\n	}\r\n	if (count($result2)>10) $member_pages .=\'</div>\';\r\n\r\n	// ********************* Available Pages *********************\r\n	$available_pages=\'\';\r\n	if ($_GET[\'access_group_manage\']!=1)\r\n	{\r\n		$qry = \"SELECT name,id FROM php_methods ORDER by name\";\r\n		$params = array();\r\n		$result3 = query($qry, $params);\r\n\r\n\r\n$page_count = 0;\r\n$available_pages_inner=\'\';\r\n		if ($_GET[\'access_group_manage\']==1) $available_pages_inner .= \'Everything\';\r\n		else foreach($result3 as $row2)\r\n		{\r\n			$found=false;\r\n			foreach($result2 as $row)\r\n			{\r\n				if ($row2[\'name\']==$row[\'name\']) $found = true;\r\n			}\r\n			$name = $row2[\'name\'];\r\n			if (!$found)\r\n			{\r\n				if (strpos($name,\'cms_\')===0) {}\r\n				else if (strpos($name,\'hlp_\')===0) {}\r\n				else if (strpos($name,\'db_\')===0) {}\r\n				else if (strpos($name,\'delete_\')===0) {}\r\n				else if (strpos($name,\'style_\')===0) {}\r\n				else if (strpos($name,\'script_\')===0) {}\r\n				else if (strpos($name,\'div_\')===0) {}\r\n				else if (strpos($name,\'html_\')===0) {}\r\n				else\r\n				{$page_count+=1;\r\n					$available_pages_inner .= a(img(\'Add access\',\'/img/silk/add.png\'),\'toolkit_security_manager&access_group_manage=\'.$_GET[\'access_group_manage\'].\'&addmethod=\'.$row2[\'id\'].\'&name=\'.$row2[\'name\']).\' \';\r\n					$available_pages_inner .= a(img(\'Manage Page\',\'/img/silk/group_key.png\'),\'toolkit_security_manager&access_list_manage=\'.$row2[\'id\']).\' \';\r\n					$available_pages_inner .= $row2[\'name\'].br();\r\n				}\r\n			}\r\n		}\r\n		$available_pages .= br().\'<b>Available pages (\'.$page_count.\'):</b>\'.br();\r\n		if ($page_count>10) $available_pages .=\'<div class=\"three_col\">\';\r\n		$available_pages .= $available_pages_inner;\r\n		if ($page_count>10) $available_pages .= \'</div>\';\r\n	}\r\n\r\n	$output .= $return;\r\n	$output .= $member_users;\r\n	$output .= $member_pages;\r\n	$output .= $available_users;\r\n	$output .= $available_pages;\r\n	return;\r\n}\r\n\r\n// zzzzzzzzzzzzzzzzzzzzzzzzzzzzzzzzzzzzzzzzzzzzzzzzzzzzzzzzzzzzzzzzzzzzzzzzzzzzzzzzzzzzzzzzzzzzzzzzzzzzzzzzzzzzzzzzzzzzzzzzzzzzzzzzzzzzzzzzzzzzzzzzzzzzzzzzzzzzzzzzzzzzzzzzzzzzzzzz\r\n\r\n// ********************************************* Page Options *********************************************\r\nelse if (isset($_GET[\'access_list_manage\']) && $_GET[\'access_list_manage\']>0)\r\n{\r\n	$return=a(\'Back to security manager\',\'toolkit_security_manager\').br().br();\r\n\r\n	// If the group has been added and passed via GET\r\n	if (isset($_GET[\'addgroup\']) && $_GET[\'addgroup\']>0)\r\n	{\r\n		$qry = \"SELECT name FROM php_methods WHERE id=? LIMIT 1\";\r\n		$params = array($_GET[\'access_list_manage\']);\r\n		$result = query($qry, $params);\r\n		if (count($result)==1) $page_name = $result[0][\'name\'];\r\n		else $page_name = \'Page\';\r\n\r\n		$qry = \"SELECT id FROM access_list WHERE method_id=? AND group_id=?\";\r\n		$params = array($_GET[\'access_list_manage\'], $_GET[\'addgroup\']);\r\n		$result = query($qry, $params);\r\n		if (count($result)>0)\r\n		{\r\n			msg_user(\"Access Change Skipped\",$_GET[\'name\'].\" already has access to the page, \".$page_name.\'.\',\"index.php?toolkit_security_manager&access_list_manage=\".$_GET[\'access_list_manage\']);\r\n		}\r\n		else\r\n		{\r\n			$qry = \"INSERT INTO access_list(method_id,group_id,modified,modifier) VALUES(?,?,CURRENT_TIMESTAMP,?)\";\r\n			$params = array($_GET[\'access_list_manage\'],$_GET[\'addgroup\'],$_SESSION[\'id\']);\r\n			$result = query_execute($qry, $params);\r\n			if ($result) msg_user(\"Access Granted\",$_GET[\'name\'].\" can now access the page, \".$page_name.\'!\',\"index.php?toolkit_security_manager&access_list_manage=\".$_GET[\'access_list_manage\']);\r\n			else msg_user(\"Access Problem\",$_GET[\'name\'].\" could not be added to the access list for the page, \".$page_name.\'.\',\"index.php?toolkit_security_manager&access_list_manage=\".$_GET[\'access_list_manage\']);\r\n		}\r\n	}\r\n	// If the group has been removed and passed via GET\r\n	else if (isset($_GET[\'delgroup\']) && $_GET[\'delgroup\']>0)\r\n	{\r\n		$qry = \"SELECT name FROM php_methods WHERE id=? LIMIT 1\";\r\n		$params = array($_GET[\'access_list_manage\']);\r\n		$result = query($qry, $params);\r\n		if (count($result)==1) $page_name = $result[0][\'name\'];\r\n		else $page_name = \'Page\';\r\n\r\n		$qry = \"DELETE FROM access_list WHERE method_id=? AND group_id=? LIMIT 1\";\r\n		$params = array($_GET[\'access_list_manage\'],$_GET[\'delgroup\']);\r\n		$result = query_execute($qry, $params);\r\n		if ($result) msg_user(\"Access Removed\",$_GET[\'name\'].\" can no longer access the page, \".$page_name.\'.\',\"index.php?toolkit_security_manager&access_list_manage=\".$_GET[\'access_list_manage\']);\r\n		else msg_user(\"Access Problem\",$_GET[\'name\'].\" could not be removed from the access list for, \".$page_name.\'.\',\"index.php?toolkit_security_manager&access_list_manage=\".$_GET[\'access_list_manage\']);\r\n		//return;\r\n	}\r\n	// ********************************************* Page Trash *********************************************\r\n	else if (isset($_GET[\'trash\']))\r\n	{\r\n		db_method_trash($_GET[\'trash\']);\r\n		msg_user(\"Trash\",\"Method \".$_GET[\'trash\'].\" moved to the trash!\",\"/index.php?toolkit_security_manager\");\r\n	}\r\n\r\n	$qry = \"SELECT id,name,type,title,keyword,description FROM php_methods WHERE id=?\";\r\n	$params = array($_GET[\'access_list_manage\']);\r\n	$result = query($qry, $params);\r\n\r\n	if (count($result)!=1) { header(\"Location: index.php?toolkit_security_manager\"); return; }\r\n	\r\n	$group_name = $result[0][\'name\'];\r\n\r\n	$return.=\'<b>Manage Page:</b> \'.$result[0][\'name\'].br();//.\'<b>Description:</b> \'.$result[0][\'description\'].br();\r\n	if ($group_name != \'Administrators\' && $group_name != \'Authenticated\' && $group_name != \'Anonymous\')\r\n	{\r\n		$return .= br();\r\n		$return .= a(img(\'Edit Page\',\'img/silk/page_white_edit.png\'),\'toolkit_edit_method=\'.$result[0][\'name\']).\' \';\r\n		$return .= a(img(\'Trash Page\',\'/img/silk/page_white_database.png\'),\'toolkit_security_manager&access_list_manage=\'.$_GET[\'access_list_manage\'].\'&trash=\'.$result[0][\'name\']).\' \';\r\n		$return .= br();\r\n	}\r\n\r\n	// ********************* Member Groups *********************\r\n	$member_groups=\'\';\r\n\r\n	$qry = \"SELECT access_group.name, access_group.id FROM access_group\r\n	INNER JOIN access_list WHERE access_list.group_id = access_group.id AND \r\n	access_list.method_id=?\";\r\n\r\n	$params = array($_GET[\'access_list_manage\']);\r\n	$result2 = query($qry, $params);\r\n\r\n	$member_groups .= br().\'<b>This page can be accessed by the following security groups (\'.(count($result2)+1).\'):</b>\'.br();\r\n	if ((count($result2)+1)>10) $member_groups .=\'<div class=\"three_col\">\';\r\n\r\n	$member_groups .= img(\'The Administrators security group always has access to every page.\',\'/img/silk/information.png\').\' \';\r\n	$member_groups .= a(img(\'Manage Group\',\'/img/silk/group_key.png\'),\'toolkit_security_manager&access_group_manage=1&name=Administrators\').\' \';\r\n	$member_groups .= \'Administrators\'.br();\r\n\r\n	foreach($result2 as $row2)\r\n	{\r\n		$member_groups .= a(img(\'Remove access\',\'/img/silk/delete.png\'),\'toolkit_security_manager&access_list_manage=\'.$_GET[\'access_list_manage\'].\'&delgroup=\'.$row2[\'id\'].\'&name=\'.$row2[\'name\']).\' \';\r\n		$member_groups .= a(img(\'Manage Group\',\'/img/silk/group_key.png\'),\'toolkit_security_manager&access_group_manage=\'.$row2[\'id\'].\'&name=\'.$row2[\'name\']).\' \';\r\n		$member_groups .= $row2[\'name\'].br();\r\n	}\r\n	if ((count($result2)+1)>10) $member_groups .=\'</div>\';\r\n\r\n	// ********************* Available Groups *********************\r\n	$available_pages=\'\';\r\n	if ($_GET[\'access_list_manage\']!=1)\r\n	{\r\n		$qry = \"SELECT name,id FROM access_group ORDER by name\";\r\n		$params = array();\r\n		$result3 = query($qry, $params);\r\n\r\n		if ((count($result3)-count($result2)-1)!=0)\r\n		{\r\n			$available_pages .= br().\'<b>Available security groups (\'.(count($result3)-count($result2)-1).\'):</b>\'.br();\r\n			if ((count($result3)-count($result2)-1)>10) $available_pages .=\'<div class=\"three_col\">\';\r\n		}\r\n\r\n		if ($_GET[\'access_list_manage\']==1) $available_pages .= \'Everything\';\r\n		else foreach($result3 as $row2)\r\n		{\r\n			$found=false;\r\n			foreach($result2 as $row)\r\n			{\r\n				if ($row2[\'name\']==$row[\'name\']) $found = true;\r\n			}\r\n\r\n			if (!$found && $row2[\'name\'] != \'Administrators\')\r\n			{\r\n				$available_pages .= a(img(\'Add access\',\'/img/silk/add.png\'),\'toolkit_security_manager&access_list_manage=\'.$_GET[\'access_list_manage\'].\'&addgroup=\'.$row2[\'id\'].\'&name=\'.$row2[\'name\']).\' \';\r\n				$available_pages .= a(img(\'Manage Group\',\'/img/silk/group_key.png\'),\'toolkit_security_manager&access_group_manage=\'.$row2[\'id\'].\'&name=\'.$row2[\'name\']).\' \';\r\n				$available_pages .= $row2[\'name\'].br();\r\n			}\r\n		}\r\n		if ((count($result3)-count($result2)-1)>10) $available_pages .= \'</div>\';\r\n	}\r\n\r\n	$output .= $return;\r\n	//$output .= $member_users;\r\n	$output .= $member_groups;\r\n	//$output .= $available_users;\r\n	$output .= $available_pages;\r\n	return;\r\n}\r\n\r\n\r\n\r\n\r\n// ********************************************* User Create *********************************************\r\nelse if (isset($_GET[\'user_create\']))\r\n{\r\n	$output .= <<< OUTPUT\r\n\r\n<div class=\"fluid-row\">\r\n\r\n<div class=\"span4\">\r\n</div>\r\n\r\n<div class=\"span4\">\r\n<form class=\"well\" onsubmit=\"return validateForm()\" method=\"post\">\r\n<h3>Create User</h3>\r\n	<form onsubmit=\"return validateLoginCreateForm()\" method=\"post\">\r\n	<label>Username:</label><input class=\"span12\" type=\"text\" id=\"user\" name=\"user\" />\r\n	<label>Password:</label><input class=\"span12\" type=\"password\" id=\"pass\" name=\"pass\" />\r\n	<label>Email:</label><input class=\"span12\" type=\"text\" id=\"email\" name=\"email\" />\r\n<div class=\"clear\"></div>\r\n<button type=\"submit\" class=\"btn\" title=\"Click once to create\">Create</button>\r\n</form>\r\n</div>\r\n\r\n<div class=\"span4\">\r\n</div>\r\n\r\n</div>\r\n<div class=\"clear\"></div>\r\n\r\n	<script>document.getElementById(\"user\").focus();</script>\r\nOUTPUT;\r\n}\r\n// ********************************************* Group Create *********************************************\r\nelse if (isset($_GET[\'group_create\']))\r\n{\r\n	$output .= <<< OUTPUT\r\n<div class=\"fluid-row\">\r\n\r\n<div class=\"span4\">\r\n</div>\r\n\r\n<div class=\"span4\">\r\n<form class=\"well\" onsubmit=\"return validateForm()\" method=\"post\">\r\n	<h3>Create Group</h3>\r\n	<label>Group Name:</label><input class=\"span12\" type=\"text\" id=\"name\" name=\"name\" />\r\n	<label>Description:</label><input class=\"span12\" type=\"text\" id=\"description\" name=\"description\" />\r\n<div class=\"clear\"></div>\r\n<button type=\"submit\" class=\"btn\" title=\"Click once to create\">Create</button>\r\n</form>\r\n</div>\r\n\r\n<div class=\"span4\">\r\n</div>\r\n\r\n</div>\r\n<div class=\"clear\"></div>\r\n	\r\n	<script>document.getElementById(\"name\").focus();</script>\r\n\r\nOUTPUT;\r\n}\r\n\r\n// Output the icons at the top\r\n//$output .= \'<b>Current User:</b> \'.$_SESSION[\'username\'].br().br();\r\n\r\n//$output .= \'<b>Security Options:</b>\'.br();\r\n//$output .= a(img(\'Create User\',\'/img/silk/user_add.png\'),\'toolkit_security_manager&user_create\').\' \';\r\n//$output .= a(img(\'Create Group\',\'/img/silk/group_add.png\'),\'toolkit_security_manager&group_create\').\' \';\r\n\r\n// Output the users\r\n$result = query(\"SELECT id,username FROM access_user ORDER BY username ASC\", array());\r\n$return=\'\';\r\nforeach($result as $row)\r\n{\r\n	// Output the user\r\n	$return.=a($row[\'username\'],\'toolkit_security_manager&user_info=\'.$row[\'id\']).br();\r\n}\r\n$output .= a(img(\'Create User\',\'/img/silk/user_add.png\'),\'toolkit_security_manager&user_create\').\' \';\r\n$output .= \'<b>Users (\'.count($result).\'):</b>\'.br().$