nline-block;\">\'.$add.\'</div>\';\r\n		else\r\n		{\r\n			//$return .= $name.\' \'.$remove.br();\r\n			$return .= a($name,\"toolkit_security_manager&access_list_manage=$id\").\' \'.$remove.br();\r\n			$page_counter += 1;\r\n		}\r\n	}\r\n\r\n$output .= br().a(img(\'Create Page\',\'/img/silk/page_add.png\'),\'toolkit_add_method&html\').\' \';\r\n$output .= \'<b>Pages (\'.$page_counter.\'):</b>\';\r\nif ($page_counter>10) $output .= \'<div class=\"three_col\">\';\r\n$output .= $return;\r\nif ($page_counter>10) $output .= \'</div>\';','2012-08-04 15:26:35',2,1,'if (isset($_GET[\'user_create\']))\r\n{\r\n	$qry = \"INSERT INTO access_user (\r\n	username,\r\n	password,\r\n	email,\r\n	first_login,\r\n	last_login,\r\n	total_login,\r\n	modified,\r\n	modifier,\r\n	created,\r\n	creator) VALUES (?,?,?,0,0,0,CURRENT_TIMESTAMP,?,CURRENT_TIMESTAMP,?)\";\r\n	$params = array(secure_username($_POST[\'user\']),secure_password($_POST[\'user\'], $_POST[\'pass\']),$_POST[\'email\'],$_SESSION[\'id\'],$_SESSION[\'id\']);\r\n	$return = query_execute($qry, $params);\r\n	\r\n	if ($return) msg_user(\'Account Creation\',\'User, \'.$_POST[\'user\'].\', created successfully\',\"index.php?toolkit_security_manager\");\r\n	else msg_user(\'Account Creation\',\'User, \'.$_POST[\'user\'].\', already exists\',\"index.php?toolkit_security_manager\");\r\n}\r\nelse if (isset($_GET[\'user_delete\']))\r\n{\r\n	$val = $_POST[\'user_id\'];\r\n\r\n	// Delete the user from the database\r\n	$qry = \"DELETE FROM access_user WHERE id=? LIMIT 1\";\r\n	$params = array($val);\r\n	query_execute($qry, $params);\r\n\r\n	// Delete the user from any groups\r\n	$qry = \"DELETE FROM access_membership WHERE user_id=?\";\r\n	$params = array($val);\r\n	query_execute($qry, $params);\r\n\r\n	// Check for proper removal\r\n	$qry = \"SELECT id FROM access_user WHERE id=? LIMIT 1\";\r\n	$params = array($val);\r\n	$result = query($qry, $params);\r\n\r\n	if (!$result) msg_user(\"User Removal\",\"User, \".$_POST[\'user\'].\", removed successfully!\",\"index.php?toolkit_security_manager\");\r\n	else msg_user(\"User Removal\",\"User, \".$_POST[\'user\'].\", was NOT removed successfully.\",\"index.php?toolkit_security_manager\");\r\n}\r\nelse if (isset($_GET[\'user_edit\']))\r\n{\r\n	if ($_POST[\'pass_new\']!=$_POST[\'pass\']) $pass_new = secure_password($_POST[\'user\'], $_POST[\'pass_new\']);\r\n	else $pass_new = $_POST[\'pass\'];\r\n\r\n	$qry = \"UPDATE access_user SET username=?, password=?, email=?, modified=CURRENT_TIMESTAMP, modifier=? WHERE id=?\";\r\n	$params = array(secure_username($_POST[\'user\']),$pass_new,$_POST[\'email\'],$_SESSION[\'id\'],$_GET[\'user_edit\']);\r\n	$return = query_execute($qry, $params);\r\n	\r\n	if ($return) msg_user(\'Account Update\',\'User, \'.$_POST[\'user\'].\', updated successfully!\',\"index.php?toolkit_security_manager&user_info=\".$_GET[\'user_edit\']);\r\n	else msg_user(\'Account Update\',\'User, \'.$_POST[\'user\'].\', was NOT updated successfully.\',\"index.php?toolkit_security_manager&user_info=\".$_GET[\'user_edit\']);\r\n}\r\nelse if (isset($_GET[\'group_create\']))\r\n{\r\n	$qry = \"INSERT INTO access_group (name,description,modified,modifier,created,creator)\r\n		VALUES (?,?,CURRENT_TIMESTAMP,?,CURRENT_TIMESTAMP,?)\";\r\n	$params = array($_POST[\'name\'],$_POST[\'description\'],$_SESSION[\'id\'],$_SESSION[\'id\']);\r\n	$return = query_execute($qry, $params);\r\n	\r\n	if ($return) msg_user(\'Group Creation\',\'Group, \'.$_POST[\'name\'].\', created successfully\',\"index.php?toolkit_security_manager\");\r\n	else msg_user(\'Group Creation\',\'Group, \'.$_POST[\'name\'].\', already exists\',\"index.php?toolkit_security_manager\");\r\n}\r\nelse if (isset($_GET[\'group_delete\']))\r\n{\r\n	$val = $_POST[\'group_id\'];\r\n\r\n	// Delete the group from the access list\r\n	$qry = \"DELETE FROM access_list WHERE group_id=?\";\r\n	$params = array($val);\r\n	query_execute($qry, $params);\r\n\r\n	// Delete the group from the access menu\r\n	$qry = \"DELETE FROM 