ie = \'sess_\'.$_COOKIE[ini_get(\'session.name\')];\r\n	else $mycookie = \'\';\r\n	if (ini_get(\'session.save_path\')==\'\') $path = sys_get_temp_dir();\r\n	else $path = ini_get(\'session.save_path\');\r\n	foreach (glob($path.DIR_SEP.\"ses*\") as $filename)\r\n	{\r\n		if (basename($filename)!=$mycookie) unlink($filename);\r\n	}\r\n	msg_user(\"Session Removal\",\"All sessions were deleted.\",\'/toolkit_session_manager\');\r\n}\r\nelse if (isset($_GET[\'empty\']))\r\n{\r\n	if (ini_get(\'session.save_path\')==\'\') $path = sys_get_temp_dir();\r\n	else $path = ini_get(\'session.save_path\');\r\n	foreach (glob($path.DIR_SEP.\"ses*\") as $filename)\r\n	{\r\n		if (filesize($filename)==0) unlink($filename);\r\n	}\r\n	msg_user(\"Session Removal\",\"All empty sessions were deleted.\",\'/toolkit_session_manager\');\r\n}\r\nelse if (isset($_GET[\'logged\']))\r\n{\r\n	if (!isset($_GET[\'mine\'])) $mycookie = \'sess_\'.$_COOKIE[ini_get(\'session.name\')];\r\n	else $mycookie = \'\';\r\n	if (ini_get(\'session.save_path\')==\'\') $path = sys_get_temp_dir();\r\n	else $path = ini_get(\'session.save_path\');\r\n	foreach (glob($path.DIR_SEP.\"ses*\") as $filename)\r\n	{\r\n		if (strpos(file_get_contents($filename),\'id|\')!==false)\r\n		{		\r\n			if (basename($filename)!=$mycookie) unlink($filename);\r\n		}\r\n	}\r\n	msg_user(\"Session Removal\",\"All sessions were deleted.\",\'/toolkit_session_manager\');\r\n}\r\n\r\nfunction item_right($filename)\r\n{\r\n	$div_name = basename($filename);\r\n	$menu_name = basename($filename).\'_m\';\r\n\r\n	$file_size = filesize($filename);\r\n	$file_contents = htmlentities(file_get_contents($filename), ENT_QUOTES, \"UTF-8\");\r\n\r\n//$file_contents = str_replace(\';\',\'<br />\',$file_contents);\r\n\r\n	$mycookie = \'sess_\'.$_COOKIE[ini_get(\'session.name\')];\r\n	if (basename($filename)==$mycookie) $mine=true;\r\n	else $mine=false;\r\n\r\n	if (!$mine) $img = img(\'Another user session\',\'/img/silk/bullet_green.png\');\r\n	else $img = img(\'My session\',\'/img/silk/bullet_red.png\');\r\n\r\n	$entry1=\'\';\r\n	// Prevent Windows Problems\r\n	if (!IS_WIN_OS)\r\n	{\r\n		$entry1=\'<li><a href=\"\'.$_SERVER[\'QUERY_STRING\'].\'&delete=\'.$filename.\'\">Delete session</a></li>\';\r\n		$entry2=\'<li><a href=\"\'.$_SERVER[\'QUERY_STRING\'].\'&logged&mine\">Delete all logged in sessions, including mine</a></li>\';\r\n		$entry3=\'<li><a href=\"\'.$_SERVER[\'QUERY_STRING\'].\'&deleteall&mine\">Delete all sessions, including mine</a></li>\';\r\n	}else\r\n	{\r\n		if (!$mine) $entry1=\'<li><a href=\"\'.$_SERVER[\'QUERY_STRING\'].\'&delete=\'.$filename.\'\">Delete session</a></li>\';\r\n		$entry2=\'\';\r\n		$entry3=\'\';\r\n	}\r\n\r\n	$text = <<< OUTPUT\r\n\r\n<div class=\"btn-group pull-right\">\r\n<a class=\"btn dropdown-toggle\" data-toggle=\"dropdown\" href=\"#\">Options<span class=\"caret\"></span></a>\r\n<ul class=\"dropdown-menu\">\r\n\r\n			{$entry1}\r\n			<li><a href=\"{$_SERVER[\'QUERY_STRING\']}&empty\">Delete all empty sessions</a></li>\r\n			<li><a href=\"{$_SERVER[\'QUERY_STRING\']}&logged\">Delete all logged in sessions, except mine</a></li>\r\n			{$entry2}\r\n			<li><a href=\"{$_SERVER[\'QUERY_STRING\']}&deleteall\">Delete all sessions, except mine</a></li>\r\n			{$entry3}\r\n			</ul>\r\n		</div>\r\n<pre>{$img}{$filename} - {$file_size} bytes<br />{$file_contents}</pre>\r\n\r\n	\r\n	<script language=\"javascript\">\r\n	\r\n	$(\"#{$div_name}\").bind(\'contextmenu\',function(e){\r\n		$(\"#{$menu_name}\").css({left: e.pageX-10, top: e.pageY-35, zIndex: \'101\', position: \'absolute\', display: \'block\'}).show();\r\n	}).bind(\'contextmenu\' , function(){\r\n	return false;\r\n	});\r\n	$(\"#{$div_name}\").mouseleave(function(){\r\n	$(\"#{$menu_name}\").hide();\r\n	});\r\n	\r\n	</script>\r\n\r\nOUTPUT;\r\n\r\n	return array($text,$mine);\r\n}\r\n\r\n$empty = 0;\r\n$total = 0;\r\n$loggedin = 0;\r\n$text = \'\';\r\n$mine = \'\';\r\nif (ini_get(\'session.save_path\')==\'\') $path = sys_get_temp_dir();\r\nelse $path = ini_get(\'session.save_path\');\r\nforeach (glob($path.DIR_SEP.\"ses*\") as $filename)\r\n{\r\n\r\n	if (filesize($filename)==0) $empty+=1;\r\n	else\r\n	{\r\n		$return_arr = item_right($filename); \r\n		if ($return_arr[1]) $mine .= $return_arr[0];\r\n		else $text .= $return_arr[0];\r\n	}\r\n\r\n	if (strpos(file_get_contents($filename),\'id|\')!==false) $loggedin+=1;\r\n\r\n	$total+=1;\r\n}\r\n\r\n$output .= \'<b>Empty sessions:</b> \'. $empty.br();\r\n$output .= \'<b>Logged in sessions:</b> \'.$loggedin.br();\r\n$output .= \'<b>Total sessions:</b> \'. $total.br().br();\r\n\r\n$output .= $mine;\r\n$output .= $text;','2012-08-08 22:33:52',2,1,'','Session Manager','',''),(281,'toolkit_silk_gallery','$files = glob(CMS_ROOT.DIR_SEP.\'img\'.DIR_SEP.\'silk\'.DIR_SEP.\"*\", GLOB_MARK);\r\n\r\n$output .= \'<div id=\"img_path\" name=\"img_path\" style=\"display: inline;\"></div><br /><br /><b>Preview: </b><br />\';\r\nforeach($files as $f)\r\n{\r\n	$rel = str_replace(array(CMS_ROOT,\'\\\\\'),array(\'\',\'/\'),$f);\r\n	if (stripos($f,\".png\")!==false ||\r\n	stripos($f,\".gif\")!==false ||\r\n	stripos($f,\".jpg\")!==false ||\r\n	stripos($f,\".jpeg\")!==false ||\r\n	stripos($f,\".ico\")!==false ||\r\n	stripos($f,\".bmp\")!==false) $output .= img_special($rel,$rel,true,\'3px 2px 2px 3px\', \"$(\'#img_path\').html(\'img(\\\'Image Text\\\',\\\'$rel\\\')\');\").\' \';\r\n}','2012-07-09 21:10:49',2,1,'','Silk Gallery','',''),(285,'toolkit_index_basic','<script>\r\n$(document).ready(function() {\r\n	$(\'#main_sidebar\').css(\'display\',\'none\');$(\'#main_content\').removeClass(\'span10\');\r\n	$(\'#main_content\').addClass(\'span12\');\r\n});\r\n</script>\r\n\r\n<div class=\"row-fluid\">\r\n	<div class=\"hero-unit span6\">\r\n		<h1>Toolkit</h1>\r\n		<p>Welcome, Admin! Take a look around the FusionLeaf interface.</p>\r\n		<p><a class=\"btn btn-primary btn-large\" href=\"/page_guide_planters\">Planter\'s Guide</a></p>\r\n	</div>\r\n	<div class=\"span4 well\">\r\n		<p>Thanks for trying FusionLeaf! Be sure to check out the Planter\'s Guide for more information on the different features included in the FusionLeaf Content Management System. If you have any problems or find any bugs, please create a new <a href=\"http://code.google.com/p/fusionleaf/issues\">issue</a>.</p>\r\n		<p>FusionLeaf is licensed under the <a href=\"http://www.apache.org/licenses/LICENSE-2.0.html\">Apache License, Version 2.0</a> so feel free to change anything you need to fit your requirements. The CMS is very customizable and includes a full arsenal of tools.</p>\r\n		<p>Check for new versions on <a href=\"http://www.fusionleaf.com\">FusionLeaf.com</a>.</p>\r\n	</div>\r\n	<div class=\"span2 well\">\r\n		<p><strong>Tip:</strong></p>\r\n		<p>Once you are comfortable with the Basic Toolkit, you can use the Main Page Selection tool to change the default Administrators page to the advanced Web Toolkit which is page: /toolkit_index.</p>\r\n	</div>\r\n</div>\r\n<div class=\"row-fluid\">\r\n	<div class=\"span2 well\">\r\n		<a href=\"toolkit_security_manager\"><img src=\"/img/silk/group_key.png\"/> Security Controller</a>\r\n		<p>Modify the users, security groups, and access lists for every page.</p>\r\n	</div>\r\n	<div class=\"span2 well\">\r\n		<a href=\"toolkit_file_manager\"><img src=\"/img/silk/folder_page.png\"/> File Organizer\r\n		</a>\r\n		<p>Upload, decompress, and modify any file on your web server.</p>\r\n	</div>\r\n	<div class=\"span2 well\">\r\n		<a href=\"toolkit_session_manager\"><img src=\"/img/silk/lock.png\"/> Session Manager\r\n		</a>\r\n		<p>Quickly see and destroy any sessions on your web site.</p>\r\n	</div>\r\n	<div class=\"span2 well\">\r\n		<a href=\"toolkit_site_settings\"><img src=\"/img/silk/layout_content.png\"/> Site Settings\r\n		</a>\r\n		<p>Change the location of the navigation bar or the \"Powered by\" text.</p>\r\n	</div>\r\n	<div class=\"span2 well\">\r\n		<a href=\"toolkit_bootstrap_themes\"><img src=\"/img/silk/palette.png\"/> Theme Selector\r\n		</a>\r\n		<p>Pick a new theme from the selection of swatches from Bootswatch.</p>\r\n	</div>\r\n	<div class=\"span2 well\">\r\n		<a href=\"toolkit_menu_builder\"><img src=\"/img/silk/tab.png\"/> Menu Builder\r\n		</a>\r\n		<p>Create a new navigation menu or customize an existing one.</p>\r\n	</div>\r\n</div>\r\n<div class=\"row-fluid\">\r\n	<div class=\"span2 well\">\r\n		<a href=\"toolkit_query\"><img src=\"/img/silk/database_table.png\"/> Database Manager</a>\r\n		<p>Access any of the tables or add a new row a data easily.</p>\r\n	</div>\r\n	<div class=\"span2 well\">\r\n		<a href=\"toolkit_database?backup\"><img src=\"/img/silk/database_save.png\"/> Backup Database</a>\r\n		<p>Save and email the database to prevent any data loss.</p>\r\n	</div>\r\n	<div class=\"span2 well\">\r\n		<a href=\"toolkit_database?restore\"><img src=\"/img/silk/database_refresh.png\"/> Restore Database</a>\r\n		<p>Restore the newest database backup from development.</p>\r\n	</div>\r\n	<div class=\"span2 well\">\r\n		<a href=\"toolkit_search\"><img src=\"/img/silk/zoom.png\"/> Search/Replace</a>\r\n		<p>Find what you\'re looking for or make quick changes.</p>\r\n	</div>\r\n	<div class=\"span2 well\">\r\n		<a href=\"recache\"><img src=\"/img/silk/vector.png\"/> Recache</a>\r\n		<p>Clear out Memcache, XCache, and the file cache if you make an external change.</p>\r\n	</div>\r\n	<div class=\"span2 well\">\r\n		<a href=\"toolkit_update_sitemap\"><img src=\"/img/silk/chart_organisation.png\"/> Sitemap</a>\r\n		<p>Generate a new sitemap when you make changes to public pages.</p>\r\n	</div>\r\n</div>\r\n<div class=\"row-fluid\">\r\n	<div class=\"span2 well\">\r\n		<a href=\"toolkit_index_selection\"><img src=\"/img/silk/house.png\"/> Main Page Selection</a>\r\n		<p>Change the default home page for the main security groups.</p>\r\n	</div>\r\n	<div class=\"span2 well\">\r\n		<a href=\"toolkit_silk_gallery\"><img src=\"/img/silk/application_view_tile.png\"/> Silk Gallery</a>\r\n		<p>Quickly find a Silk icon to use on any of your pages.</p>\r\n	</div>\r\n	<div class=\"span2 well\">\r\n		<a href=\"toolkit_command_shell\"><img src=\"/img/silk/application_osx_terminal.png\"/> Command Shell</a>\r\n		<p>Run a command on your web server and view the output.</p>\r\n	</div>\r\n	<div class=\"span2 well\">\r\n		<a href=\"toolkit_twitter\"><img src=\"/img/social/twitter.png\"/> Tweet</a>\r\n		<p>Send a tweet to keep everyone updated.</p>\r\n	</div>\r\n	<div class=\"span2 well\">\r\n		<a href=\"toolkit_error_log\"><img src=\"/img/silk/error.png\"/> Error Log</a>\r\n		<p>View any problems or errors that occurred on the web site.</p>\r\n	</div>\r\n	<div class=\"span2 well\">\r\n		<a href=\"toolkit_index\"><img src=\"/img/silk/bricks.png\"/> Web Toolkit</a>\r\n		<p>See all the pages included in FusionLeaf.</p>\r\n	</div>\r\n</div>','2012-08-08 19:04:57',2,0,'','Basic Toolkit','',''),(286,'toolkit_menu_builder','$output .= a(img(\'Add a menu item\',\'/img/silk/tab_add.png\'),\'toolkit_menu_builder&add\').br();\r\n$output .= \'<div style=\"text-align: right;\">Test out your menus here:</div>\';\r\n\r\n\r\n\r\n// ********************************************* Add a menu item *********************************************\r\nif (isset($_GET[\'add\']) || isset($_GET[\'edit\']))\r\n{\r\n	if (isset($_GET[\'edit\']))\r\n	{\r\n		$edit = $_GET[\'edit\'];\r\n		$qry = \"SELECT id,label,url,parent FROM php_menu WHERE id=? LIMIT 1\";\r\n		$params = array($edit);\r\n		$edit_info = query($qry, $params);\r\n	}\r\n	else $edit=false;\r\n\r\n	// ******************************** Build select ******************************** \r\n	$qry = \"SELECT id,label FROM php_menu WHERE parent=0 ORDER BY id\";\r\n	$params = array();\r\n	$result = query($qry, $params);\r\n	\r\n	$select = \'<select id=\"parent\" name=\"parent\">\';\r\n	$select .= \'<option value=\"0\">(No parent)</option>\';\r\n\r\n	$label = \'\';\r\n	$url = \'\';\r\n	$button_text = \"Add\";\r\n	if ($edit)\r\n	{\r\n		$label = $edit_info[0][\'label\'];\r\n		$url = $edit_info[0][\'url\'];\r\n		$button_text = \"Update\";\r\n	}\r\n\r\n	foreach($result as $row)\r\n	{\r\n		$selected = \'\';\r\n		if ($edit)\r\n		{\r\n			if ($edit_info[0][\'parent\']==$row[\'id\']) $selected=\'selected\';\r\n		}\r\n		$select .= \'<option \'.$selected.\' value=\"\'.$row[\'id\'].\'\">\'.$row[\'label\'].\'</option>\';\r\n	}\r\n	$select .= \'</select>\';\r\n\r\n	// ******************************** Output form ******************************** \r\n	$output .= <<< OUTPUT\r\n<div class=\"fluid-row\">\r\n\r\n<div class=\"span4\">\r\n</div>\r\n	\r\n	<div class=\"span4\">\r\n	<form class=\"well\" onsubmit=\"return validateForm()\" method=\"post\">\r\n	<h3>{$button_text} Menu Item</h3>\r\n	<label>Label:</label><input class=\"span12\" type=\"text\" id=\"label\" name=\"label\" value=\"{$label}\" />\r\n	<label>URL (optional):</label><input class=\"span12\" type=\"text\" id=\"url_optional\" name=\"url_optional\" value=\"{$url}\" />\r\n	<label>Parent:</label>{$select}\r\n	<div class=\"clear\"></div>\r\n	<button type=\"submit\" class=\"btn\" title=\"Click once to {$button_text} menu item\">{$button_text}</button>\r\n	</form>\r\n	</div>\r\n	\r\n<div class=\"span4\">\r\n</div>\r\n\r\n</div>\r\n	<div class=\"clear\"></div>\r\n\r\n<script>document.getElementById(\"label\").focus();</script>\r\nOUTPUT;\r\n}\r\n// ********************************************* Move a menu item *********************************************\r\nelse if (isset($_GET[\'down\']))\r\n{\r\n	$num = floatval($_GET[\'down\']);\r\n	$num += 1.5;\r\n	$qry = \"UPDATE php_menu SET position=? WHERE id=?\";\r\n	$params = array($num,$_GET[\'id\']);\r\n	$result = query_execute($qry, $params);\r\n	msg_user(\"Menu Moved\",\"Menu item moved down!\",\'toolkit_menu_builder\');\r\n}\r\nelse if (isset($_GET[\'up\']))\r\n{\r\n	$num = floatval($_GET[\'up\']);\r\n	$num -= 1.5;\r\n	$qry = \"UPDATE php_menu SET position=? WHERE id=?\";\r\n	$params = array($num,$_GET[\'id\']);\r\n	$result = query_execute($qry, $params);\r\n	msg_user(\"Menu Moved\",\"Menu item moved up!\",\'toolkit_menu_builder\');\r\n}\r\nelse if (isset($_GET[\'delete\']))\r\n{\r\n	$qry = \"DELETE FROM php_menu WHERE id=?\";\r\n	$params = array($_GET[\'delete\']);\r\n	$result = query_execute($qry, $params);\r\n\r\n	$qry = \"DELETE FROM php_menu WHERE parent=?\";\r\n	$params = array($_GET[\'delete\']);\r\n	$result = query_execute($qry, $params);\r\n\r\n	$qry = \"DELETE FROM access_menu WHERE menu_id=?\";\r\n	$params = array($_GET[\'delete\']);\r\n	$result = query_execute($qry, $params);\r\n\r\n	msg_user(\"Menu Deletion\",\"Menu item deleted!\",\'toolkit_menu_builder\');\r\n}\r\n\r\n// ********************************************* Change security settings *********************************************\r\n	// If the group has been added and passed via GET\r\n	if (isset($_GET[\'addgroup\']) && $_GET[\'addgroup\']>0)\r\n	{\r\n		$qry = \"SELECT label FROM php_menu WHERE id=? LIMIT 1\";\r\n		$params = array($_GET[\'access_list_manage\']);\r\n		$result = query($qry, $params);\r\n		if (count($result)==1) $page_name = $result[0][\'label\'];\r\n		else $page_name = \'Page\';\r\n\r\n		$qry = \"SELECT id FROM access_menu WHERE menu_id=? AND group_id=?\";\r\n		$params = array($_GET[\'access_list_manage\'], $_GET[\'addgroup\']);\r\n		$result = query($qry, $params);\r\n		if (count($result)>0)\r\n		{\r\n			msg_user(\"Access Change Skipped\",$_GET[\'name\'].\" already has access to the menu, \".$page_name.\'.\',\"index.php?toolkit_menu_builder\");\r\n		}\r\n		else\r\n		{\r\n			$qry = \"INSERT INTO access_menu(menu_id,group_id,modified,modifier) VALUES(?,?,CURRENT_TIMESTAMP,?)\";\r\n			$params = array($_GET[\'access_list_manage\'],$_GET[\'addgroup\'],$_SESSION[\'id\']);\r\n			$result = query_execute($qry, $params);\r\n			if ($result) msg_user(\"Access Granted\",$_GET[\'name\'].\" can now access the menu, \".$page_name.\'!\',\"index.php?toolkit_menu_builder\");\r\n			else msg_user(\"Access Problem\",$_GET[\'name\'].\" could not be added to the access list for the menu, \".$page_name.\'.\',\"index.php?toolkit_menu_builder\");\r\n		}\r\n	}\r\n	// If the group has been removed and passed via GET\r\n	else if (isset($_GET[\'delgroup\']) && $_GET[\'delgroup\']>0)\r\n	{\r\n		$qry = \"SELECT label FROM php_menu WHERE id=? LIMIT 1\";\r\n		$params = array($_GET[\'access_list_manage\']);\r\n		$result = query($qry, $params);\r\n		if (count($result)==1) $page_name = $result[0][\'label\'];\r\n		else $page_name = \'Page\';\r\n\r\n		$qry = \"DELETE FROM access_menu WHERE menu_id=? AND group_id=? LIMIT 1\";\r\n		$params = array($_GET[\'access_list_manage\'],$_GET[\'delgroup\']);\r\n		$result = query_execute($qry, $params);\r\n		if ($result) msg_user(\"Access Removed\",$_GET[\'name\'].\" can no longer access the menu, \".$page_name.\'.\',\"index.php?toolkit_menu_builder\");\r\n		else msg_user(\"Access Problem\",$_GET[\'name\'].\" could not be removed from the access list for the menu, \".$page_name.\'.\',\"index.php?toolkit_menu_builder\");\r\n	}\r\n\r\n\r\n$qry = \"SELECT id,label,url FROM php_menu WHERE parent=? ORDER BY position\";\r\n$params = array(0);\r\n$result = query($qry, $params);\r\n\r\n$new_menu = array();\r\n//foreach($result as $row)\r\n\r\n$has_sisters = (count($result)>1);\r\n\r\n$menu_out=\'\';\r\n$menu_out_final=\'\';\r\n\r\nfor($j=0;$j<count($result);$j++)\r\n{$new_menu = array();\r\n	$menu_num = $j+1;\r\n	$menu_out = \"<b>Menu $menu_num: </b>\".br();\r\n	$row = $result[$j];\r\n\r\n	// Down Arrow		\r\n	if ($has_sisters && $j!=count($result)-1) $menu_out .= a(img(\'Move Down\',\'/img/silk/arrow_down.png\'),\'toolkit_menu_builder&id=\'.$row[\'id\'].\'&down=\'.$j.\'&total=\'.count($result)).\' \';\r\n	else if (count($result)>2) $menu_out .= img(\'\',\'/img/silk/arrow_up.png\',false).\' \';\r\n	else $menu_out .= img(\'\',\'/img/silk/arrow_up.png\',false).\' \';\r\n\r\n	// Up Arrow\r\n	if ($has_sisters && $j!=0) $menu_out .= a(img(\'Move Up\',\'/img/silk/arrow_up.png\'),\'toolkit_menu_builder&id=\'.$row[\'id\'].\'&up=\'.$j.\'&total=\'.count($result)).\' \';\r\n	else if (count($result)>2) $menu_out .= img(\'\',\'/img/silk/arrow_up.png\',false).\' \';\r\n	else $menu_out .= img(\'\',\'/img/silk/arrow_up.png\',false).\' \';\r\n\r\n	$menu_out .= img(\'\',\'/img/silk/bullet_green.png\',false).\' \';\r\n	$menu_out .= img(\'\',\'/img/silk/bullet_green.png\',false).\' \';\r\n\r\n	$menu_out .= a(img(\'Edit Parent Menu Item\',\'/img/silk/tab_edit.png\'),\'toolkit_menu_builder&edit=\'.$row[\'id\']).\' \';\r\n	$menu_out .= a(img(\'Delete Parent Menu Item and All Children\',\'/img/silk/tab_delete.png\'),\'toolkit_menu_builder&delete=\'.$row[\'id\']).\' \';\r\n	$menu_out .= \'<b>\'.$row[\'label\'].\'</b>\';\r\n\r\n$menu_out .= br();\r\n\r\n	$qry = \"UPDATE php_menu SET position=? WHERE id=?\";\r\n	$params = array($j,$row[\'id\']);\r\n	query_execute($qry, $params);\r\n\r\n	$qry = \"SELECT id,label,url FROM php_menu WHERE parent=? ORDER BY position\";\r\n	$params = array($row[\'id\']);\r\n	$result2 = query($qry, $params);\r\n	$children = array();\r\n	//foreach($result2 as $row2)\r\n\r\n	$has_brothers = (count($result2)>1);\r\n	\r\n	if (count($result2)==0)\r\n	{\r\n		$menu_out .= img(\'\',\'/img/silk/tab_edit.png\',false).\' \';\r\n		$menu_out .= img(\'\',\'/img/silk/tab_edit.png\',false).\' \';\r\n		$menu_out .= img(\'\',\'/img/silk/tab_edit.png\',false).\' \';\r\n		$menu_out .= img(\'\',\'/img/silk/tab_edit.png\',false).\' \';\r\n		$menu_out .= img(\'\',\'/img/silk/tab_edit.png\',false).\' \';\r\n		$menu_out .= img(\'\',\'/img/silk/tab_edit.png\',false).\' \';\r\n		$menu_out .= \'(No child menu items)\'.br();\r\n	}\r\n\r\n	for($i=0;$i<count($result2);$i++)\r\n	{\r\n		$row2 = $result2[$i];\r\n		array_push($children, array($row2[\'label\'],$row2[\'url\']));\r\n\r\n		$menu_out .= img(\'\',\'/img/silk/tab_edit.png\',false).\' \';\r\n		$menu_out .= img(\'\',\'/img/silk/tab_edit.png\',false).\' \';\r\n\r\n		$qry = \"UPDATE php_menu SET position=? WHERE id=?\";\r\n		$params = array($i,$row2[\'id\']);\r\n		query_execute($qry, $params);\r\n\r\n		// Down Arrow		\r\n		if ($has_brothers && $i!=count($result2)-1) $menu_out .= a(img(\'Move Down\',\'/img/silk/arrow_down.png\'),\'toolkit_menu_builder&id=\'.$row2[\'id\'].\'&down=\'.$i.\'&total=\'.count($result2)).\' \';\r\n		else if (count($result2)>2) $menu_out .= img(\'\',\'/img/silk/arrow_up.png\',false).\' \';\r\n		else $menu_out .= img(\'\',\'/img/silk/arrow_up.png\',false).\' \';\r\n\r\n		// Up Arrow\r\n		if ($has_brothers && $i!=0) $menu_out .= a(img(\'Move Up\',\'/img/silk/arrow_up.png\'),\'toolkit_menu_builder&id=\'.$row2[\'id\'].\'&up=\'.$i.\'&total=\'.count($result2)).\' \';\r\n		else if (count($result2)>2) $menu_out .= img(\'\',\'/img/silk/arrow_up.png\',false).\' \';\r\n		else $menu_out .= img(\'\',\'/img/silk/arrow_up.png\',false).\' \';\r\n\r\n		$menu_out .= img(\'\',\'/img/silk/tab_edit.png\',false).\' \';\r\n		$menu_out .= img(\'\',\'/img/silk/tab_edit.png\',false).\' \';\r\n\r\n		$menu_out .= a(img(\'Edit Child Menu Item\',\'/img/silk/tab_edit.png\'),\'toolkit_menu_builder&edit=\'.$row2[\'id\']).\' \';\r\n		$menu_out .= a(img(\'Delete Child Menu Item\',\'/img/silk/tab_delete.png\'),\'toolkit_menu_builder&delete=\'.$row2[\'id\']).\' \';\r\n		$menu_out .= $row2[\'label\'].br();\r\n	}\r\n	$parent_url = false;\r\n	if (strlen($row[\'url\'])>0) $parent_url = $row[\'url\'];\r\n	$parent = array($row[\'label\'],$parent_url,$children);\r\n	array_push($new_menu, $parent);\r\n\r\n\r\n\r\n\r\n\r\n	// ********************* Member Groups *********************\r\n	$member_groups=\'\';\r\n\r\n	$qry = \"SELECT access_group.name, access_group.id FROM access_group\r\n	INNER JOIN access_menu WHERE access_menu.group_id = access_group.id AND \r\n	access_menu.menu_id=?\";\r\n\r\n	$params = array($row[\'id\']);\r\n	$result2 = query($qry, $params);\r\n\r\n	$member_groups .= br().\'<b>This menu is set for the following security groups (\'.(count($result2)).\'):</b>\'.br();\r\n	if ((count($result2)