'\')\r\n		{\r\n			$(\'#methodtextedit_dyn\').val(\'blank\');\r\n		}\r\n\r\n		if ($(\'#methodtextedit\').val()==\'blank\')\r\n		{\r\n			$(\'#methodtextedit\').val(\'\');\r\n			$(\'#methodtexteditorg\').val(\'blank\');\r\n		}\r\n\r\n		$(\'#methodtextedit\').removeAttr(\'disabled\');\r\n		$(\'#methodtextedit\').css(\'display\',\'\');\r\n	}\r\n}\r\n\r\n$(function() {\r\n	checkDynamic()\r\n});\r\n\r\n</script>\r\n\r\n	<div style=\"clear:both;\"></div>\r\n\r\n<!-- Beginning of the Save form -->\r\n<form name=\"standardform\" class=\"form-horizontal\" id=\"editfrm\" method=\"post\" onsubmit=\"return validateEditMethodForm()\">\r\n<fieldset>\r\n<!-- Tab \"Panes\" -->\r\n<div class=\"tab-content\">\r\n\r\n<style>\r\ntextarea {  height: 400px; }\r\n</style>\r\n\r\n<!-- Tab: Page -->\r\n<div class=\"tab-pane fade active in\" id=\"tab1\" style=\"height: auto;\">\r\n\r\n	<input type=\"text\" name=\"methodnameedit\" id=\"methodnameedit\" placeholder=\"Name\" \r\n	title=\"Page Name: one word, only letters, numbers, and underscores\"\r\n	value=\"{$page_arr[0][\'name\']}\"/>\r\n\r\n	<input type=\"hidden\" id=\"methodtitle_org\" name=\"methodtitle_org\" value=\"{$page_arr[0][\'title\']}\" />\r\n	<input type=\"hidden\" id=\"methoddescription_org\" name=\"methoddescription_org\" value=\"{$page_arr[0][\'description\']}\" />\r\n	<input type=\"hidden\" id=\"methodkeyword_org\" name=\"methodkeyword_org\" value=\"{$page_arr[0][\'keyword\']}\" />\r\n	<input type=\"hidden\" id=\"methodtype_org\" name=\"methodtype_org\" value=\"{$page_arr[0][\'type\']}\" />\r\n\r\n	<input type=\"text\" name=\"methodtitle_optional\" id=\"methodtitle_optional\" placeholder=\"Title\" \r\n	title=\"Page Title: max of 70 characters, title shows in search engines\"\r\n	value=\"{$page_arr[0][\'title\']}\"/>\r\n\r\n	<input type=\"text\" name=\"methoddescription_optional\" id=\"methoddescription_optional\" placeholder=\"Description\" \r\n	title=\"Page Description: max of 160 characters, description shows in search engines\"\r\n	value=\"{$page_arr[0][\'description\']}\"/>\r\n\r\n	<input type=\"text\" name=\"methodkeyword_optional\" id=\"methodkeyword_optional\" placeholder=\"Keywords\" \r\n	title=\"Page Keywords: enter about 5 words separated by commas, the more relevant the better\"\r\n	value=\"{$page_arr[0][\'keyword\']}\"/>\r\n			<label class=\"radio inline\">\r\n				<input type=\"radio\" id=\"rad1\" name=\"methodtypeedit\" value=\"0\" {$type0} onchange=\"checkDynamic()\"/> HTML\r\n			</label>\r\n			<label class=\"radio inline\">\r\n				<input type=\"radio\" id=\"rad2\" name=\"methodtypeedit\" value=\"1\" {$type1} onchange=\"checkDynamic()\"/> PHP\r\n			</label>\r\n			<label class=\"radio inline\" title=\"Dynamic Page, simply add a table name below\">\r\n				<input type=\"radio\" id=\"rad10\" name=\"methodtypeedit\" value=\"10\" {$type10} onchange=\"checkDynamic()\"/> DYN\r\n			</label>\r\n\r\n	<div class=\"clear\"></div><br />\r\n\r\n	<textarea id=\"methodtextedit\" name=\"methodtextedit\" wrap=\"off\" class=\"span12\" placeholder=\"PHP or HTML code goes here\">{$page_text}</textarea>\r\n\r\n	<textarea id=\"methodtexteditorg\" name=\"methodtexteditorg\" style=\"display:none;\">{$page_text}</textarea>\r\n\r\n	<input type=\"text\" name=\"methodtextedit\" id=\"methodtextedit_dyn\" placeholder=\"Table name\" \r\n	title=\"Create a new table name using only letters and underscores, no spaces, 64 characters max\" maxlength=\"64\"\r\n	value=\"{$page_text}\" disabled=\"disabled\" style=\"display: none;\" />\r\n\r\n	<div class=\"clear\"></div>\r\n\r\n	<select id=\"language\" name=\"language\" title=\"Select the correct language and click Highlight\"\r\n	style=\"float: left; padding: 3px 4px 4px 0px; margin-right: 7px; display: none;\">\r\n	{$select_language}\r\n	<option value=\"htm\">HTML</option>\r\n	<option value=\"css\">CSS</option>\r\n	<option value=\"js\">JavaScript</option>\r\n	<option value=\"php\">PHP</option>\r\n	<option value=\"sql\">MySQL</option>\r\n	<option value=\"sqlite\">